                <span>Digital Locker</span>
            </div>
            <nav className="flex-grow">
                <ul>
                    {navItems.map(item => (
                        <li key={item.name}>
                            <a
                                href="#"
                                onClick={(e) => {e.preventDefault(); setActiveView(item.name);}}
                                className={`flex items-center gap-3 px-4 py-3 my-1 rounded-lg transition-colors duration-200 ${
                                    // Active state uses Mint background and dark text
                                    activeView === item.name
                                        ? `bg-[${COLORS.mint}] text-[${COLORS.mintText}] shadow-md font-semibold`
                                        // Inactive state uses white icons and text on Sage background
                                        : `hover:bg-[${COLORS.darkSage}] hover:text-white`
