                                    {allFiles.length > 0 && (
                                        <button
                                            onClick={() => {
                                                if (selectedFiles.length === allFiles.length && allFiles.length > 0) {
                                                    // Deselect all
                                                    handleSelectFile('deselect-all');
                                                } else {
                                                    // Select all
                                                    handleSelectAll();
