                                                    if (file.isPasswordProtected) {
                                                        if (window.onShowPasswordVerificationModal) {
                                                            window.onShowPasswordVerificationModal(file);
                                                        }
                                                    } else {
                                                        handleFileClick(file);
                                                    }
                                                }}
                                                onMenuClick={() => {
                                                    if (file.isPasswordProtected) {
                                                        if (window.onShowPasswordVerificationModal) {
                                                            window.onShowPasswordVerificationModal(file);
