sourcesContent:
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";

// Hero Carousel Component
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description: "SecureVault provides bank-grade encryption for your most important documents. Safe, organized, and accessible anywhere, anytime.",
      image: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=800&h=600&fit=crop&crop=center",
      alt: "Digital fortress and security"
    },
    {
      title: "Bank-Grade",
      highlight: "Security",
      description: "Military-level encryption ensures your documents are protected with the highest security standards available today.",
      image: "https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center",
      alt: "Security lock and protection"
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description: "Retrieve your files from any device, anywhere in the world. Your documents follow you wherever you go.",
      image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=800&h=600&fit=crop&crop=center",
      alt: "Global access and connectivity"
    },
    {
      title: "Smart",
      highlight: "Organization",
      description: "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.",
      image: "https://plus.unsplash.com/premium_photo-1683288662019-c92caea8276d?ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&q=80&w=1074",
      alt: "Document organization and filing"
    }
  ];

  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length, isAutoPlay]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 10000); // Resume auto-play after 10 seconds
  };

  return (
    <section id="hero" className="min-h-[80vh] flex items-center pt-20 pb-12 px-6 relative overflow-hidden">
      {/* Dynamic Background */}
      <div 
