import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";

// HERO SLIDE COMPONENT (Modernized)
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault protects your most important files with next-generation encryption, powered by AI and cloud security.",
      image:
        "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?w=1400&h=900&fit=crop&crop=center",
      alt: "Cyber fortress and digital protection",
    },
    {
      title: "Bank-Grade",
      highlight: "Encryption",
      description:
        "We use cutting-edge AES-256 encryption to keep your digital identity locked and protected at all times.",
      image:
        "https://images.unsplash.com/photo-1605902711622-cfb43c4437b5?w=1400&h=900&fit=crop&crop=center",
      alt: "Encryption and cyber lock",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Access your documents securely from any device. Sync instantly with cloud backups and smart categorization.",
      image:
        "https://images.unsplash.com/photo-1503023345310-bd7c1de61c7d?w=1400&h=900&fit=crop&crop=center",
      alt: "Access documents globally",
    },
  ];

  // Auto-play
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [isAutoPlay]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 8000);
  };

  return (
    <section className="relative min-h-[90vh] flex items-center justify-center overflow-hidden">
      {/* Background Gradient + Overlay */}
      <motion.div
        key={`bg-${currentSlide}`}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
        className="absolute inset-0 z-0"
        style={{
          backgroundImage: `url(${slides[currentSlide].image})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      />
      <div className="absolute inset-0 bg-gradient-to-r from-indigo-900/70 via-purple-900/60 to-indigo-900/70 z-10 mix-blend-multiply" />

      {/* Animated Floating Blobs */}
      <motion.div
        className="absolute top-10 left-10 w-72 h-72 bg-indigo-400 rounded-full blur-3xl opacity-30"
        animate={{ y: [0, 30, 0], x: [0, 15, 0] }}
        transition={{ repeat: Infinity, duration: 8, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute bottom-10 right-10 w-96 h-96 bg-purple-500 rounded-full blur-3xl opacity-25"
        animate={{ y: [0, -40, 0], x: [0, -20, 0] }}
        transition={{ repeat: Infinity, duration: 10, ease: "easeInOut" }}
      />

      {/* Content */}
      <div className="relative z-20 max-w-6xl mx-auto px-6 grid grid-cols-1 lg:grid-cols-2 gap-10 items-center">
        {/* Text Content in Glass Box */}
        <motion.div
          key={`text-${currentSlide}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7 }}
          className="bg-white/10 backdrop-blur-md p-10 rounded-2xl shadow-xl border border-white/20"
        >
          <h1 className="text-5xl lg:text-6xl font-extrabold text-white leading-tight drop-shadow-lg">
            {slides[currentSlide].title}{" "}
            <span className="text-indigo-300">{slides[currentSlide].highlight}</span>
          </h1>
          <p className="mt-6 text-gray-200 text-lg leading-relaxed max-w-md">
            {slides[currentSlide].description}
          </p>

          <div className="mt-8 flex flex-wrap gap-4">
            {isAuthenticated ? (
              <button
                onClick={handleDashboardNavigation}
                className="bg-indigo-500 hover:bg-indigo-600 text-white font-semibold py-3 px-6 rounded-xl shadow-lg flex items-center transition duration-300"
              >
                Go to Dashboard <ArrowRight className="ml-2 w-5 h-5" />
              </button>
            ) : (
              <>
                <button
                  onClick={() => navigate("/register")}
                  className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-xl shadow-lg transition duration-300"
                >
                  Start Free Trial
                </button>
                <button
                  onClick={() => scrollToSection("features")}
                  className="bg-white/20 hover:bg-white/30 text-white font-semibold py-3 px-6 rounded-xl border border-white/30 backdrop-blur-md transition duration-300"
                >
                  Learn More
                </button>
              </>
            )}
          </div>
        </motion.div>

        {/* Slide Indicators + Image Text */}
        <div className="hidden lg:flex flex-col justify-center items-center relative">
          <motion.div
            key={`img-${currentSlide}`}
            initial={{ opacity: 0, scale: 0.97 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            className="w-full h-[400px] rounded-3xl overflow-hidden shadow-2xl relative"
          >
            <img
              src={slides[currentSlide].image}
              alt={slides[currentSlide].alt}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-black/30" />
          </motion.div>

          <div className="flex justify-center mt-6 space-x-3">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => handleSlideChange(index)}
                className={`transition-all duration-300 ${
                  currentSlide === index
                    ? "w-6 h-3 rounded-full bg-indigo-400"
                    : "w-3 h-3 rounded-full bg-white/40"
                }`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

