import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ArrowRight } from "lucide-react";
import { useNavigate } from "react-router-dom";

const Home = ({ isAuthenticated, handleDashboardNavigation }) => {
  const navigate = useNavigate();
  const featuresRef = useRef(null);
  const aboutRef = useRef(null);
  const contactRef = useRef(null);

  const scrollToSection = (sectionId) => {
    const sectionRefs = {
      features: featuresRef,
      about: aboutRef,
      contact: contactRef,
    };
    sectionRefs[sectionId]?.current?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900 overflow-x-hidden">
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-white shadow-md z-50">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-indigo-600">SecureVault</h1>
          <nav className="flex space-x-6">
            <button onClick={() => scrollToSection("features")} className="hover:text-indigo-600 transition">
              Features
            </button>
            <button onClick={() => scrollToSection("about")} className="hover:text-indigo-600 transition">
              About
            </button>
            <button onClick={() => scrollToSection("contact")} className="hover:text-indigo-600 transition">
              Contact
            </button>
          </nav>
        </div>
      </header>

      {/* HERO CAROUSEL */}
      <HeroCarousel
        isAuthenticated={isAuthenticated}
        handleDashboardNavigation={handleDashboardNavigation}
        navigate={navigate}
        scrollToSection={scrollToSection}
      />

      {/* FEATURES SECTION */}
      <section ref={featuresRef} className="py-20 bg-white text-center">
        <h2 className="text-4xl font-bold text-indigo-700 mb-6">Key Features</h2>
        <p className="max-w-2xl mx-auto text-gray-600 mb-12">
          SecureVault provides enterprise-grade digital file protection with the latest in AI-powered encryption,
          seamless file syncing, and secure sharing options.
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 px-6">
          {[
            {
              title: "Advanced Encryption",
              desc: "Your files are secured using AES-256 and blockchain-based integrity validation.",
            },
            {
              title: "Cloud Sync",
              desc: "Access your documents from anywhere with real-time cloud synchronization.",
            },
            {
              title: "AI Detection",
              desc: "AI-based anomaly detection ensures your data remains tamper-proof.",
            },
          ].map((feature, i) => (
            <motion.div
              key={i}
              whileHover={{ scale: 1.05 }}
              className="bg-indigo-50 p-8 rounded-2xl shadow-lg hover:shadow-xl transition"
            >
              <h3 className="text-2xl font-semibold text-indigo-700 mb-3">{feature.title}</h3>
              <p className="text-gray-600">{feature.desc}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* ABOUT SECTION */}
      <section ref={aboutRef} className="py-20 bg-gray-100 text-center">
        <h2 className="text-4xl font-bold text-indigo-700 mb-6">About SecureVault</h2>
        <p className="max-w-3xl mx-auto text-gray-700 leading-relaxed px-4">
          SecureVault is a next-generation digital locker platform designed to protect your files with advanced AI
          encryption, seamless cloud integration, and user-friendly access controls. Trusted by thousands of users and
          businesses worldwide, SecureVault empowers you to take control of your digital identity securely.
        </p>
      </section>

      {/* CONTACT SECTION */}
      <section ref={contactRef} className="py-20 bg-white text-center">
        <h2 className="text-4xl font-bold text-indigo-700 mb-6">Get in Touch</h2>
        <p className="max-w-2xl mx-auto text-gray-600 mb-8">
          Have questions or need enterprise solutions? Our team is here to assist you 24/7.
        </p>
        <button className="bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-3 rounded-lg font-semibold shadow-lg transition">
          Contact Us
        </button>
      </section>

      {/* FOOTER */}
      <footer className="bg-gray-900 text-gray-300 py-8 text-center">
        <p>Â© {new Date().getFullYear()} SecureVault. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;

/* ------------------ Updated Hero Carousel ------------------ */
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault protects your most important files with next-generation encryption, powered by AI and cloud security.",
      image: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?w=1400&h=900&fit=crop&crop=center",
      alt: "Cyber fortress and digital protection",
    },
    {
      title: "Bank-Grade",
      highlight: "Encryption",
      description:
        "We use cutting-edge AES-256 encryption to keep your digital identity locked and protected at all times.",
      image: "https://images.unsplash.com/photo-1605902711622-cfb43c4437b5?w=1400&h=900&fit=crop&crop=center",
      alt: "Encryption and cyber lock",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Access your documents securely from any device. Sync instantly with cloud backups and smart categorization.",
      image: "https://images.unsplash.com/photo-1503023345310-bd7c1de61c7d?w=1400&h=900&fit=crop&crop=center",
      alt: "Access documents globally",
    },
  ];

  // Auto-play slides
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [isAutoPlay]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 8000);
  };

  return (
    <section className="relative min-h-[90vh] flex items-center justify-center overflow-hidden text-white" id="hero">
      {/* Background Transition */}
      <AnimatePresence mode="wait">
        <motion.div
          key={slides[currentSlide].image}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 1.2 }}
          className="absolute inset-0 bg-cover bg-center z-0"
          style={{ backgroundImage: `url(${slides[currentSlide].image})` }}
        />
      </AnimatePresence>

      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-r from-indigo-900/80 via-purple-900/70 to-indigo-900/80 z-10" />

      {/* Floating Animation Blobs */}
      <motion.div
        className="absolute top-10 left-10 w-72 h-72 bg-indigo-400 rounded-full blur-3xl opacity-30 z-0"
        animate={{ y: [0, 30, 0], x: [0, 15, 0] }}
        transition={{ duration: 8, repeat: Infinity, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute bottom-10 right-10 w-96 h-96 bg-purple-500 rounded-full blur-3xl opacity-30 z-0"
        animate={{ y: [0, -20, 0], x: [0, -15, 0] }}
        transition={{ duration: 9, repeat: Infinity, ease: "easeInOut" }}
      />

      {/* Text Content */}
      <div className="relative z-20 text-center px-6 max-w-4xl mx-auto">
        <motion.h1
          key={`title-${currentSlide}`}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -30 }}
          transition={{ duration: 0.8 }}
          className="text-5xl sm:text-6xl font-extrabold mb-4"
        >
          {slides[currentSlide].title}{" "}
          <span className="text-indigo-300">{slides[currentSlide].highlight}</span>
        </motion.h1>

        <motion.p
          key={`desc-${currentSlide}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.8, delay: 0.1 }}
          className="text-lg sm:text-xl text-gray-200 max-w-2xl mx-auto mb-8"
        >
          {slides[currentSlide].description}
        </motion.p>

        {/* Buttons */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.3 }}
          className="flex flex-wrap justify-center gap-4"
        >
          {isAuthenticated ? (
            <button
              onClick={handleDashboardNavigation}
              className="bg-indigo-600 hover:bg-indigo-700 px-6 py-3 rounded-lg text-white font-semibold flex items-center gap-2 shadow-lg"
            >
              Go to Dashboard <ArrowRight className="w-5 h-5" />
            </button>
          ) : (
            <>
              <button
                onClick={() => navigate("/register")}
                className="bg-purple-600 hover:bg-purple-700 px-6 py-3 rounded-lg text-white font-semibold shadow-lg"
              >
                Start Free Trial
              </button>
              <button
                onClick={() => scrollToSection("features")}
                className="bg-transparent border border-gray-300 hover:bg-white/10 px-6 py-3 rounded-lg font-semibold text-gray-200 transition-colors"
              >
                Learn More
              </button>
            </>
          )}
        </motion.div>

        {/* Slide Indicators */}
        <div className="flex justify-center mt-10 space-x-3">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => handleSlideChange(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                currentSlide === index ? "bg-indigo-400 w-6" : "bg-gray-400/60"
              }`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};
