import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
  MapPin,
  Facebook,
  Twitter,
  Linkedin,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";

// How It Works Cards Component
const HowItWorksCards = () => {
  const [currentCard, setCurrentCard] = useState(0);

  const cards = [
    {
      icon: CloudUpload,
      title: "1. Upload & Encrypt",
      description: "Simply drag and drop your files into our secure portal. Advanced AES-256 encryption happens instantly on your device before transmission, ensuring zero-knowledge security.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: ScanSearch,
      title: "2. Organize & Find",
      description: "AI-powered smart categorization automatically sorts your documents. Lightning-fast search with OCR technology finds any document in milliseconds, not minutes.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: FileLock2,
      title: "3. Access & Control",
      description: "Access your vault from any device with biometric authentication. Granular permission controls let admins manage team access with enterprise-grade security protocols.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentCard((prev) => (prev + 1) % cards.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [cards.length]);

  return (
    <div className="relative max-w-6xl mx-auto h-[500px] flex items-center justify-center">
      {cards.map((card, index) => {
        const Icon = card.icon;
        const isActive = index === currentCard;
        const isPrevious = index === (currentCard - 1 + cards.length) % cards.length;
        const isNext = index === (currentCard + 1) % cards.length;

        let zIndex = 1;
        let transform = { x: 0, z: -200, rotateY: 180, opacity: 0, scale: 0.7 };

        if (isActive) {
          zIndex = 3;
          transform = { x: 0, z: 0, rotateY: 0, opacity: 1, scale: 1 };
        } else if (isPrevious) {
          zIndex = 2;
          transform = { x: -120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        } else if (isNext) {
          zIndex = 2;
          transform = { x: 120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        }

        return (
          <motion.div
            key={index}
            animate={transform}
            transition={{
              duration: 0.8,
              type: "spring",
              bounce: 0.3
            }}
            whileHover={isActive ? {
              scale: 1.05,
              rotateY: 5,
              z: 50
            } : {
              scale: 1.02,
              z: 0,
              opacity: 1
            }}
            onClick={() => setCurrentCard(index)}
            className={`absolute p-8 bg-gradient-to-br ${card.gradient} rounded-2xl shadow-2xl ${card.border} w-80 h-96 flex flex-col justify-center items-center text-center cursor-pointer`}
            style={{ zIndex }}
          >
            <motion.div
              whileHover={{
                rotate: [0, -10, 10, 0],
                scale: 1.2
              }}
              transition={{ duration: 0.5 }}
            >
              <Icon className="w-16 h-16 mb-6 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-2xl font-bold mb-4 text-gray-800">{card.title}</h3>
            <p className="text-gray-600 leading-relaxed text-sm">
              {card.description}
            </p>
            <motion.div
              className="mt-4 w-12 h-1 rounded-full"
              style={{backgroundColor: '#662222'}}
              initial={{ width: 0 }}
              animate={{ width: isActive ? 48 : 24 }}
              transition={{ duration: 0.5 }}
            />
          </motion.div>
        );
      })}

      {/* Navigation Dots */}
      <div className="absolute bottom-8 flex space-x-2">
        {cards.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentCard(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              currentCard === index ? "w-6" : "bg-gray-400"
            }`}
            style={currentCard === index ? {backgroundColor: '#662222'} : {}}
          />
        ))}
      </div>
    </div>
  );
};

// Flip Card Stack Component
const FlipCardStack = () => {
  const [currentCard, setCurrentCard] = useState(0);

  const cards = [
    {
      icon: User,
      title: "For Individuals",
      description: "Keep your passports, birth certificates, wills, and personal IDs safe from loss or unauthorized access. Personal vault with biometric security.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: Briefcase,
      title: "For Freelancers",
      description: "Securely store client contracts, invoices, project files, and tax documents, all in one organized workspace with client portals.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: Users,
      title: "For Admin Teams",
      description: "Manage sensitive company documents, employee records, and financial reports with robust admin controls and team permissions.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentCard((prev) => (prev + 1) % cards.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [cards.length]);

  return (
    <div className="relative max-w-6xl mx-auto h-[500px] flex items-center justify-center">
      {cards.map((card, index) => {
        const Icon = card.icon;
        const isActive = index === currentCard;
        const isPrevious = index === (currentCard - 1 + cards.length) % cards.length;
        const isNext = index === (currentCard + 1) % cards.length;

        let zIndex = 1;
        let transform = { x: 0, z: -200, rotateY: 180, opacity: 0, scale: 0.7 };

        if (isActive) {
          zIndex = 3;
          transform = { x: 0, z: 0, rotateY: 0, opacity: 1, scale: 1 };
        } else if (isPrevious) {
          zIndex = 2;
          transform = { x: -120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        } else if (isNext) {
          zIndex = 2;
          transform = { x: 120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        }

        return (
          <motion.div
            key={index}
            animate={transform}
            transition={{
              duration: 0.8,
              type: "spring",
              bounce: 0.3
            }}
            whileHover={isActive ? {
              scale: 1.05,
              rotateY: 5,
              z: 50
            } : {
              scale: 1.02,
              z: 0,
              opacity: 1
            }}
            onClick={() => setCurrentCard(index)}
            className={`absolute p-8 bg-gradient-to-br ${card.gradient} rounded-2xl shadow-2xl ${card.border} w-80 h-96 flex flex-col justify-center items-center text-center cursor-pointer`}
            style={{ zIndex }}
          >
            <motion.div
              whileHover={{
                rotate: [0, -10, 10, 0],
                scale: 1.2
              }}
              transition={{ duration: 0.5 }}
            >
              <Icon className="w-16 h-16 mb-6 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-2xl font-bold mb-4 text-gray-800">{card.title}</h3>
            <p className="text-gray-600 leading-relaxed text-sm">
              {card.description}
            </p>
            <motion.div
              className="mt-4 w-12 h-1 rounded-full"
              style={{backgroundColor: '#662222'}}
              initial={{ width: 0 }}
              animate={{ width: isActive ? 48 : 24 }}
              transition={{ duration: 0.5 }}
            />
          </motion.div>
        );
      })}

      {/* Navigation Dots */}
      <div className="absolute bottom-8 flex space-x-2">
        {cards.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentCard(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              currentCard === index ? "w-6" : "bg-gray-400"
            }`}
            style={currentCard === index ? {backgroundColor: '#662222'} : {}}
          />
        ))}
      </div>
    </div>
  );
};

// HERO SLIDE COMPONENT (Modernized)
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault protects your most important files with next-generation encryption, powered by AI and cloud security.",
      image:
        "https://media.istockphoto.com/id/2206161408/photo/glowing-shield-symbolizing-cybersecurity-on-a-high-tech-circuit-background.webp?a=1&b=1&s=612x612&w=0&k=20&c=pp7VyJc4-NM3-KGhQ4rm_89T-7lEmzrF6kNefVHzU18=",
      alt: "Cyber fortress and digital protection",
    },
    {
      title: "Bank-Grade",
      highlight: "Encryption",
      description:
        "We use cutting-edge AES-256 encryption to keep your digital identity locked and protected at all times.",
      image:
        "https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center",
      alt: "Encryption and cyber lock",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Access your documents securely from any device. Sync instantly with cloud backups and smart categorization.",
      image:
        "https://images.unsplash.com/photo-1605902711622-cfb43c4437b5?w=1400&h=900&fit=crop&crop=center",
      alt: "Access documents globally",
    },
    { title: "Smart",
      highlight: "Organization",
      description: "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.", 
      image: "https://plus.unsplash.com/premium_photo-1683288662019-c92caea8276d?ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&q=80&w=1074",
      alt: "Document organization and filing" 
    }
  ];

  // Auto-play
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [isAutoPlay]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 8000);
  };

  return (
    <section className="relative min-h-[90vh] flex items-center justify-center overflow-hidden">
      {/* Background Gradient + Overlay */}
      <motion.div
        key={`bg-${currentSlide}`}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
        className="absolute inset-0 z-0"
        style={{
          backgroundImage: `url(${slides[currentSlide].image})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      />
      <div className="absolute inset-0 bg-gradient-to-r from-stone-900/70 via-rose-900/60 to-stone-900/70 z-10 mix-blend-multiply" />

      {/* Animated Floating Blobs */}
      <motion.div
        className="absolute top-10 left-10 w-72 h-72 bg-rose-300 rounded-full blur-3xl opacity-30"
        animate={{ y: [0, 30, 0], x: [0, 15, 0] }}
        transition={{ repeat: Infinity, duration: 8, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute bottom-10 right-10 w-96 h-96 bg-rose-400 rounded-full blur-3xl opacity-25"
        animate={{ y: [0, -40, 0], x: [0, -20, 0] }}
        transition={{ repeat: Infinity, duration: 10, ease: "easeInOut" }}
      />

      {/* Content */}
      <div className="relative z-20 max-w-6xl mx-auto px-6 grid grid-cols-1 lg:grid-cols-2 gap-10 items-center">
        {/* Text Content in Glass Box */}
        <motion.div
          key={`text-${currentSlide}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7 }}
          className="bg-white/10 backdrop-blur-md p-10 rounded-2xl shadow-xl border border-white/20"
        >
          <h1 className="text-5xl lg:text-6xl font-extrabold text-white leading-tight drop-shadow-lg">
            {slides[currentSlide].title}{" "}
            <span className="text-rose-300">{slides[currentSlide].highlight}</span>
          </h1>
          <p className="mt-6 text-gray-200 text-lg leading-relaxed max-w-md">
            {slides[currentSlide].description}
          </p>

          <div className="mt-8 flex flex-wrap gap-4">
            {isAuthenticated ? (
              <button
                onClick={handleDashboardNavigation}
                className="text-white font-semibold py-3 px-6 rounded-xl shadow-lg flex items-center transition duration-300"
                style={{backgroundColor: '#662222'}}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#4A1A1A'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#662222'}
              >
                Go to Dashboard <ArrowRight className="ml-2 w-5 h-5" />
              </button>
            ) : (
              <>
                <button
                  onClick={() => navigate("/register")}
                  className="text-white font-semibold py-3 px-6 rounded-xl shadow-lg transition duration-300"
                  style={{backgroundColor: '#662222'}}
                  onMouseEnter={(e) => e.target.style.backgroundColor = '#4A1A1A'}
                  onMouseLeave={(e) => e.target.style.backgroundColor = '#662222'}
                >
                  Start Free Trial
                </button>
                <button
                  onClick={() => scrollToSection("features")}
                  className="bg-white/20 hover:bg-white/30 text-white font-semibold py-3 px-6 rounded-xl border border-white/30 backdrop-blur-md transition duration-300"
                >
                  Learn More
                </button>
              </>
            )}
          </div>
        </motion.div>

        {/* Slide Indicators + Image Text */}
        <div className="hidden lg:flex flex-col justify-center items-center relative">
          <motion.div
            key={`img-${currentSlide}`}
            initial={{ opacity: 0, scale: 0.97 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            className="w-full h-[400px] rounded-3xl overflow-hidden shadow-2xl relative"
          >
            <img
              src={slides[currentSlide].image}
              alt={slides[currentSlide].alt}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-black/30" />
          </motion.div>

          <div className="flex justify-center mt-6 space-x-3">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => handleSlideChange(index)}
                className={`transition-all duration-300 ${
                  currentSlide === index
                    ? "w-6 h-3 rounded-full"
                    : "w-3 h-3 rounded-full bg-white/40"
                }`}
                style={currentSlide === index ? {backgroundColor: '#662222'} : {}}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

// MAIN HOME PAGE COMPONENT
export default function Home() {
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth();

  const handleDashboardNavigation = () => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    if (user?.role === "ADMIN") navigate("/admin-dashboard");
    else navigate("/dashboard");
  };

  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="min-h-screen flex flex-col bg-white text-gray-900 font-inter">
      <Navbar />

      {/* HERO SLIDE SECTION */}
      <HeroCarousel
        isAuthenticated={isAuthenticated}
        handleDashboardNavigation={handleDashboardNavigation}
        navigate={navigate}
        scrollToSection={scrollToSection}
      />
      {/* HOW IT WORKS SECTION */}
      <section id="how-it-works" className="py-24 px-8 bg-gray-50 relative overflow-hidden">
        {/* Animated Background Elements */}
        <motion.div
          className="absolute top-10 left-10 w-32 h-32 bg-rose-300 rounded-full blur-2xl opacity-20"
          animate={{ y: [0, 20, 0], rotate: [0, 180, 360] }}
          transition={{ repeat: Infinity, duration: 8, ease: "easeInOut" }}
        />
        <motion.div
          className="absolute bottom-10 right-10 w-40 h-40 bg-rose-400 rounded-full blur-2xl opacity-20"
          animate={{ y: [0, -30, 0], rotate: [360, 180, 0] }}
          transition={{ repeat: Infinity, duration: 10, ease: "easeInOut" }}
        />

        <motion.h2 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-4 text-center"
          style={{color: '#662222'}}
        >
          How It Works
        </motion.h2>
        <motion.p 
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-lg text-gray-600 text-center max-w-2xl mx-auto mb-16"
        >
          Transform your document security with our revolutionary 3-step process. Experience military-grade protection with consumer-friendly simplicity.
        </motion.p>

        <div className="max-w-7xl mx-auto flex justify-center">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <HowItWorksCards />
          </motion.div>
        </div>
      </section>

      {/* SECURITY FEATURES SECTION */}
      <section id="security" className="py-24 px-8 bg-white relative overflow-hidden">
        {/* Animated Security Grid Background */}
        <motion.div 
          className="absolute inset-0 opacity-5"
          animate={{ 
            backgroundPosition: ['0% 0%', '100% 100%'] 
          }}
          transition={{ 
            repeat: Infinity, 
            duration: 20, 
            ease: "linear" 
          }}
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23FB923C' fill-opacity='0.4'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
            backgroundSize: '60px 60px'
          }}
        />

        <motion.h2 
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, type: "spring", bounce: 0.3 }}
          className="text-4xl font-bold mb-4 text-center"
          style={{color: '#662222'}}
        >
          Your Security is Our Priority
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-lg text-gray-600 text-center max-w-3xl mx-auto mb-16"
        >
          Military-grade protection meets user-friendly design. Experience uncompromising security without sacrificing convenience.
        </motion.p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
          {/* Card 1: End-to-End Encryption */}
          <motion.div 
            initial={{ opacity: 0, y: 50, rotateX: -30 }}
            whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
            transition={{ duration: 0.8, delay: 0.1 }}
            whileHover={{ 
              y: -10,
              rotateY: 5,
              boxShadow: "0 20px 40px rgba(251, 146, 60, 0.2)"
            }}
            className="bg-gradient-to-br from-orange-100 to-red-100 p-8 rounded-2xl shadow-lg relative overflow-hidden group"
          >
            <motion.div
              className="absolute -top-6 -right-6 w-24 h-24 bg-red-200 rounded-full opacity-20"
              animate={{ 
                scale: [1, 1.3, 1],
                rotate: [0, 180, 360]
              }}
              transition={{ repeat: Infinity, duration: 8, ease: "easeInOut" }}
            />
            <motion.div
              whileHover={{ 
                scale: 1.2,
                rotate: [0, -10, 10, 0],
                transition: { duration: 0.5 }
              }}
              className="mb-6 relative z-10"
            >
              <ShieldCheck className="w-14 h-14 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-xl font-bold mb-3 text-gray-800 text-center">
              End-to-End Encryption
            </h3>
            <p className="text-gray-600 text-center text-sm leading-relaxed">
              AES-256 military-grade encryption ensures only you can access your files. Zero-knowledge architecture means even we can't see your data.
            </p>
            <motion.div
              className="mt-4 flex justify-center space-x-1"
              initial={{ opacity: 0 }}
              whileInView={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              {[...Array(3)].map((_, i) => (
                <motion.div
                  key={i}
                  className="w-2 h-2 rounded-full"
                  style={{backgroundColor: '#662222'}}
                  animate={{ 
                    scale: [1, 1.5, 1],
                    opacity: [0.5, 1, 0.5]
                  }}
                  transition={{ 
                    repeat: Infinity, 
                    duration: 2, 
                    delay: i * 0.3
                  }}
                />
              ))}
            </motion.div>
          </motion.div>

          {/* Card 2: Multi-Factor Authentication */}
          <motion.div 
            initial={{ opacity: 0, x: -50, rotateY: -30 }}
            whileInView={{ opacity: 1, x: 0, rotateY: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            whileHover={{ 
              y: -10,
              rotateX: 5,
              boxShadow: "0 20px 40px rgba(251, 146, 60, 0.2)"
            }}
            className="bg-gradient-to-br from-orange-100 to-red-100 p-8 rounded-2xl shadow-lg relative overflow-hidden group"
          >
            <motion.div
              className="absolute -bottom-4 -left-4 w-20 h-20 bg-red-200 rounded-full opacity-25"
              animate={{ 
                x: [0, 15, 0],
                y: [0, -15, 0]
              }}
              transition={{ repeat: Infinity, duration: 6, ease: "easeInOut" }}
            />
            <motion.div
              whileHover={{ 
                scale: 1.2,
                rotateZ: 360,
                transition: { duration: 0.8 }
              }}
              className="mb-6 relative z-10"
            >
              <Fingerprint className="w-14 h-14 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-xl font-bold mb-3 text-gray-800 text-center">
              Multi-Factor Authentication
            </h3>
            <p className="text-gray-600 text-center text-sm leading-relaxed">
              Biometric authentication, SMS codes, and authenticator apps provide multiple layers of security for your account.
            </p>
            <motion.div
              className="mt-4 w-full h-1 rounded-full overflow-hidden"
              style={{backgroundColor: 'rgba(102, 34, 34, 0.3)'}}
              initial={{ opacity: 0 }}
              whileInView={{ opacity: 1 }}
              transition={{ delay: 0.6 }}
            >
              <motion.div
                className="h-full rounded-full"
                style={{backgroundColor: '#662222'}}
                initial={{ width: "0%" }}
                whileInView={{ width: "100%" }}
                transition={{ duration: 2, delay: 0.8 }}
              />
            </motion.div>
          </motion.div>

          {/* Card 3: Real-time Monitoring */}
          <motion.div 
