import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { documentAPI, folderAPI, activityAPI } from '../services/api';

// Theme Colors
const COLORS = {
    sage: '#96A78D',
    mint: '#B6CEB4',
    lightSage: '#D9E9CF',
    offWhite: '#F0F0F0',
    darkSage: '#7E8F76',
    textColor: '#374151',
    mintText: '#547A50',
};

// Utility Functions
const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

const getFileType = (filename) => {
    const ext = filename.split('.').pop().toLowerCase();
    switch (ext) {
        case 'pdf': return 'PDF';
        case 'doc':
        case 'docx': return 'Document';
        case 'xls':
        case 'xlsx': return 'Spreadsheet';
        case 'jpg':
        case 'jpeg':
        case 'png':
        case 'gif': return 'Image';
        case 'mp4':
        case 'mov': return 'Video';
        case 'txt': return 'Text';
        case 'zip':
        case 'rar': return 'Archive';
        default: return 'Other';
    }
};

// SVG Icons
const FileTextIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M15 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L15 2z"></path>
        <polyline points="14 2 14 8 20 8"></polyline>
        <line x1="16" y1="13" x2="8" y2="13"></line>
        <line x1="16" y1="17" x2="8" y2="17"></line>
        <line x1="10" y1="9" x2="8" y2="9"></line>
    </svg>
);

const UploadIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
        <polyline points="17 8 12 3 7 8"></polyline>
        <line x1="12" y1="3" x2="12" y2="15"></line>
    </svg>
);

const FolderIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M4 20h16a2 2 0 0 0 2-2V8a2 2 0 0 0-2-2h-7.93a2 2 0 0 1-1.66-.9l-.82-1.2A2 2 0 0 0 7.93 3H4a2 2 0 0 0-2 2v13c0 1.1.9 2 2 2z"></path>
    </svg>
);

const HistoryIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M12 2v5"></path>
        <path d="M12 17v5"></path>
        <circle cx="12" cy="12" r="10"></circle>
    </svg>
);

const ZapIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <polygon points="13 2 3 14 12 14 11 22 21 10 12 10 13 2"></polygon>
    </svg>
);

const DownloadIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
        <polyline points="7 10 12 15 17 10"></polyline>
        <line x1="12" y1="15" x2="12" y2="3"></line>
    </svg>
);

const XIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M18 6 6 18"></path>
        <path d="m6 6 12 12"></path>
    </svg>
);

const TrashIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M3 6h18"></path>
        <path d="M19 6v14c0 1-1 2-2 2H7c-1 0-2-1-2-2V6"></path>
        <path d="M8 6V4c0-1 1-2 2-2h4c1 0 2 1 2 2v2"></path>
    </svg>
);

const PlusIcon = ({ className = "h-5 w-5" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
        <path d="M12 5v14"></path>
        <path d="M5 12h14"></path>
    </svg>
);

// Modal Component
const Modal = ({ title, children, onClose, isOpen }) => {
    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4" onClick={onClose}>
            <div 
                className="bg-white rounded-xl shadow-2xl max-w-lg w-full transform transition-all overflow-hidden"
                onClick={e => e.stopPropagation()}
            >
                <div className="p-4 flex justify-between items-center border-b" style={{backgroundColor: COLORS.lightSage}}>
                    <h3 className="text-xl font-semibold" style={{color: COLORS.textColor}}>{title}</h3>
                    <button onClick={onClose} className="text-gray-600 hover:text-red-500 p-2 rounded-full transition">
                        <XIcon className="h-6 w-6" />
                    </button>
                </div>
                <div className="p-6 max-h-[80vh] overflow-y-auto">
                    {children}
                </div>
            </div>
        </div>
    );
};

// New Folder Modal
const NewFolderModal = ({ isOpen, onClose, onCreateFolder }) => {
    const [folderName, setFolderName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (folderName.trim()) {
            onCreateFolder(folderName.trim());
            setFolderName('');
            onClose();
        }
    };

    return (
        <Modal title="Create New Folder" isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        Folder Name
                    </label>
                    <input
                        type="text"
                        value={folderName}
                        onChange={(e) => setFolderName(e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-sage-500"
                        placeholder="Enter folder name"
                        autoFocus
                    />
                </div>
                <div className="flex justify-end gap-3 pt-4">
                    <button
                        type="button"
                        onClick={onClose}
                        className="px-4 py-2 text-gray-600 hover:text-gray-800 transition"
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                        style={{backgroundColor: COLORS.sage}}
                        disabled={!folderName.trim()}
                    >
                        Create Folder
                    </button>
                </div>
            </form>
        </Modal>
    );
};

// File Detail Modal
const FileDetailModal = ({ file, isOpen, onClose, onDeleteFile, onDownloadFile }) => {
    const [isDeleting, setIsDeleting] = useState(false);

    if (!file) return null;

    const handleDownload = () => {
        onDownloadFile(file);
    };

    const confirmDelete = async () => {
        if (!isDeleting) {
            setIsDeleting(true);
            try {
                await onDeleteFile(file);
                onClose();
            } catch (error) {
                console.error("Failed to delete file:", error);
            } finally {
                setIsDeleting(false);
            }
        }
    };

    return (
        <Modal title={`File Details: ${file.fileName}`} isOpen={isOpen} onClose={onClose}>
            <div className="space-y-4">
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Name</p>
                    <p className="text-sm">{file.fileName}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Type</p>
                    <p className="text-sm">{file.fileType}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Size</p>
                    <p className="text-sm">{formatFileSize(file.fileSize)}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">Upload Date</p>
                    <p className="text-sm">{new Date(file.uploadDate).toLocaleString()}</p>
                </div>

                {/* File Preview Section */}
                {file.previewUrl && getFileType(file.fileName) === 'Image' && (
                    <div className="mt-6 border border-gray-200 rounded-lg p-4 text-center">
                        <p className="font-semibold mb-3" style={{color: COLORS.textColor}}>File Preview</p>
                        <img 
                            src={file.previewUrl} 
                            alt="File Preview" 
                            className="max-w-full max-h-60 mx-auto rounded-lg shadow-md object-contain" 
                        />
                    </div>
                )}

                {/* Actions */}
                <div className="flex justify-end gap-3 pt-4 border-t mt-6">
                    <button
                        onClick={confirmDelete}
                        disabled={isDeleting}
                        className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition bg-red-500 hover:bg-red-600 shadow-md disabled:opacity-50"
                    >
                        <TrashIcon className="h-5 w-5 mr-2" />
                        {isDeleting ? 'Deleting...' : 'Delete File'}
                    </button>
                    <button
                        onClick={handleDownload}
                        className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                        style={{backgroundColor: COLORS.sage}}
                    >
                        <DownloadIcon className="h-5 w-5 mr-2" />
                        Download
                    </button>
                </div>
            </div>
        </Modal>
    );
};

// Sidebar Component
const Sidebar = ({ activeView, setActiveView }) => {
    const { user } = useAuth();

    const navItems = [
        { name: 'My Files', icon: FileTextIcon },
        { name: 'History', icon: HistoryIcon },
        { name: 'Activity Logs', icon: ZapIcon },
    ];

    return (
        <aside className="w-64 flex-shrink-0 text-gray-800 flex flex-col p-4 shadow-xl" style={{backgroundColor: COLORS.sage}}>
            <div className="text-white text-2xl font-bold mb-6 flex items-center gap-2">
                <FolderIcon className="h-7 w-7" />
                <span>File Dashboard</span>
            </div>
            <div className="text-white text-xs mb-8 p-2 rounded-lg break-all" style={{backgroundColor: COLORS.darkSage}}>
                User: <span className="font-mono text-xs">{user?.email || 'Guest'}</span>
            </div>
            <nav className="flex-grow">
                <ul>
                    {navItems.map(item => (
                        <li key={item.name}>
                            <a
                                href="#"
                                onClick={(e) => {e.preventDefault(); setActiveView(item.name);}}
                                className={`flex items-center gap-3 px-4 py-3 my-1 rounded-lg transition-colors duration-200 ${
                                    activeView === item.name
                                        ? 'font-semibold shadow-md'
                                        : 'hover:text-white text-white'
                                }`}
                                style={{
                                    backgroundColor: activeView === item.name ? COLORS.mint : 'transparent',
                                    color: activeView === item.name ? COLORS.mintText : 'white'
                                }}
                            >
                                <item.icon className="h-5 w-5"/>
                                <span>{item.name}</span>
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>
        </aside>
    );
};

// Header Component
const Header = ({ title }) => {
    const { user } = useAuth();

    return (
        <header className="bg-white sticky top-0 z-10 shadow-sm p-4 flex justify-between items-center">
            <h1 className="text-2xl font-semibold" style={{color: COLORS.textColor}}>{title}</h1>
            <div className="flex items-center gap-4">
                <div className="text-sm text-gray-600">
                    Welcome, {user?.name || user?.email || 'User'}
                </div>
                <div className="h-10 w-10 rounded-full flex items-center justify-center text-white font-semibold" style={{backgroundColor: COLORS.sage}}>
                    {(user?.name || user?.email || 'U').charAt(0).toUpperCase()}
                </div>
            </div>
        </header>
    );
};

// File Icon Component
const FileIconByType = ({ type }) => {
    let iconClass = "h-6 w-6 mr-3";

    switch (type) {
        case 'Image': 
            return <div className={`${iconClass} text-blue-500`}></div>;
        case 'PDF': 
            return <div className={`${iconClass} text-red-500`}></div>;
        case 'Document': 
            return <div className={`${iconClass} text-green-600`}></div>;
        case 'Archive': 
            return <div className={`${iconClass} text-yellow-600`}></div>;
        default: 
            return <FileTextIcon className={`${iconClass} text-gray-500`} />;
    }
};

export default function UserFileDashboard() {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('My Files');
    const [files, setFiles] = useState([]);
    const [folders, setFolders] = useState([]);
    const [activityLogs, setActivityLogs] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalFile, setModalFile] = useState(null);
    const [isNewFolderModalOpen, setIsNewFolderModalOpen] = useState(false);
    const [isUploading, setIsUploading] = useState(false);
    const [currentFolder, setCurrentFolder] = useState(null);
    const [breadcrumb, setBreadcrumb] = useState([]);
    const fileInputRef = useRef(null);

    // Load data from backend
    useEffect(() => {
        if (user) {
            loadFolders();
            loadFiles();
            loadActivityLogs();
        }
    }, [user]);

    const loadFolders = async () => {
        try {
            const response = await folderAPI.getAllFolders();
            setFolders(response.data);
        } catch (error) {
            console.error('Failed to load folders:', error);
        }
    };

    const loadFiles = async () => {
        try {
            const response = await documentAPI.getAllDocuments();
            setFiles(response.data);
        } catch (error) {
            console.error('Failed to load files:', error);
        }
    };

    const loadActivityLogs = async () => {
        try {
            const response = await activityAPI.getAllLogs();
            setActivityLogs(response.data);
        } catch (error) {
            console.error('Failed to load activity logs:', error);
        }
    };

    // Activity Logger
    const logActivity = async (action, details) => {
        try {
            await activityAPI.recordActivity(action, details);
            // Reload activity logs to get the latest
            loadActivityLogs();
        } catch (error) {
            console.error('Failed to log activity:', error);
        }
    };

    // File Upload Handler
    const handleFileUpload = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        setIsUploading(true);

        try {
            // Simulate file upload
            const newFile = {
                id: Date.now(),
                fileName: file.name,
                fileType: getFileType(file.name),
                fileSize: file.size,
                uploadDate: new Date().toISOString(),
                previewUrl: file.type.startsWith('image/') ? URL.createObjectURL(file) : null,
                downloadUrl: URL.createObjectURL(file), // In real app, this would be server URL
                folderId: currentFolder?.id || null,
            };

            setFiles(prev => [newFile, ...prev]);
            logActivity('UPLOAD', { fileName: file.name, fileSize: formatFileSize(file.size) });

        } catch (error) {
            console.error("Upload failed:", error);
            alert("File upload failed.");
        } finally {
            setIsUploading(false);
            event.target.value = null;
        }
    };

    // Folder Creation Handler
    const handleCreateFolder = (folderName) => {
        const newFolder = {
            id: Date.now(),
            name: folderName,
            createdDate: new Date().toISOString(),
            parentId: currentFolder?.id || null,
        };
        setFolders(prev => [newFolder, ...prev]);
        logActivity('CREATE_FOLDER', { folderName });
    };

    // Folder Navigation Handlers
    const handleFolderClick = (folder) => {
        setCurrentFolder(folder);
        setBreadcrumb(prev => [...prev, folder]);
        logActivity('OPEN_FOLDER', { folderName: folder.name });
    };

    const handleBreadcrumbClick = (index) => {
        if (index === -1) {
            // Go to root
            setCurrentFolder(null);
            setBreadcrumb([]);
        } else {
            // Go to specific folder in breadcrumb
            const targetFolder = breadcrumb[index];
            setCurrentFolder(targetFolder);
            setBreadcrumb(prev => prev.slice(0, index + 1));
        }
    };

    // File Delete Handler
    const handleDeleteFile = async (file) => {
        setFiles(prev => prev.filter(f => f.id !== file.id));
        logActivity('DELETE', { fileName: file.fileName });
    };

    // File Download Handler
    const handleDownloadFile = (file) => {
        const link = document.createElement('a');
        link.href = file.downloadUrl;
        link.download = file.fileName;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        logActivity('DOWNLOAD', { fileName: file.fileName });
    };

    // File Click Handler
    const handleFileClick = (file) => {
        setModalFile(file);
        setIsModalOpen(true);
    };

    // Get current folder's files and subfolders
    const getCurrentFolderItems = () => {
        const currentFolderId = currentFolder?.id || null;
        const currentFiles = files.filter(file => file.folderId === currentFolderId);
        const currentFolders = folders.filter(folder => folder.parentId === currentFolderId);
        return { currentFiles, currentFolders };
    };

    // My Files View
    const MyFilesView = () => {
        const { currentFiles, currentFolders } = getCurrentFolderItems();

        return (
            <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
                {/* Breadcrumb Navigation */}
                <div className="mb-4">
                    <nav className="flex items-center space-x-2 text-sm">
                        <button 
                            onClick={() => handleBreadcrumbClick(-1)}
                            className="text-blue-600 hover:text-blue-800 transition"
                        >
                            Home
                        </button>
                        {breadcrumb.map((folder, index) => (
                            <React.Fragment key={folder.id}>
                                <span className="text-gray-400">/</span>
                                <button 
                                    onClick={() => handleBreadcrumbClick(index)}
                                    className="text-blue-600 hover:text-blue-800 transition"
                                >
                                    {folder.name}
                                </button>
                            </React.Fragment>
                        ))}
                    </nav>
                </div>

                <div className="flex flex-col sm:flex-row justify-between items-center mb-6 gap-4 border-b pb-4">
                    <h2 className="text-xl font-semibold flex-shrink-0" style={{color: COLORS.textColor}}>
                        {currentFolder ? `${currentFolder.name} (${currentFiles.length} files, ${currentFolders.length} folders)` : `My Files (${currentFiles.length} files, ${currentFolders.length} folders)`}
                    </h2>
                    <div className="flex gap-3">
                        <button 
                            onClick={() => setIsNewFolderModalOpen(true)}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                            style={{backgroundColor: COLORS.darkSage}}
                        >
                            <PlusIcon className="h-5 w-5 mr-2" />
                            New Folder
                        </button>

                        <input type="file" ref={fileInputRef} onChange={handleFileUpload} className="hidden" />
                        <button 
                            onClick={() => !isUploading && fileInputRef.current?.click()}
                            disabled={isUploading}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md disabled:opacity-50"
                            style={{backgroundColor: COLORS.sage}}
                        >
                            <UploadIcon className="h-5 w-5 mr-2" />
                            {isUploading ? 'Uploading...' : 'Upload File'}
                        </button>
                    </div>
                </div>

                {/* Folders Section */}
                {currentFolders.length > 0 && (
                    <div className="mb-6">
                        <h3 className="text-lg font-medium mb-3" style={{color: COLORS.textColor}}>Folders</h3>
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                            {currentFolders.map(folder => (
                                <div 
                                    key={folder.id} 
                                    className="p-4 border rounded-lg hover:shadow-md transition cursor-pointer hover:bg-gray-50" 
                                    style={{borderColor: COLORS.lightSage}}
                                    onClick={() => handleFolderClick(folder)}
                                >
                                    <FolderIcon className="h-8 w-8 mb-2" style={{color: COLORS.sage}} />
                                    <p className="font-medium text-sm" style={{color: COLORS.textColor}}>{folder.name}</p>
                                    <p className="text-xs text-gray-500">{new Date(folder.createdDate).toLocaleDateString()}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Files Section */}
                <div className="overflow-x-auto min-h-[400px]">
                    {currentFiles.length === 0 && currentFolders.length === 0 ? (
                        <div className="p-10 text-center text-gray-500">
                            <FileTextIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                            <p>{currentFolder ? `This folder is empty. Start by uploading files or creating folders.` : `No files uploaded yet. Start by clicking "Upload File".`}</p>
                        </div>
                    ) : currentFiles.length === 0 ? (
                        <div className="p-10 text-center text-gray-500">
                            <FileTextIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                            <p>No files in this folder. Upload some files to get started.</p>
                        </div>
                    ) : (
                        <table className="min-w-full text-left border-collapse">
                            <thead className="border-b bg-gray-50">
                                <tr>
                                    <th className="p-4 font-medium text-gray-600">File Name</th>
                                    <th className="p-4 font-medium text-gray-600">Type</th>
                                    <th className="p-4 font-medium text-gray-600">Size</th>
                                    <th className="p-4 font-medium text-gray-600">Date</th>
                                    <th className="p-4 font-medium text-gray-600">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {currentFiles.map(file => (
                                    <tr key={file.id} className="border-b hover:bg-gray-50 transition duration-150 cursor-pointer">
                                        <td className="p-4 flex items-center gap-3" onClick={() => handleFileClick(file)}>
                                            <FileIconByType type={file.fileType} />
                                            <p className="font-semibold" style={{color: COLORS.textColor}}>{file.fileName}</p>
                                        </td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>{file.fileType}</td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>{formatFileSize(file.fileSize)}</td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>{new Date(file.uploadDate).toLocaleDateString()}</td>
                                        <td className="p-4">
                                            <button
                                                onClick={(e) => { e.stopPropagation(); handleFileClick(file); }}
                                                className="text-sm px-3 py-1 rounded-full font-medium transition"
                                                style={{backgroundColor: COLORS.mint, color: COLORS.mintText}}
                                            >
                                                View Details
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        );
    };

    // History View
    const HistoryView = () => (
        <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
            <h2 className="text-xl font-semibold mb-6 border-b pb-4" style={{color: COLORS.textColor}}>Upload History</h2>

            <div className="overflow-x-auto min-h-[400px]">
                {files.length === 0 ? (
                    <div className="p-10 text-center text-gray-500">
                        <HistoryIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                        <p>No upload history found.</p>
                    </div>
                ) : (
                    <table className="min-w-full text-left border-collapse">
                        <thead className="border-b bg-gray-50">
                            <tr>
                                <th className="p-4 font-medium text-gray-600">File Name</th>
                                <th className="p-4 font-medium text-gray-600">Type</th>
                                <th className="p-4 font-medium text-gray-600">Size</th>
                                <th className="p-4 font-medium text-gray-600">Upload Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {files.map(file => (
                                <tr key={file.id} className="border-b hover:bg-gray-50 transition duration-150">
                                    <td className="p-4 flex items-center gap-3">
                                        <FileIconByType type={file.fileType} />
                                        <p className="font-semibold" style={{color: COLORS.textColor}}>{file.fileName}</p>
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{file.fileType}</td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{formatFileSize(file.fileSize)}</td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{new Date(file.uploadDate).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );

    // Activity Logs View
    const ActivityLogView = () => (
        <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
            <h2 className="text-xl font-semibold mb-6 border-b pb-4" style={{color: COLORS.textColor}}>Activity Logs</h2>

            <div className="overflow-x-auto min-h-[400px]">
                {activityLogs.length === 0 ? (
                    <div className="p-10 text-center text-gray-500">
                        <ZapIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                        <p>No activity logs found. Start interacting with your files!</p>
                    </div>
                ) : (
                    <table className="min-w-full text-left border-collapse">
                        <thead className="border-b bg-gray-50">
                            <tr>
                                <th className="p-4 font-medium text-gray-600">Action</th>
                                <th className="p-4 font-medium text-gray-600">Details</th>
                                <th className="p-4 font-medium text-gray-600">Timestamp</th>
                            </tr>
                        </thead>
                        <tbody>
                            {activityLogs.map(log => (
                                <tr key={log.id} className="border-b hover:bg-gray-50 transition duration-150">
                                    <td className={`p-4 font-semibold ${
                                        log.action === 'DELETE' ? 'text-red-600' : 
                                        log.action === 'UPLOAD' ? 'text-green-600' : 
                                        'text-blue-600'
                                    }`}>
                                        {log.action}
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>
                                        {log.details.fileName || log.details.folderName}
                                        {log.details.fileSize && ` (${log.details.fileSize})`}
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>
                                        {new Date(log.timestamp).toLocaleString()}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );

    const renderContent = () => {
        switch(activeView) {
            case 'My Files':
                return <MyFilesView />;
            case 'History':
                return <HistoryView />;
            case 'Activity Logs':
                return <ActivityLogView />;
            default:
                return <MyFilesView />;
        }
    };

    return (
        <div className="flex h-screen font-sans antialiased" style={{backgroundColor: COLORS.offWhite}}>
            <Sidebar activeView={activeView} setActiveView={setActiveView} />
            <main className="flex-1 flex flex-col overflow-hidden">
                <Header title={activeView} />
                <div className="flex-1 p-6 overflow-y-auto">
                    {renderContent()}
                </div>
            </main>

            <FileDetailModal
                file={modalFile}
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onDeleteFile={handleDeleteFile}
                onDownloadFile={handleDownloadFile}
            />

            <NewFolderModal
                isOpen={isNewFolderModalOpen}
                onClose={() => setIsNewFolderModalOpen(false)}
                onCreateFolder={handleCreateFolder}
            />
        </div>
    );
}
