// HERO SLIDE COMPONENT (Modernized)
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault protects your most important files with next-generation encryption, powered by AI and cloud security.",
      image: "/image1.jpg",
      alt: "Cyber fortress and digital protection",
    },
    {
      title: "Bank-Grade",
      highlight: "Encryption",
      description:
        "We use cutting-edge AES-256 encryption to keep your digital identity locked and protected at all times.",
      image: "/image2.jpg",
      alt: "Encryption and cyber lock",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Access your documents securely from any device. Sync instantly with cloud backups and smart categorization.",
      image: "/image3.jpg",
      alt: "Access documents globally",
    },
    { 
      title: "Smart",
      highlight: "Organization",
      description: "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.", 
      image: "/image4.jpg",
      alt: "Document organization and filing" 
    }
  ];

  // Auto-play
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [isAutoPlay, slides.length]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 8000);
  };

  return (
    <section className="relative h-[70vh] flex items-center justify-center overflow-hidden" style={{ backgroundColor: COLORS.white }}>
      {/* Background Image with Parallax Effect */}
      <div className="absolute inset-0">
        <motion.div
          key={`bg-${currentSlide}`}
          initial={{ scale: 1.1, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1.2, ease: "easeOut" }}
          className="w-full h-full"
        >
          <img
            src={slides[currentSlide].image}
            alt={slides[currentSlide].alt}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-r from-black/70 via-black/40 to-transparent" />
        </motion.div>
      </div>

      {/* Content */}
      <div className="relative z-20 w-full px-6 max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
        {/* Text Content */}
        <motion.div
          key={`text-${currentSlide}`}
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="text-left"
        >
          <motion.h1 
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="text-4xl lg:text-6xl font-bold leading-tight mb-6 text-white" 
            style={{ fontFamily: 'Inter, sans-serif', fontWeight: 700 }}
          >
            {slides[currentSlide].title}{" "}
            <span className="bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
              {slides[currentSlide].highlight}
            </span>
          </motion.h1>

          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            className="text-xl leading-relaxed mb-8 text-gray-200" 
            style={{ fontFamily: 'Lato, sans-serif' }}
          >
            {slides[currentSlide].description}
          </motion.p>

          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
            className="flex flex-wrap gap-4"
          >
            {isAuthenticated ? (
              <button
                onClick={handleDashboardNavigation}
                className="text-white font-semibold py-4 px-8 rounded-lg flex items-center transition-all duration-300 transform hover:scale-105 hover:shadow-xl"
                style={{backgroundColor: COLORS.primary, fontFamily: 'Inter, sans-serif', fontWeight: 600}}
              >
                Go to Dashboard <ArrowRight className="ml-2 w-5 h-5" />
              </button>
            ) : (
              <>
                <button
                  onClick={() => navigate("/register")}
                  className="text-white font-semibold py-4 px-8 rounded-lg transition-all duration-300 flex items-center transform hover:scale-105 hover:shadow-xl"
                  style={{backgroundColor: COLORS.primary, fontFamily: 'Inter, sans-serif', fontWeight: 600}}
                >
                  Start Protecting My Files <ArrowRight className="ml-2 w-5 h-5" />
                </button>
                <button
                  onClick={() => scrollToSection("features")}
                  className="font-semibold py-4 px-8 rounded-lg border-2 border-white/30 backdrop-blur-sm transition-all duration-300 text-white hover:bg-white/10"
                  style={{ fontFamily: 'Inter, sans-serif', fontWeight: 600 }}
                >
                  Learn More
                </button>
              </>
            )}
          </motion.div>
        </motion.div>

        {/* Slide Indicators */}
        <div className="flex lg:justify-end justify-center items-end">
          <motion.div 
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 1 }}
            className="flex flex-col gap-4"
          >
            {slides.map((slide, index) => (
              <motion.button
                key={index}
                onClick={() => handleSlideChange(index)}
                className={`group flex items-center gap-3 p-3 rounded-lg backdrop-blur-sm transition-all duration-300 ${
                  currentSlide === index
                    ? "bg-white/20 border border-white/30"
                    : "bg-black/20 border border-white/10 hover:bg-white/10"
                }`}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <div className={`w-3 h-3 rounded-full transition-all duration-300 ${
                  currentSlide === index ? "bg-white" : "bg-white/50 group-hover:bg-white/70"
                }`} />
                <span className={`text-sm font-medium transition-all duration-300 ${
                  currentSlide === index ? "text-white" : "text-white/70 group-hover:text-white/90"
                }`}>
                  {slide.title} {slide.highlight}
                </span>
              </motion.button>
            ))}
          </motion.div>
        </div>
      </div>
    </section>
  );
};

