import React, { useState, useEffect, useRef } from 'react';

// NOTE: This application requires Chart.js to be globally loaded (window.Chart) 
// for the analytics graphs to function correctly. This is usually done via a CDN 
// link in the index.html file of the hosting environment.

// --- SVG Icon Components (Lucide Icons) ---
const HomeIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="m3 9 9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path><polyline points="9 22 9 12 15 12 15 22"></polyline></svg>);
const UsersIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2"></path><circle cx="9" cy="7" r="4"></circle><path d="M22 21v-2a4 4 0 0 0-3-3.87"></path><path d="M16 3.13a4 4 0 0 1 0 7.75"></path></svg>);
const FileIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z"></path><polyline points="14 2 14 8 20 8"></polyline></svg>);
const AlertCircleIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="8" x2="12" y2="12"></line><line x1="12" y1="16" x2="12.01" y2="16"></line></svg>);
const SearchIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>);
const MoreVerticalIcon = ({ className = "h-5 w-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="1"></circle><circle cx="12" cy="5" r="1"></circle><circle cx="12" cy="19" r="1"></circle></svg>);

// --- Mock Data ---
const initialUsers = [
    { id: 1, name: 'Alice Johnson', email: 'alice.j@example.com', role: 'Premium', storageUsed: 15.2, joined: '2023-01-15', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026704d` },
    { id: 2, name: 'Bob Williams', email: 'bob.w@example.com', role: 'Standard', storageUsed: 4.8, joined: '2023-02-20', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026705d` },
    { id: 3, name: 'Charlie Brown', email: 'charlie.b@example.com', role: 'Standard', storageUsed: 9.1, joined: '2023-03-10', status: 'Suspended', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026706d` },
    { id: 4, name: 'Diana Miller', email: 'diana.m@example.com', role: 'Premium', storageUsed: 25.6, joined: '2023-04-05', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026707d` },
    { id: 5, name: 'Ethan Davis', email: 'ethan.d@example.com', role: 'Free', storageUsed: 1.2, joined: '2023-05-22', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026708d` },
    { id: 6, name: 'Fiona Green', email: 'fiona.g@example.com', role: 'Premium', storageUsed: 30.5, joined: '2023-06-11', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026709d` },
    { id: 7, name: 'George Hall', email: 'george.h@example.com', role: 'Standard', storageUsed: 6.7, joined: '2023-07-01', status: 'Active', avatar: `https://i.pravatar.cc/150?u=a042581f4e29026710d` },
];

const stats = [
    { name: 'Total Users', value: '1,428', change: '+12.5%', icon: UsersIcon, color: 'bg-blue-500' },
    { name: 'Total Files', value: '25,690', change: '+8.2%', icon: FileIcon, color: 'bg-green-500' },
    { name: 'Storage Used', value: '1.2 TB', change: '+2.1%', icon: HomeIcon, color: 'bg-yellow-500' },
    { name: 'Security Alerts', value: '4', change: 'New', icon: AlertCircleIcon, color: 'bg-red-500' }
];

const monthlyUsageData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    data: [350, 420, 500, 610, 750, 800, 780, 920, 1050, 1150, 1200, 1350],
};

const fileTypeData = {
    labels: ['Documents', 'Images', 'Videos', 'Archives', 'Others'],
    data: [45, 30, 15, 5, 5], // Percentages
};

const demographicsData = {
    labels: ['Standard', 'Premium', 'Enterprise'],
    data: [60, 30, 10], // Percentages
    colors: ['#3b82f6', '#10b981', '#f59e0b']
};

// --- Chart/Analytics Components ---

const ChartFallback = () => (
    <div className="flex items-center justify-center h-full bg-red-50 border border-red-200 rounded-lg p-4 text-red-700">
        <AlertCircleIcon className="h-6 w-6 mr-3 flex-shrink-0" />
        <p className="text-sm font-medium">
            Chart.js is not loaded globally. Please ensure the library CDN is available in the environment to display graphs.
        </p>
    </div>
);

const ChartContainer = ({ title, children }) => (
    <div className="bg-white p-6 rounded-xl shadow-md h-full flex flex-col">
        <h3 className="text-lg font-semibold text-gray-800 mb-4 border-b pb-2">{title}</h3>
        <div className="flex-1 min-h-[250px] relative">
            {children}
        </div>
    </div>
);

// Generic chart creation function
const useChart = (config) => {
    const chartRef = useRef(null);

    useEffect(() => {
        if (chartRef.current && typeof window.Chart !== 'undefined') {
            const ctx = chartRef.current.getContext('2d');
            
            // Destroy existing chart instance if it exists to prevent memory leaks/errors
            if (chartRef.current.chartInstance) {
                chartRef.current.chartInstance.destroy();
            }

            // Create new chart instance and store it on the ref
            chartRef.current.chartInstance = new window.Chart(ctx, config);

            // Cleanup function
            return () => {
                if (chartRef.current && chartRef.current.chartInstance) {
                    chartRef.current.chartInstance.destroy();
                }
            };
        }
    }, [config.data.datasets, config.data.labels]); // Depend on configuration data

    if (typeof window.Chart === 'undefined') {
        return <ChartFallback />;
    }

    return <canvas ref={chartRef} className="w-full h-full"></canvas>;
};

const LineChart = () => {
    const config = {
        type: 'line',
        data: {
            labels: monthlyUsageData.labels,
            datasets: [{
                label: 'Monthly Storage Usage (GB)',
                data: monthlyUsageData.data,
                borderColor: '#3b82f6',
                backgroundColor: 'rgba(59, 130, 246, 0.2)',
                fill: true,
                tension: 0.4
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
                y: { beginAtZero: true, title: { display: true, text: 'Usage (GB)' } }
            },
            plugins: { legend: { display: true } }
        }
    };
    return useChart(config);
};

const PieChart = () => {
    const config = {
        type: 'doughnut',
        data: {
            labels: demographicsData.labels,
            datasets: [{
                label: 'User Demographics',
                data: demographicsData.data,
                backgroundColor: demographicsData.colors,
                hoverOffset: 8
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
                legend: { position: 'right' },
                title: { display: false }
            }
        }
    };
    return useChart(config);
};

const BarChart = () => {
    const config = {
        type: 'bar',
        data: {
            labels: fileTypeData.labels,
            datasets: [{
                label: 'Storage Breakdown (%)',
                data: fileTypeData.data,
                backgroundColor: [
                    '#10b981', '#f59e0b', '#ef4444', '#3b82f6', '#6b7280'
                ],
                borderColor: '#fff',
                borderWidth: 1
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
                y: {
                    beginAtZero: true,
                    max: 100,
                    ticks: { callback: (value) => value + '%' }
                }
            },
            plugins: { legend: { display: false } }
        }
    };
    return useChart(config);
};


// --- Layout Components ---

const Sidebar = ({ activeView, setActiveView }) => {
    const navItems = [
        { name: 'Dashboard', icon: HomeIcon },
        { name: 'Users', icon: UsersIcon },
        { name: 'Queries', icon: AlertCircleIcon },
    ];
    
    return (
        <aside className="w-64 flex-shrink-0 bg-gray-800 text-gray-300 flex flex-col p-4 shadow-xl">
            <div className="text-white text-2xl font-bold mb-10 flex items-center gap-2">
                <svg xmlns="http://www.w3.org/2000/svg" width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect><path d="M7 11V7a5 5 0 0 1 10 0v4"></path></svg>
                <span>Digital Locker</span>
            </div>
            <nav className="flex-grow">
                <ul>
                    {navItems.map(item => (
                        <li key={item.name}>
                            <a
                                href="#"
                                onClick={(e) => {e.preventDefault(); setActiveView(item.name);}}
                                className={`flex items-center gap-3 px-4 py-3 my-1 rounded-lg transition-colors duration-200 ${
                                    activeView === item.name
                                        ? 'bg-blue-600 text-white shadow-md font-semibold'
                                        : 'hover:bg-gray-700 hover:text-white'
                                }`}
                            >
                                <item.icon />
                                <span>{item.name}</span>
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>
        </aside>
    );
};

const Header = ({ title }) => {
    return (
        <header className="bg-white sticky top-0 z-10 shadow-sm p-4 flex justify-between items-center">
            <h1 className="text-2xl font-semibold text-gray-800">{title}</h1>
            <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                    <img src={`https://i.pravatar.cc/150?u=admin`} onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/150x150/f0f0f0/666?text=AD" }} alt="Admin Avatar" className="h-10 w-10 rounded-full object-cover ring-2 ring-blue-500" />
                    <div>
                        <p className="font-semibold text-sm text-gray-800">Admin User</p>
                        <p className="text-xs text-gray-500">System Administrator</p>
                    </div>
                </div>
            </div>
        </header>
    );
};

const StatCard = ({ item }) => (
    <div className="bg-white p-6 rounded-xl shadow-md flex items-start justify-between transition-transform hover:scale-[1.02] cursor-pointer ring-1 ring-gray-100">
        <div>
            <p className="text-sm font-medium text-gray-500">{item.name}</p>
            <p className="text-3xl font-bold text-gray-800 mt-1">{item.value}</p>
            <p className={`text-xs mt-2 font-medium ${item.change.startsWith('+') ? 'text-green-600' : (item.change === 'New' ? 'text-red-600' : 'text-red-600')}`}>
                {item.change} vs last period
            </p>
        </div>
        <div className={`${item.color} p-3 rounded-full text-white shadow-lg`}>
            <item.icon className="h-6 w-6" />
        </div>
    </div>
);

const DashboardView = () => (
    <div className="space-y-6">
        <h2 className="text-xl font-semibold text-gray-700">System Overview</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {stats.map(item => (
                <StatCard key={item.name} item={item} />
            ))}
        </div>
        
        <h2 className="text-xl font-semibold text-gray-700 pt-2">Analytics & Usage Statistics</h2>
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-2 min-h-[400px]">
                <ChartContainer title="Monthly Storage Usage (GB) - Trend">
                    <LineChart />
                </ChartContainer>
            </div>
            <div className="lg:col-span-1 min-h-[400px]">
                <ChartContainer title="User Demographics by Plan - Proportion">
                    <PieChart />
                </ChartContainer>
            </div>
        </div>

        <div className="pt-2 min-h-[400px]">
            <ChartContainer title="Storage Breakdown by File Type - Comparison">
                <BarChart />
            </ChartContainer>
        </div>
    </div>
);

const UserTable = ({ users, searchTerm, setSearchTerm }) => (
    <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
        <div className="flex flex-col sm:flex-row justify-between items-center mb-6 gap-4">
            <h2 className="text-xl font-semibold text-gray-800 flex-shrink-0">All Registered Users</h2>
            <div className="relative w-full sm:max-w-sm">
                <input
                    type="text"
                    placeholder="Search users by name or email..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg w-full focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none transition"
                />
                <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            </div>
        </div>
        <div className="overflow-x-auto">
            <table className="min-w-full text-left border-collapse">
                <thead className="border-b bg-gray-50">
                    <tr>
                        <th className="p-4 font-medium text-gray-600">Name</th>
                        <th className="p-4 font-medium text-gray-600">Role</th>
                        <th className="p-4 font-medium text-gray-600">Storage Used (GB)</th>
                        <th className="p-4 font-medium text-gray-600">Joined Date</th>
                        <th className="p-4 font-medium text-gray-600">Status</th>
                        <th className="p-4 font-medium text-gray-600">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.length > 0 ? (
                        users.map(user => (
                            <tr key={user.id} className="border-b hover:bg-blue-50/50 transition duration-150">
                                <td className="p-4 flex items-center gap-3">
                                    <img 
                                        src={user.avatar} 
                                        onError={(e) => { e.target.onerror = null; e.target.src=`https://placehold.co/150x150/3b82f6/fff?text=${user.name.charAt(0)}` }} 
                                        alt={user.name} 
                                        className="h-10 w-10 rounded-full object-cover ring-1 ring-gray-300" 
                                    />
                                    <div>
                                        <p className="font-semibold text-gray-800">{user.name}</p>
                                        <p className="text-sm text-gray-500">{user.email}</p>
                                    </div>
                                </td>
                                <td className="p-4 text-gray-700">{user.role}</td>
                                <td className="p-4 text-gray-700">{user.storageUsed} GB</td>
                                <td className="p-4 text-gray-700">{user.joined}</td>
                                <td className="p-4">
                                    <span className={`px-3 py-1 text-xs font-semibold rounded-full shadow-sm ${
                                        user.status === 'Active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                                    }`}>
                                        {user.status}
                                    </span>
                                </td>
                                <td className="p-4">
                                    <button className="text-gray-500 hover:text-blue-600 p-1 rounded-full hover:bg-gray-100 transition">
                                        <MoreVerticalIcon />
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" className="p-8 text-center text-gray-500 bg-gray-50 rounded-b-xl">
                                No users found matching "{searchTerm}".
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    </div>
);

const UsersView = () => {
    const [searchTerm, setSearchTerm] = useState('');
    
    // Filter logic
    const filteredUsers = initialUsers.filter(user =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <UserTable users={filteredUsers} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
    );
};

const QueriesView = () => (
    <div className="bg-white p-10 rounded-xl shadow-md ring-1 ring-gray-100 mt-6 flex flex-col items-center justify-center text-center h-[70vh]">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">User Support Queries & Alerts</h2>
        <AlertCircleIcon className="h-16 w-16 text-red-500 mb-6" />
        <p className="text-gray-600 max-w-lg">
            This section will manage real-time support requests and security alerts from the system, allowing the administration team to address issues quickly.
        </p>
        <button className="mt-8 bg-indigo-600 text-white py-3 px-6 rounded-lg font-semibold shadow-lg hover:bg-indigo-700 transition transform hover:scale-[1.02]">
            View Active Tickets (4 Open)
        </button>
    </div>
);


// --- Main App Component ---

export default function App() {
    const [activeView, setActiveView] = useState('Dashboard');

    const renderContent = () => {
        switch(activeView) {
            case 'Dashboard':
                return <DashboardView />;
            case 'Users':
                return <UsersView />;
            case 'Queries':
                return <QueriesView />;
            default:
                return <DashboardView />;
        }
    };

    return (
        <div className="flex h-screen bg-gray-100 font-sans antialiased">
            <Sidebar activeView={activeView} setActiveView={setActiveView} />
            <main className="flex-1 flex flex-col overflow-hidden">
                <Header title={activeView} />
                <div className="flex-1 p-6 overflow-y-auto">
                    {renderContent()}
                </div>
            </main>
        </div>
    );
}
