import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar({ user, onLogout }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("user");
    onLogout();
    navigate("/login");
  };

  const handlePreviewAdmin = () => {
    if (typeof window !== 'undefined' && window.confirm('Preview as Admin? This sets a temporary demo user in localStorage.')) {
      // onPreview will be supplied by App to set demo user
      if (typeof onLogout === 'function') {
        // reuse onLogout to clear first
        onLogout();
      }
      // navigate to dashboard for preview
      navigate('/dashboard');
    }
  };

  return (
    <nav className="bg-gray-900 text-white shadow-md px-6 py-4 flex justify-between items-center">
      {/* Left side - Logo and links */}
      <div className="flex items-center space-x-8">
        <Link
          to={user ? "/dashboard" : "/"}
          className="font-extrabold text-xl hover:text-gray-300 transition-colors"
        >
          DocuManage
        </Link>

        {user?.role === "user" && (
          <div className="flex space-x-4">
            <Link
              to="/dashboard"
              className="hover:text-gray-300 transition-colors"
            >
              My Files
            </Link>
            <Link
              to="/upload"
              className="hover:text-gray-300 transition-colors"
            >
              Upload
            </Link>
            <Link
              to="/history"
              className="hover:text-gray-300 transition-colors"
            >
              History
            </Link>
          </div>
        )}

        {user?.role === "admin" && (
          <div className="flex space-x-4">
            <Link
              to="/dashboard"
              className="hover:text-gray-300 transition-colors"
            >
              Admin Panel
            </Link>
            <Link
              to="/users"
              className="hover:text-gray-300 transition-colors"
            >
              Users
            </Link>
            <Link
              to="/system"
              className="hover:text-gray-300 transition-colors"
            >
              System
            </Link>
          </div>
        )}
      </div>

      {/* Right side - User info and logout */}
        <div className="flex items-center space-x-4">
          {user ? (
            <>
              <span className="font-medium">{user?.name}</span>
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 transition-colors px-4 py-2 rounded-lg font-semibold"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="px-4 py-2 rounded hover:bg-gray-800 transition">Login</Link>
              <Link to="/register" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition">Register</Link>
              <button
                onClick={() => { if (typeof onLogout === 'function') onLogout(); /* placeholder */ window.localStorage.setItem('user', JSON.stringify({ name: 'Admin (preview)', role: 'admin' })); navigate('/dashboard'); }}
                className="ml-2 px-3 py-1 bg-gray-700 text-white rounded text-sm"
              >
                Preview Admin
              </button>
              <button
                onClick={() => { if (typeof onLogout === 'function') onLogout(); /* placeholder */ window.localStorage.setItem('user', JSON.stringify({ name: 'User (preview)', role: 'user' })); navigate('/dashboard'); }}
                className="ml-2 px-3 py-1 bg-gray-700 text-white rounded text-sm"
              >
                Preview User
              </button>
            </>
          )}
        </div>
    </nav>
  );
}
