// Dropbox-style Activity Log View
export const DropboxActivityLogView = ({ activityLogs }) => {
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);

    const paginatedLogs = paginateData(activityLogs, currentPage, itemsPerPage);
    const currentLogs = paginatedLogs.data;

    const formatActivityDetails = (log) => {
        const details = log.details || {};
        let description = '';

        switch (log.action) {
            case 'UPLOAD':
                description = `Uploaded file: ${details.fileName || 'Unknown file'}`;
                if (details.fileSize) description += ` (${details.fileSize})`;
                break;
            case 'DOWNLOAD':
                description = `Downloaded file: ${details.fileName || 'Unknown file'}`;
                break;
            case 'DELETE':
                description = `Deleted file: ${details.fileName || 'Unknown file'}`;
                break;
            case 'RENAME':
                description = `Renamed file from "${details.oldName || 'Unknown'}" to "${details.newName || details.fileName || 'Unknown'}"`;
                break;
            case 'VIEW':
                description = `Viewed file: ${details.fileName || 'Unknown file'}`;
                break;
            case 'CREATE_FOLDER':
                description = `Created folder: ${details.folderName || 'Unknown folder'}`;
                break;
            case 'OPEN_FOLDER':
                description = `Opened folder: ${details.folderName || 'Unknown folder'}`;
                break;
            default:
                description = `${log.action}: ${details.fileName || details.folderName || 'Unknown item'}`;
        }

        return description;
    };

    const getActionColor = (action) => {
        switch (action) {
            case 'DELETE': return 'bg-red-100 text-red-800';
            case 'UPLOAD': case 'CREATE_FOLDER': return 'bg-green-100 text-green-800';
            case 'DOWNLOAD': case 'VIEW': return 'bg-blue-100 text-blue-800';
            case 'RENAME': return 'bg-orange-100 text-orange-800';
            case 'OPEN_FOLDER': return 'bg-purple-100 text-purple-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    const formatTimestamp = (timestamp) => {
        try {
            const date = new Date(timestamp);
            if (isNaN(date.getTime())) {
                return 'Invalid date';
            }
            return date.toLocaleString('en-US', {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit',
                second: '2-digit'
            });
        } catch (error) {
            return 'Invalid date';
        }
    };

    return (
        <div className="h-full flex flex-col bg-white">
            {/* Header */}
            <div className="border-b border-gray-200 bg-white sticky top-0 z-10">
                <div className="px-6 py-4">
                    <h2 className="text-xl font-semibold text-gray-900">Activity Logs ({activityLogs.length} activities)</h2>
                </div>
            </div>

            {/* Content */}
            <div className="flex-1 p-6 overflow-y-auto">
                {activityLogs.length === 0 ? (
                    <div className="flex flex-col items-center justify-center h-96 text-center">
                        <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mb-6">
                            <ZapIcon className="w-12 h-12 text-gray-400" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900 mb-2">No activity yet</h3>
                        <p className="text-gray-500">Start interacting with your files to see activity here.</p>
                    </div>
                ) : (
                    <>
                        <div className="space-y-3">
                            {currentLogs.map(log => (
                                <div key={log.id || Math.random()} className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                                    <div className="flex justify-between items-start">
                                        <div className="flex-1">
                                            <div className="flex items-center gap-3 mb-2">
                                                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getActionColor(log.action)}`}>
                                                    {log.action}
                                                </span>
                                                <span className="text-sm text-gray-500">
                                                    {formatTimestamp(log.timestamp)}
                                                </span>
                                            </div>
                                            <p className="text-sm text-gray-900">
                                                {formatActivityDetails(log)}
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>

                        {/* Pagination */}
                        <div className="flex items-center justify-between mt-6 px-4 py-3 bg-white border border-gray-200 rounded-lg">
                            <div className="flex items-center gap-4">
                                <div className="flex items-center gap-2">
                                    <span className="text-sm text-gray-600">Show:</span>
                                    <select 
                                        value={itemsPerPage} 
                                        onChange={(e) => {
                                            setItemsPerPage(Number(e.target.value));
                                            setCurrentPage(1);
                                        }}
                                        className="border rounded px-2 py-1 text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    >
                                        <option value={10}>10</option>
                                        <option value={15}>15</option>
                                        <option value={20}>20</option>
                                        <option value={25}>25</option>
                                    </select>
                                </div>

                            </div>
                            {paginatedLogs.totalPages > 1 && (
                                <div className="flex items-center space-x-2">
                                    <button
                                        onClick={() => setCurrentPage(currentPage - 1)}
                                        disabled={currentPage === 1}
                                        className="px-3 py-1 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                                    >
                                        Previous
                                    </button>
                                    <span className="text-sm text-gray-700">
                                        Page {currentPage} of {paginatedLogs.totalPages}
                                    </span>
                                    <button
                                        onClick={() => setCurrentPage(currentPage + 1)}
                                        disabled={currentPage === paginatedLogs.totalPages}
                                        className="px-3 py-1 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                                    >
                                        Next
                                    </button>
                                </div>
                            )}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};
