import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Login from "./components/LoginForm";
import Register from "./components/RegisterForm";
import UserDashboard from "./components/UserDashboard";
import AdminDashboard from "./components/AdminDashboard";
import FileHistoryLog from "./components/FileHistoryLog";
import UploadPage from "./components/UploadPage";
import UsersPage from "./components/UsersPage";
import SystemPage from "./components/SystemPage";
// Optional: import Upload page if you have it
// import UploadPage from "./components/UploadPage";

function App() {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));

  const handleLogin = (userOrRole) => {
    // Accept either a role string (legacy) or a full user object
    if (userOrRole && typeof userOrRole === "object") {
      localStorage.setItem("user", JSON.stringify(userOrRole));
      setUser(userOrRole);
      return;
    }
    const role = userOrRole;
    const newUser = { name: role === "admin" ? "Admin" : "User", role };
    localStorage.setItem("user", JSON.stringify(newUser));
    setUser(newUser);
  };

  const handlePreview = (role) => {
    // Set a demo user in localStorage so protected routes become visible for preview
    const demo = { name: role === "admin" ? "Admin (preview)" : "User (preview)", role };
    localStorage.setItem("user", JSON.stringify(demo));
    setUser(demo);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <Router>
      {/* Navbar always rendered */}
  <Navbar user={user} onLogout={handleLogout} onPreview={handlePreview} />

      <main className="p-6">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Routes */}
          {user ? (
            <>
              {/* Dashboard route - dynamic based on role */}
              <Route
                path="/dashboard"
                element={
                  user.role === "admin" ? (
                    <AdminDashboard user={user} onLogout={handleLogout} />
                  ) : (
                    <UserDashboard user={user} onLogout={handleLogout} />
                  )
                }
              />

              {/* History / File logs */}
              <Route
                path="/history"
                element={<FileHistoryLog user={user} onLogout={handleLogout} />}
              />

              {/* Upload / Users / System pages */}
              <Route path="/upload" element={<UploadPage user={user} onLogout={handleLogout} />} />
              <Route path="/users" element={<UsersPage user={user} onLogout={handleLogout} />} />
              <Route path="/system" element={<SystemPage user={user} onLogout={handleLogout} />} />

              {/* Optional Upload page for users */}
              {/* <Route
                path="/upload"
                element={<UploadPage user={user} onLogout={handleLogout} />}
              /> */}

              {/* Catch-all redirect to dashboard */}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </>
          ) : (
            // If not logged in, redirect unknown routes to home
            <Route path="*" element={<Navigate to="/" />} />
          )}
        </Routes>
      </main>
    </Router>
  );
}

export default App;
