import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { COLORS, formatFileSize } from './DashboardUtils';

// SVG Icons
const XIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
    </svg>
);

const TrashIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
    </svg>
);

const DownloadIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
    </svg>
);

const FileTextIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
    </svg>
);

const HistoryIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
);

const ZapIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
    </svg>
);

const FolderIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z" />
    </svg>
);

// Modal Component
export const Modal = ({ title, children, onClose, isOpen }) => {
    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4" onClick={onClose}>
            <div 
                className="bg-white rounded-xl shadow-2xl max-w-lg w-full transform transition-all overflow-hidden"
                onClick={e => e.stopPropagation()}
            >
                <div className="p-4 flex justify-between items-center border-b" style={{backgroundColor: COLORS.lightSage}}>
                    <h3 className="text-xl font-semibold" style={{color: COLORS.textColor}}>{title}</h3>
                    <button onClick={onClose} className="text-gray-600 hover:text-red-500 p-2 rounded-full transition">
                        <XIcon className="h-6 w-6" />
                    </button>
                </div>
                <div className="p-6 max-h-[80vh] overflow-y-auto">
                    {children}
                </div>
            </div>
        </div>
    );
};

// New Folder Modal
export const NewFolderModal = ({ isOpen, onClose, onCreateFolder }) => {
    const [folderName, setFolderName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (folderName.trim()) {
            onCreateFolder(folderName.trim());
            setFolderName('');
            onClose();
        }
    };

    return (
        <Modal title="Create New Folder" isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        Folder Name
                    </label>
                    <input
                        type="text"
                        value={folderName}
                        onChange={(e) => setFolderName(e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-sage-500"
                        placeholder="Enter folder name"
                        autoFocus
                    />
                </div>
                <div className="flex justify-end gap-3 pt-4">
                    <button
                        type="button"
                        onClick={onClose}
                        className="px-4 py-2 text-gray-600 hover:text-gray-800 transition"
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                        style={{backgroundColor: COLORS.sage}}
                        disabled={!folderName.trim()}
                    >
                        Create Folder
                    </button>
                </div>
            </form>
        </Modal>
    );
};

// Eye Icon for View
const EyeIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
    </svg>
);

// Edit Icon for Rename
const EditIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
    </svg>
);

// File Detail Modal with Enhanced Features
export const FileDetailModal = ({ file, isOpen, onClose, onDeleteFile, onDownloadFile, onRenameFile, onViewFile }) => {
    const [isDeleting, setIsDeleting] = useState(false);
    const [isRenaming, setIsRenaming] = useState(false);
    const [newFileName, setNewFileName] = useState('');
    const [activeTab, setActiveTab] = useState('details');
    const [activityHistory, setActivityHistory] = useState([]);

    // Initialize rename field when modal opens
    React.useEffect(() => {
        if (file && isOpen) {
            setNewFileName(file.name || file.fileName || '');
            // Load activity history for this file
            loadFileActivity();
        }
    }, [file, isOpen]);

    if (!file) return null;

    const loadFileActivity = () => {
        if (!file) return;
        // Get real activity logs from localStorage or props
        const allLogs = JSON.parse(localStorage.getItem('activityLogs') || '[]');
        const fileSpecificLogs = allLogs.filter(log => 
            log.details?.fileName === (file.name || file.fileName) ||
            log.details?.fileId === file.id
        );
        setActivityHistory(fileSpecificLogs);
    };

    const handleDownload = () => {
        onDownloadFile(file);
    };

    const handleView = () => {
        if (onViewFile) {
            onViewFile(file);
        } else {
            // Default view behavior - open in new tab
            if (file.fileUrl) {
                window.open(file.fileUrl, '_blank');
            } else {
                alert('File preview not available');
            }
        }
    };

    const handleRename = async () => {
        if (!newFileName.trim() || newFileName === (file.name || file.fileName)) {
            setIsRenaming(false);
            return;
        }

        try {
            if (onRenameFile) {
                await onRenameFile(file.id, newFileName.trim());
                // Update local file object immediately
                file.name = newFileName.trim();
                file.fileName = newFileName.trim();
            }
            setIsRenaming(false);
        } catch (error) {
            console.error('Failed to rename file:', error);
            alert('Failed to rename file');
        }
    };

    const confirmDelete = async () => {
        if (!isDeleting) {
            setIsDeleting(true);
            try {
                await onDeleteFile(file);
                onClose();
            } catch (error) {
                console.error("Failed to delete file:", error);
            } finally {
                setIsDeleting(false);
            }
        }
    };

    const getFileExtension = (fileName) => {
        return fileName ? fileName.split('.').pop()?.toLowerCase() || 'unknown' : 'unknown';
    };

    const getCorrectFileSize = () => {
        const size = file.size || file.fileSize || 0;
        return formatFileSize(size);
    };

    const getCorrectFileName = () => {
        return file.name || file.fileName || 'Unknown File';
    };

    const getCorrectFileType = () => {
        const fileName = getCorrectFileName();
        const extension = getFileExtension(fileName);

        // Map extensions to readable types
        const typeMap = {
            'pdf': 'PDF Document',
            'doc': 'Word Document', 'docx': 'Word Document',
            'xls': 'Excel Spreadsheet', 'xlsx': 'Excel Spreadsheet',
            'ppt': 'PowerPoint Presentation', 'pptx': 'PowerPoint Presentation',
            'jpg': 'JPEG Image', 'jpeg': 'JPEG Image', 'png': 'PNG Image', 'gif': 'GIF Image',
            'txt': 'Text File',
            'zip': 'ZIP Archive', 'rar': 'RAR Archive',
            'mp4': 'MP4 Video', 'avi': 'AVI Video',
            'mp3': 'MP3 Audio', 'wav': 'WAV Audio'
        };

        return typeMap[extension] || file.fileType || `${extension.toUpperCase()} File`;
    };

    return (
        <Modal title={`File Details`} isOpen={isOpen} onClose={onClose}>
            <div className="space-y-4">
                {/* Tab Navigation */}
                <div className="flex border-b">
                    <button
                        onClick={() => setActiveTab('details')}
                        className={`px-4 py-2 font-medium ${activeTab === 'details' ? 'border-b-2 border-blue-500 text-blue-600' : 'text-gray-600'}`}
                    >
                        Details
                    </button>
                    <button
                        onClick={() => setActiveTab('history')}
                        className={`px-4 py-2 font-medium ${activeTab === 'history' ? 'border-b-2 border-blue-500 text-blue-600' : 'text-gray-600'}`}
                    >
                        History
                    </button>
                    <button
                        onClick={() => setActiveTab('activity')}
                        className={`px-4 py-2 font-medium ${activeTab === 'activity' ? 'border-b-2 border-blue-500 text-blue-600' : 'text-gray-600'}`}
                    >
                        Activity Logs
                    </button>
                </div>

                {/* Tab Content */}
                {activeTab === 'details' && (
                    <div className="space-y-4">
                        {/* File Name with Rename */}
                        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <p className="font-semibold text-sm">File Name</p>
                            <div className="flex items-center gap-2">
                                {isRenaming ? (
                                    <div className="flex items-center gap-2">
                                        <input
                                            type="text"
                                            value={newFileName}
                                            onChange={(e) => setNewFileName(e.target.value)}
                                            className="text-sm border rounded px-2 py-1"
                                            onKeyPress={(e) => e.key === 'Enter' && handleRename()}
                                            autoFocus
                                        />
                                        <button onClick={handleRename} className="text-green-600 hover:text-green-800">
                                            ‚úì
                                        </button>
                                        <button onClick={() => setIsRenaming(false)} className="text-red-600 hover:text-red-800">
                                            ‚úï
                                        </button>
                                    </div>
                                ) : (
                                    <div className="flex items-center gap-2">
                                        <p className="text-sm">{getCorrectFileName()}</p>
                                        <button
                                            onClick={() => setIsRenaming(true)}
                                            className="text-blue-600 hover:text-blue-800"
                                            title="Rename file"
                                        >
                                            <EditIcon className="h-4 w-4" />
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>

                        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <p className="font-semibold text-sm">File Type</p>
                            <p className="text-sm">{getCorrectFileType()}</p>
                        </div>

                        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <p className="font-semibold text-sm">File Size</p>
                            <p className="text-sm">{getCorrectFileSize()}</p>
                        </div>

                        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <p className="font-semibold text-sm">Upload Date</p>
                            <p className="text-sm">{new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleString()}</p>
                        </div>

                        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <p className="font-semibold text-sm">File Extension</p>
                            <p className="text-sm uppercase">.{getFileExtension(getCorrectFileName())}</p>
                        </div>

                        {file.parentFolder && (
                            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                <p className="font-semibold text-sm">Location</p>
                                <p className="text-sm">{file.parentFolder.name || 'Root Folder'}</p>
                            </div>
                        )}
                    </div>
                )}

                {activeTab === 'history' && (
                    <div className="space-y-3">
                        <h4 className="font-semibold text-gray-700">File History</h4>
                        <div className="space-y-2 max-h-60 overflow-y-auto">
                            <div className="p-3 bg-gray-50 rounded-lg">
                                <p className="text-sm font-medium">File Uploaded</p>
                                <p className="text-xs text-gray-600">{new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleString()}</p>
                            </div>
                            <div className="p-3 bg-gray-50 rounded-lg">
                                <p className="text-sm font-medium">Last Accessed</p>
                                <p className="text-xs text-gray-600">{new Date().toLocaleString()}</p>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'activity' && (
                    <div className="space-y-3">
                        <h4 className="font-semibold text-gray-700">Activity Logs</h4>
                        <div className="space-y-2 max-h-60 overflow-y-auto">
                            {activityHistory.map(activity => (
                                <div key={activity.id} className="p-3 bg-gray-50 rounded-lg">
                                    <div className="flex justify-between items-start">
                                        <div>
                                            <p className="text-sm font-medium">{activity.action}</p>
                                            <p className="text-xs text-gray-600">by {activity.user}</p>
                                        </div>
                                        <p className="text-xs text-gray-500">
                                            {new Date(activity.timestamp).toLocaleString()}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Action Buttons */}
                <div className="flex justify-between pt-4 border-t mt-6">
                    <button
                        onClick={handleView}
                        className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md bg-blue-500 hover:bg-blue-600"
                    >
                        <EyeIcon className="h-5 w-5 mr-2" />
                        View File
                    </button>

                    <div className="flex gap-3">
                        <button
                            onClick={confirmDelete}
                            disabled={isDeleting}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition bg-red-500 hover:bg-red-600 shadow-md disabled:opacity-50"
                        >
                            <TrashIcon className="h-5 w-5 mr-2" />
                            {isDeleting ? 'Deleting...' : 'Delete'}
                        </button>
                        <button
                            onClick={handleDownload}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                            style={{backgroundColor: COLORS.sage}}
                        >
                            <DownloadIcon className="h-5 w-5 mr-2" />
                            Download
                        </button>
                    </div>
                </div>
            </div>
        </Modal>
    );
};

// Sidebar Component
export const Sidebar = ({ activeView, setActiveView }) => {
    const { user } = useAuth();

    const navItems = [
        { name: 'My Files', icon: FileTextIcon },
        { name: 'History', icon: HistoryIcon },
        { name: 'Activity Logs', icon: ZapIcon },
    ];

    return (
        <aside className="w-64 flex-shrink-0 text-gray-800 flex flex-col p-4 shadow-xl" style={{backgroundColor: COLORS.sage}}>
            <div className="text-white text-2xl font-bold mb-6 flex items-center gap-2">
                <FolderIcon className="h-7 w-7" />
                <span>File Dashboard</span>
            </div>
            <div className="text-white text-xs mb-8 p-2 rounded-lg break-all" style={{backgroundColor: COLORS.darkSage}}>
                User: <span className="font-mono text-xs">{user?.email || 'Guest'}</span>
            </div>
            <nav className="flex-grow">
                <ul>
                    {navItems.map(item => (
                        <li key={item.name}>
                            <button
                                onClick={() => setActiveView(item.name)}
                                className={`flex items-center gap-3 px-4 py-3 my-1 rounded-lg transition-colors duration-200 w-full text-left ${
                                    activeView === item.name
                                        ? 'font-semibold shadow-md'
                                        : 'hover:text-white text-white'
                                }`}
                                style={{
                                    backgroundColor: activeView === item.name ? COLORS.mint : 'transparent',
                                    color: activeView === item.name ? COLORS.mintText : 'white'
                                }}
                            >
                                <item.icon className="h-5 w-5"/>
                                <span>{item.name}</span>
                            </button>
                        </li>
                    ))}
                </ul>
            </nav>
        </aside>
    );
};

// Profile Dropdown Component
const ProfileDropdown = () => {
    const { user, logout } = useAuth();
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="relative">
            <button
                onClick={() => setIsOpen(!isOpen)}
                className="h-10 w-10 rounded-full flex items-center justify-center text-white font-semibold hover:opacity-80 transition"
                style={{backgroundColor: COLORS.sage}}
            >
                {(user?.name || user?.email || 'U').charAt(0).toUpperCase()}
            </button>

            {isOpen && (
                <>
                    <div className="fixed inset-0 z-10" onClick={() => setIsOpen(false)} />
                    <div className="absolute right-0 mt-2 w-64 bg-white rounded-lg shadow-lg border z-20">
                        <div className="p-4 border-b">
                            <p className="font-semibold text-gray-800">{user?.name || 'User'}</p>
                            <p className="text-sm text-gray-600">{user?.email}</p>
                            <p className="text-xs text-gray-500 mt-1">Role: {user?.role || 'USER'}</p>
                        </div>
                        <div className="p-2">
                            <button
                                onClick={() => {
                                    logout();
                                    setIsOpen(false);
                                }}
                                className="w-full text-left px-3 py-2 text-sm text-red-600 hover:bg-red-50 rounded transition"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

// Header Component
export const Header = ({ title, backendConnected }) => {
    const { user } = useAuth();

    return (
        <header className="bg-white sticky top-0 z-10 shadow-sm p-4 flex justify-between items-center">
            <div className="flex items-center gap-4">
                <h1 className="text-2xl font-semibold" style={{color: COLORS.textColor}}>{title}</h1>
                {!backendConnected && (
                    <span className="px-2 py-1 text-xs bg-yellow-100 text-yellow-800 rounded-full">
                        Offline Mode
                    </span>
                )}
            </div>
            <div className="flex items-center gap-4">
                <div className="text-sm text-gray-600">
                    Welcome, {user?.name || user?.email || 'User'}
                </div>
                <ProfileDropdown />
            </div>
        </header>
    );
};

// File Icon Component with Better Type Detection
export const FileIconByType = ({ type, fileName }) => {
    let iconClass = "h-6 w-6 mr-3";

    // Get file extension for better type detection
    const getExtension = (name) => {
        if (!name) return '';
        return name.split('.').pop()?.toLowerCase() || '';
    };

    const extension = fileName ? getExtension(fileName) : '';

    // Enhanced type detection
    if (['jpg', 'jpeg', 'png', 'gif', 'bmp', 'svg', 'webp'].includes(extension) || type?.toLowerCase().includes('image')) {
        return <div className={`${iconClass} text-blue-500`}>üñºÔ∏è</div>;
    }
    if (['pdf'].includes(extension) || type?.toLowerCase().includes('pdf')) {
        return <div className={`${iconClass} text-red-500`}>üìÑ</div>;
    }
    if (['doc', 'docx', 'txt', 'rtf'].includes(extension) || type?.toLowerCase().includes('document') || type?.toLowerCase().includes('word')) {
        return <div className={`${iconClass} text-green-600`}>üìù</div>;
    }
    if (['xls', 'xlsx', 'csv'].includes(extension) || type?.toLowerCase().includes('excel') || type?.toLowerCase().includes('spreadsheet')) {
        return <div className={`${iconClass} text-green-700`}>üìä</div>;
    }
    if (['ppt', 'pptx'].includes(extension) || type?.toLowerCase().includes('powerpoint') || type?.toLowerCase().includes('presentation')) {
        return <div className={`${iconClass} text-orange-600`}>üìΩÔ∏è</div>;
    }
    if (['zip', 'rar', '7z', 'tar', 'gz'].includes(extension) || type?.toLowerCase().includes('archive')) {
        return <div className={`${iconClass} text-yellow-600`}>üì¶</div>;
    }
    if (['mp4', 'avi', 'mov', 'wmv', 'flv', 'webm'].includes(extension) || type?.toLowerCase().includes('video')) {
        return <div className={`${iconClass} text-purple-600`}>üé•</div>;
    }
    if (['mp3', 'wav', 'flac', 'aac', 'ogg'].includes(extension) || type?.toLowerCase().includes('audio')) {
        return <div className={`${iconClass} text-pink-600`}>üéµ</div>;
    }

    return <FileTextIcon className={`${iconClass} text-gray-500`} />;
};
