import React from 'react';
import { COLORS } from '../dashboard/DashboardUtils';

const XIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
    </svg>
);

const Modal = ({ 
    title, 
    children, 
    onClose, 
    isOpen, 
    size = 'medium',
    showCloseButton = true,
    className = ''
}) => {
    if (!isOpen) return null;

    const sizes = {
        small: 'max-w-md',
        medium: 'max-w-lg',
        large: 'max-w-2xl',
        xlarge: 'max-w-4xl'
    };

    return (
        <div 
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4" 
            onClick={onClose}
        >
            <div 
                className={`bg-white rounded-xl shadow-2xl ${sizes[size]} w-full transform transition-all overflow-hidden ${className}`}
                onClick={e => e.stopPropagation()}
            >
                {title && (
                    <div 
                        className="p-4 flex justify-between items-center border-b bg-gradient-to-r" 
                        style={{background: `linear-gradient(to right, ${COLORS.cardGradientFrom}, ${COLORS.cardGradientTo})`}}
                    >
                        <h3 className="text-xl font-semibold" style={{color: COLORS.primary}}>
                            {title}
                        </h3>
                        {showCloseButton && (
                            <button 
                                onClick={onClose} 
                                className="text-gray-600 hover:text-red-500 p-2 rounded-full transition"
                            >
                                <XIcon className="h-6 w-6" />
                            </button>
                        )}
                    </div>
                )}
                <div className="p-6 max-h-[80vh] overflow-y-auto">
                    {children}
                </div>
            </div>
        </div>
    );
};

export default Modal;
