// --- Chart Helper ---
const useChart = (config) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current && typeof window.Chart !== 'undefined') {
      const ctx = chartRef.current.getContext('2d');
      if (chartRef.current.chartInstance) chartRef.current.chartInstance.destroy();

      const chartInstance = new window.Chart(ctx, config);
      chartRef.current.chartInstance = chartInstance;

      return () => chartInstance.destroy();
    }
  }, [config]);

  if (typeof window.Chart === 'undefined') {
    return <div className="p-4 text-center text-red-600">Chart.js not loaded</div>;
  }

  return <canvas ref={chartRef} className="w-full h-full"></canvas>;
};

const LineChart = () => {
  const config = useMemo(() => ({
    type: 'line',
    data: {
      labels: monthlyUsageData.labels,
      datasets: [{
        label: 'Monthly Storage Usage (GB)',
        data: monthlyUsageData.data,
        borderColor: theme.sage,
        backgroundColor: theme.lightSage,
        fill: true,
        tension: 0.4
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: { legend: { display: true } }
    }
  }), []);

  return useChart(config);
};

const PieChart = () => {
  const config = useMemo(() => ({
    type: 'doughnut',
    data: {
      labels: demographicsData.labels,
      datasets: [{
        data: demographicsData.data,
        backgroundColor: demographicsData.colors
      }]
    },
    options: { responsive: true, maintainAspectRatio: false }
  }), []);

  return useChart(config);
};

