import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";

// Hero Carousel Component
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description: "SecureVault provides bank-grade encryption for your most important documents. Safe, organized, and accessible anywhere, anytime.",
      image: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=800&h=600&fit=crop&crop=center",
      alt: "Digital fortress and security"
    },
    {
      title: "Bank-Grade",
      highlight: "Security",
      description: "Military-level encryption ensures your documents are protected with the highest security standards available today.",
      image: "https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center",
      alt: "Security lock and protection"
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description: "Retrieve your files from any device, anywhere in the world. Your documents follow you wherever you go.",
      image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=800&h=600&fit=crop&crop=center",
      alt: "Global access and connectivity"
    },
    {
      title: "Smart",
      highlight: "Organization",
      description: "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.",
      image: "https://plus.unsplash.com/premium_photo-1683288662019-c92caea8276d?ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&q=80&w=1074",
      alt: "Document organization and filing"
    }
  ];

  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length, isAutoPlay]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 10000); // Resume auto-play after 10 seconds
  };

  const slideVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.15 } },
    exit: { opacity: 0, transition: { duration: 0.1 } },
  };

  const textVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 0.2, ease: "easeOut", delay: 0.05 },
    },
    exit: {
      opacity: 0,
      x: -20,
      transition: { duration: 0.1, ease: "easeIn" },
    },
  };

  const imageVariants = {
    hidden: { opacity: 0, x: 20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 0.2, ease: "easeOut", delay: 0.02 },
    },
    exit: {
      opacity: 0,
      x: 20,
      transition: { duration: 0.1, ease: "easeIn" },
    },
  };

  return (
    <section id="hero" className="min-h-[80vh] flex items-center pt-20 pb-12 px-6 relative overflow-hidden">
      {/* Dynamic Background */}
      <div
        className="absolute inset-0 transition-all duration-200 ease-out"
        style={{
          backgroundImage: `linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(168, 85, 247, 0.1) 100%), url(${slides[currentSlide].image})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          filter: "brightness(1.1)",
        }}
      />
      <div className="absolute inset-0 bg-white/80"></div>
      {/* Background Elements */}
      <div className="absolute inset-0 -z-10">
        <div className="absolute top-20 left-10 w-72 h-72 bg-indigo-200 rounded-full blur-3xl opacity-30 animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-200 rounded-full blur-3xl opacity-30 animate-pulse delay-1000"></div>
      </div>

      <div className="max-w-6xl mx-auto w-full">
        <AnimatePresence mode="wait" initial={false}>
          <motion.div
            key={`slide-${currentSlide}`}
            variants={slideVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
            className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center min-h-[60vh]"
          >
            {/* Text Content */}
            <motion.div
              className="space-y-6"
              variants={textVariants}
            >
              <div>
                <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold text-gray-900 leading-tight">
                  {slides[currentSlide].title}{" "}
                  <span className="text-indigo-600">{slides[currentSlide].highlight}</span>
                </h1>
                <p className="text-lg text-gray-600 mt-4 leading-relaxed">
                  {slides[currentSlide].description}
                </p>
              </div>

              <div className="flex flex-wrap gap-3">
                {isAuthenticated ? (
                  <button
                    onClick={handleDashboardNavigation}
                    className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 shadow-lg flex items-center"
                  >
                    Go to Dashboard
                    <ArrowRight className="w-5 h-5 ml-2" />
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => navigate("/register")}
                      className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 shadow-lg"
                    >
                      Start Free Trial
                    </button>
                    <button
                      onClick={() => scrollToSection("features")}
                      className="bg-transparent text-gray-800 border border-gray-300 hover:bg-gray-100 font-semibold py-3 px-6 rounded-lg transition duration-300"
                    >
                      Learn More
                    </button>
                  </>
                )}
              </div>
            </motion.div>

            {/* Blended Image Content */}
            <motion.div
              className="relative h-[400px] flex items-center justify-center"
              variants={imageVariants}
              style={{
                backgroundImage: `url(${slides[currentSlide].image})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                maskImage: "radial-gradient(ellipse 80% 70% at center, black 40%, transparent 70%)",
                WebkitMaskImage: "radial-gradient(ellipse 80% 70% at center, black 40%, transparent 70%)",
              }}
            >
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-600/20 to-purple-600/20 mix-blend-overlay"></div>
            </motion.div>
          </motion.div>
        </AnimatePresence>

        {/* Enhanced Slide Indicators */}
        <div className="flex justify-center mt-8 space-x-4">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => handleSlideChange(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                currentSlide === index ? "bg-indigo-600 w-6" : "bg-gray-400"
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

