import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import FileBrowser from "../components/FileBrowser";
import UploadModal from "../components/UploadModal";

export default function UserDashboard() {
  const [showUpload, setShowUpload] = useState(false);
  const [search, setSearch] = useState("");

  const handleUploadClick = () => setShowUpload(true);
  const handleCloseModal = () => setShowUpload(false);

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-6">
        {/* Action Bar */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-800">My Documents</h1>
          <div className="flex items-center gap-3">
            <input
              type="text"
              placeholder="Search documents..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-semibold"
              onClick={() => alert('New folder creation flow here')}
            >
              New Folder
            </button>
            <button
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-semibold"
              onClick={handleUploadClick}
            >
              Upload Document
            </button>
          </div>
        </div>

        {/* File Browser */}
        <FileBrowser searchQuery={search} />

        {/* Upload Modal */}
        {showUpload && <UploadModal onClose={handleCloseModal} />}
      </div>
    </div>
  );
}
