import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import FileBrowser from "../components/FileBrowser";
import UploadModal from "../components/UploadModal";
import NewFolderModal from "../components/NewFolderModal";

export default function UserDashboard() {
  const [folders, setFolders] = useState(() => {
    const saved = localStorage.getItem("folders");
    return saved ? JSON.parse(saved) : [];
  });

  const [currentFolder, setCurrentFolder] = useState(null);
  const [showUpload, setShowUpload] = useState(false);
  const [showNewFolder, setShowNewFolder] = useState(false);
  const [search, setSearch] = useState("");

  // Save folders to localStorage
  useEffect(() => {
    localStorage.setItem("folders", JSON.stringify(folders));
  }, [folders]);

  const handleCreateFolder = (name) => {
    if (!name.trim()) return;
    const newFolder = { id: Date.now(), name, files: [] };
    setFolders([...folders, newFolder]);
  };

  const handleUpload = (fileData) => {
    const file = {
      id: Date.now(),
      name: fileData.name,
      size: `${(fileData.size / 1024).toFixed(1)} KB`,
      date: new Date().toISOString().split("T")[0],
    };

    if (currentFolder) {
      setFolders((prev) =>
        prev.map((f) =>
          f.id === currentFolder.id
            ? { ...f, files: [...f.files, file] }
            : f
        )
      );
    } else {
      // Root uploads
      setFolders((prev) => {
        const root = prev.find((f) => f.name === "__ROOT__");
        if (root) {
          root.files.push(file);
          return [...prev];
        }
        return [...prev, { id: "root", name: "__ROOT__", files: [file] }];
      });
    }
  };

  const handleOpenFolder = (folder) => {
    setCurrentFolder(folder);
  };

  const handleGoBack = () => {
    setCurrentFolder(null);
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-6">
        {/* Breadcrumb / Header */}
        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-2xl font-bold text-gray-800">
              {currentFolder ? currentFolder.name : "My Documents"}
            </h1>
            {currentFolder && (
              <button
                onClick={handleGoBack}
                className="text-blue-600 text-sm hover:underline mt-1"
              >
                ‚Üê Back to My Documents
              </button>
            )}
          </div>

          {/* Action Bar */}
          <div className="flex items-center gap-3">
            <input
              type="text"
              placeholder="Search..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border border-gray-300 rounded-lg px-3 py-2"
            />
            <button
              className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
              onClick={() => setShowNewFolder(true)}
            >
              New Folder
            </button>
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
              onClick={() => setShowUpload(true)}
            >
              Upload
            </button>
          </div>
        </div>

        {/* File Browser */}
        <FileBrowser
          folders={currentFolder ? [] : folders}
          currentFolder={currentFolder}
          onOpenFolder={handleOpenFolder}
          searchQuery={search}
        />

        {/* Modals */}
        {showUpload && (
          <UploadModal onClose={() => setShowUpload(false)} onUpload={handleUpload} />
        )}
        {showNewFolder && (
          <NewFolderModal
            onClose={() => setShowNewFolder(false)}
            onCreate={handleCreateFolder}
          />
        )}
      </div>
    </div>
  );
}
