import React from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import { ShieldCheck, CloudUpload, Lock, Mail, Phone } from "lucide-react";
import { useAuth } from "../contexts/AuthContext";

export default function Home() {
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth();

  const handleDashboardNavigation = () => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    
    if (user?.role === 'ADMIN') {
      navigate("/admin-dashboard");
    } else {
      navigate("/dashboard");
    }
  };

  return (
    <section className="min-h-screen flex flex-col text-center text-gray-900 bg-gradient-to-br from-[#B6CEB4] via-[#D9E9CF] to-[#96A78D]">
      {/* HERO SECTION */}
      <div className="flex flex-col justify-center items-center flex-grow px-6 pt-24 pb-20 relative overflow-hidden">
        {/* Background glow */}
        <div className="absolute inset-0 -z-10">
          <div className="absolute top-10 left-20 w-72 h-72 bg-[#B6CEB4]/40 rounded-full blur-3xl animate-pulse"></div>
          <div className="absolute bottom-10 right-20 w-96 h-96 bg-[#96A78D]/40 rounded-full blur-3xl animate-pulse"></div>
        </div>

        <motion.h1
          className="text-5xl sm:text-6xl font-extrabold mb-6 drop-shadow-lg text-[#2F3E27]"
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Welcome to <span className="text-[#5B7C56]">DocuManage</span>
        </motion.h1>

        <motion.p
          className="text-lg sm:text-xl max-w-2xl mb-10 text-gray-700"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          A secure digital locker system where your documents stay safe, organized, and easily accessible anywhere, anytime.
        </motion.p>

        <motion.div
          className="flex flex-wrap gap-4 justify-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1, duration: 1 }}
        >
          {isAuthenticated ? (
            <button
              onClick={handleDashboardNavigation}
              className="bg-[#96A78D] text-white px-6 py-3 rounded-lg font-semibold shadow-lg hover:scale-105 hover:bg-[#B6CEB4] transition-transform duration-200"
            >
              Go to {user?.role === 'ADMIN' ? 'Admin' : 'User'} Dashboard
            </button>
          ) : (
            <>
              <button
                onClick={() => navigate("/login")}
                className="bg-[#96A78D] text-white px-6 py-3 rounded-lg font-semibold shadow-lg hover:scale-105 hover:bg-[#B6CEB4] transition-transform duration-200"
              >
                Login to Access Dashboard
              </button>
              <button
                onClick={() => navigate("/register")}
                className="bg-white/30 border border-white/40 px-6 py-3 rounded-lg font-semibold text-gray-800 hover:bg-white/50 hover:scale-105 transition-transform duration-200"
              >
                Create Account
              </button>
            </>
          )}
        </motion.div>
      </div>

      {/* ABOUT SECTION */}
      <div className="bg-[#F0F0F0] text-gray-800 py-16 px-8">
        <h2 className="text-3xl font-bold mb-4 text-[#5B7C56]">About DocuManage</h2>
        <p className="max-w-3xl mx-auto text-lg text-gray-700">
          DocuManage is your personal cloud locker for storing and managing important files securely.
          Whether it’s certificates, ID proofs, or project documents, our platform ensures privacy, easy retrieval,
          and organized access to all your data.
        </p>
      </div>

      {/* FEATURES SECTION */}
      <div className="py-16 px-8 bg-gradient-to-r from-[#96A78D] to-[#B6CEB4] text-gray-900">
        <h2 className="text-3xl font-bold mb-10 text-[#2F3E27]">Why Choose DocuManage?</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          <div className="p-6 bg-[#F0F0F0]/70 rounded-xl backdrop-blur-lg hover:bg-[#F0F0F0]/90 transition">
            <ShieldCheck className="w-10 h-10 mx-auto mb-4 text-[#5B7C56]" />
            <h3 className="text-xl font-semibold mb-2">Secure Storage</h3>
            <p>All your files are encrypted and stored safely with strong authentication layers.</p>
          </div>
          <div className="p-6 bg-[#F0F0F0]/70 rounded-xl backdrop-blur-lg hover:bg-[#F0F0F0]/90 transition">
            <CloudUpload className="w-10 h-10 mx-auto mb-4 text-[#5B7C56]" />
            <h3 className="text-xl font-semibold mb-2">Easy Uploads</h3>
            <p>Upload any document within seconds and organize it into your personalized folders.</p>
          </div>
          <div className="p-6 bg-[#F0F0F0]/70 rounded-xl backdrop-blur-lg hover:bg-[#F0F0F0]/90 transition">
            <Lock className="w-10 h-10 mx-auto mb-4 text-[#5B7C56]" />
            <h3 className="text-xl font-semibold mb-2">Access Anywhere</h3>
            <p>Access your documents from any device with seamless cloud synchronization.</p>
          </div>
        </div>
      </div>

      {/* CONTACT SECTION */}
      <div className="bg-[#F0F0F0] text-gray-800 py-16 px-8">
        <h2 className="text-3xl font-bold mb-6 text-[#5B7C56]">Contact Us</h2>
        <p className="text-lg text-gray-700 mb-6">
          Have questions or need support? We’d love to hear from you.
        </p>
        <div className="flex flex-col md:flex-row justify-center gap-10">
          <div className="flex items-center gap-3">
            <Mail className="w-6 h-6 text-[#5B7C56]" />
            <span>support@documanage.com</span>
          </div>
          <div className="flex items-center gap-3">
            <Phone className="w-6 h-6 text-[#5B7C56]" />
            <span>+91 98765 43210</span>
          </div>
        </div>
      </div>

      {/* FOOTER */}
      <footer className="bg-[#2F3E27] text-[#D9E9CF] py-4 text-sm">
        © 2025 DocuManage | Secure Digital Locker for Everyone.
      </footer>
    </section>
  );
}
