import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import NewFolderModal from "../components/NewFolderModal";

export default function UserDashboard() {
  const [folders, setFolders] = useState(() => {
    const saved = localStorage.getItem("folders");
    return saved ? JSON.parse(saved) : [];
  });

  const [currentFolder, setCurrentFolder] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [search, setSearch] = useState("");

  useEffect(() => {
    localStorage.setItem("folders", JSON.stringify(folders));
  }, [folders]);

  const handleCreateFolder = (name) => {
    if (!name.trim()) return alert("Folder name required!");
    const newFolder = { id: Date.now(), name, files: [] };
    setFolders((prev) => [...prev, newFolder]);
  };

  const handleUpload = (fileData) => {
    const newFile = {
      id: Date.now(),
      name: fileData.name,
      size: `${(fileData.size / 1024).toFixed(1)} KB`,
      date: new Date().toISOString().split("T")[0],
      url: fileData.Url,
    };

    if (currentFolder) {
      setFolders((prev) =>
        prev.map((f) =>
          f.id === currentFolder.id
            ? { ...f, files: [...f.files, newFile] }
            : f
        )
      );
    } else {
      setFolders((prev) => {
        const root = prev.find((f) => f.name === "__ROOT__");
        if (root) {
          root.files.push(newFile);
          return [...prev];
        }
        return [...prev, { id: "root", name: "__ROOT__", files: [newFile] }];
      });
    }

    const history = JSON.parse(localStorage.getItem("history") || "[]");
    history.push({
      id: Date.now(),
      fileName: newFile.name,
      folder: currentFolder ? currentFolder.name : "Root",
      date: newFile.date,
    });
    localStorage.setItem("history", JSON.stringify(history));
  };

  const handleOpenFolder = (folder) => setCurrentFolder(folder);
  const handleGoBack = () => setCurrentFolder(null);

  const filteredFolders = currentFolder
    ? currentFolder.files.filter((file) =>
        file.name.toLowerCase().includes(search.toLowerCase())
      )
    : folders.filter((f) =>
        f.name.toLowerCase().includes(search.toLowerCase())
      );

  return (
    <div className="flex min-h-screen bg-[#F0F0F0]">
      <Sidebar />

      <div className="flex-1 p-6">
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-2xl font-bold text-[#2F3E27]">
              {currentFolder ? currentFolder.name : "My Documents"}
            </h1>
            {currentFolder && (
              <button
                onClick={handleGoBack}
                className="text-[#96A78D] text-sm hover:underline mt-1"
              >
                ‚Üê Back to My Documents
              </button>
            )}
          </div>

          {/* Actions */}
          <div className="flex items-center gap-3">
            <input
              type="text"
              placeholder="Search..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border border-[#B6CEB4] rounded-lg px-3 py-2 w-48 bg-white text-gray-700 focus:ring-2 focus:ring-[#96A78D] outline-none"
            />
            <button
              onClick={() => setShowNewFolder(true)}
              className="bg-[#96A78D] text-white px-4 py-2 rounded-lg hover:bg-[#5B7C56] transition"
            >
              New Folder
            </button>

          </div>
        </div>

        {/* File Browser */}
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-gray-500 text-center py-8">
            This dashboard has been replaced by the enhanced File Dashboard.
            <br />
            <a href="/file-dashboard" className="text-blue-600 hover:underline">
              Go to File Dashboard
            </a>
          </p>
        </div>

        {/* Modals */}

        {showNewFolder && (
          <NewFolderModal
            onClose={() => setShowNewFolder(false)}
            onCreate={handleCreateFolder}
          />
        )}
      </div>
    </div>
  );
}
