              <span className="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">Storage</span>
            </h1>
            <p className="text-xl leading-relaxed max-w-2xl mb-8 text-white/90" style={{ fontFamily: 'Lato, sans-serif' }}>
              Professional-grade security for your important files. Simple, secure, and always accessible.
            </p>

            <div className="mt-8 flex flex-wrap gap-4 justify-center lg:justify-start">
            {isAuthenticated ? (
              <button
                onClick={handleDashboardNavigation}
                className="text-white font-semibold py-4 px-8 rounded-xl flex items-center transition-all duration-300 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl transform hover:scale-105"
                style={{fontFamily: 'Inter, sans-serif', fontWeight: 600}}
              >
                Go to Dashboard <ArrowRight className="ml-2 w-5 h-5" />
              </button>
            ) : (
              <>
                <button
                  onClick={() => navigate("/register")}
                  className="text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 flex items-center bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl transform hover:scale-105"
                  style={{fontFamily: 'Inter, sans-serif', fontWeight: 600}}
                >
                  Start Protecting My Files <ArrowRight className="ml-2 w-5 h-5" />
                </button>
                <button
                  onClick={() => scrollToSection("features")}
                  className="font-semibold py-4 px-8 rounded-xl border-2 transition-all duration-300 bg-white/20 backdrop-blur-md border-white/30 text-white hover:bg-white/30 shadow-lg hover:shadow-xl transform hover:scale-105"
                  style={{ 
                    fontFamily: 'Inter, sans-serif', 
                    fontWeight: 600
                  }}
                >
                  Learn More
                </button>
              </>
            )}
            </div>
          </div>
        </motion.div>

        {/* Slide Indicators + Image Text */}
        <div className="hidden lg:flex flex-col justify-center items-center relative">
          <motion.div
            key={`img-${currentSlide}`}
            initial={{ opacity: 0, scale: 0.97 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            className="w-full h-[400px] rounded-3xl overflow-hidden shadow-2xl relative"
          >
            <img
              src={slides[currentSlide].image}
              alt={slides[currentSlide].alt}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-black/30" />
          </motion.div>

          <div className="flex justify-center mt-6 space-x-3">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => handleSlideChange(index)}
                className={`transition-all duration-300 ${
                  currentSlide === index
                    ? "w-6 h-3 rounded-full"
                    : "w-3 h-3 rounded-full"
                }`}
                style={currentSlide === index ? {backgroundColor: COLORS.primary} : {backgroundColor: COLORS.mediumGray}}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

