import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export default function DocumentViewer() {
  const { id } = useParams(); // Get file ID from URL
  const [file, setFile] = useState(null);

  useEffect(() => {
    // Load folders from localStorage
    const folders = JSON.parse(localStorage.getItem("folders") || "[]");
    // Search for the file by ID in all folders
    let foundFile = null;
    for (const folder of folders) {
      const fileMatch = folder.files.find((f) => f.id.toString() === id);
      if (fileMatch) {
        foundFile = fileMatch;
        break;
      }
    }
    setFile(foundFile);
  }, [id]);

  if (!file) {
    return (
      <div className="p-6">
        <Link to="/user-dashboard" className="text-blue-600 hover:underline">
          ← Back to My Documents
        </Link>
        <p className="text-gray-600 mt-4">File not found.</p>
      </div>
    );
  }

  return (
    <div className="p-6">
      <Link to="/user-dashboard" className="text-blue-600 hover:underline">
        ← Back to My Documents
      </Link>

      <div className="bg-white shadow-md rounded-lg p-6 mt-4">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">{file.name}</h2>
        <p className="text-gray-600">Size: {file.size}</p>
        <p className="text-gray-600">Date: {file.date}</p>

        {/* Optional: If you stored file content or URL */}
        {file.content ? (
          <div className="mt-4 border-t pt-4">
            <pre className="bg-gray-50 p-4 rounded-lg overflow-auto text-sm text-gray-700">
              {file.content}
            </pre>
          </div>
        ) : (
          <p className="text-gray-500 mt-4">No file content available.</p>
        )}
      </div>
    </div>
  );
}
