import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
  MapPin,
  Facebook,
  Twitter,
  Linkedin,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";

// Dropbox-style Professional Theme
const COLORS = {
  primary: '#0061FF',      // Primary Blue
  darkBg: '#1E1E2F',       // Dark Blue/Navy
  white: '#FFFFFF',        // White
  lightGray: '#F5F5F5',    // Light Gray
  lightBlue: '#E3F2FD',    // Light Blue Background
  mediumGray: '#A0A0A0',   // Medium Gray
  black: '#000000',        // Black
  deepBlue: '#1A4A9C',     // Deep Blue for headings
  electricViolet: '#6A0DAD', // Electric Violet for icons
  darkGray: '#444444',     // Dark Gray for text
};

// Enhanced Why Choose SecureVault Section
const UnifiedFeaturesSection = () => {
  const [activeSection, setActiveSection] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const sections = [
    {
      id: 'functionality',
      title: 'How It',
      highlight: 'Works',
      subtitle: 'Simple 4-Step Process',
      gradient: 'from-slate-800 via-gray-900 to-black',
      accentColor: 'from-blue-400 via-cyan-400 to-teal-400',
      icon: ScanSearch,
      description: 'Revolutionary 4-step process that transforms document security with military-grade protection and consumer-friendly simplicity.'
    },
    {
      id: 'security',
      title: 'Your',
      highlight: 'Security',
      subtitle: 'Military-Grade Protection',
      gradient: 'from-gray-800 via-slate-900 to-gray-900',
      accentColor: 'from-purple-400 via-pink-400 to-red-400',
      icon: ShieldCheck,
      description: 'Uncompromising security without sacrificing convenience. Experience enterprise-grade protection with user-friendly design.'
    },
    {
      id: 'accessibility',
      title: 'Secure',
      highlight: 'Vault',
      subtitle: 'For Everyone',
      gradient: 'from-indigo-900 via-purple-900 to-gray-900',
      accentColor: 'from-green-400 via-emerald-400 to-lime-400',
      icon: Users,
      description: 'Whether individual, freelancer, or admin team, our secure vault adapts to your needs with enterprise-grade protection.'
    }
  ];

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100
      });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      setActiveSection((prev) => (prev + 1) % sections.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [sections.length]);

  const currentSection = sections[activeSection];

  return (
    <section className="py-24 relative min-h-screen overflow-hidden">
      {/* Dynamic Background */}
      <motion.div 
        className="absolute inset-0"
        animate={{
          background: `linear-gradient(135deg, ${currentSection.gradient.split(' ').join(', ')})`
        }}
        transition={{ duration: 1.5, ease: "easeInOut" }}
      >
        {/* Floating Particles */}
        <div className="absolute inset-0">
          {[...Array(25)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-2 h-2 bg-white/10 rounded-full"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
              }}
              animate={{
                y: [0, -40, 0],
                opacity: [0.1, 0.6, 0.1],
                scale: [1, 1.5, 1]
              }}
              transition={{
                duration: 4 + Math.random() * 3,
                repeat: Infinity,
                delay: Math.random() * 2
              }}
            />
          ))}
        </div>

        {/* Parallax Background */}
        <motion.div 
          className="absolute inset-0 opacity-20"
          style={{
            background: `radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, rgba(255,255,255,0.1) 0%, transparent 50%)`
          }}
        />
      </motion.div>

      <div className="max-w-7xl mx-auto px-6 relative z-10">
        {/* Main Title */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-20"
        >
          <motion.h2 
            className="text-5xl lg:text-7xl font-bold mb-8"
            style={{ fontFamily: 'Inter, sans-serif', fontWeight: 700 }}
          >
            <span className="text-white drop-shadow-lg">Why Choose </span>
            <span className={`bg-gradient-to-r ${currentSection.accentColor} bg-clip-text text-transparent`}>
              SecureVault
            </span>?
          </motion.h2>
          <motion.p 
            className="text-xl leading-relaxed max-w-4xl mx-auto text-white/80"
            style={{ fontFamily: 'Lato, sans-serif' }}
          >
            Experience the perfect blend of security, simplicity, and accessibility with cutting-edge protection.
          </motion.p>
        </motion.div>

        {/* Interactive Navigation Cards */}
        <div className="flex flex-col lg:flex-row justify-center items-center gap-8 mb-20">
          {sections.map((section, index) => {
            const Icon = section.icon;
            const isActive = index === activeSection;

            return (
              <motion.button
                key={section.id}
                onClick={() => {
                  setActiveSection(index);
                  scrollToSection(section.id);
                }}
                className="relative group p-8 rounded-3xl transition-all duration-500 border-0 overflow-hidden min-w-[320px] backdrop-blur-sm"
                initial={{ opacity: 0, y: 50, scale: 0.9 }}
                whileInView={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                whileHover={{ scale: 1.05, y: -10 }}
                whileTap={{ scale: 0.95 }}
                style={{
                  background: isActive 
                    ? `linear-gradient(135deg, ${section.gradient.split(' ').join(', ')})` 
                    : 'rgba(255,255,255,0.1)',
                  border: isActive ? '2px solid rgba(255,255,255,0.3)' : '1px solid rgba(255,255,255,0.1)'
                }}
              >
                {/* Glowing Effect */}
                {isActive && (
                  <motion.div
                    className="absolute inset-0 rounded-3xl"
                    animate={{
                      boxShadow: [
                        '0 0 30px rgba(255,255,255,0.2)',
                        '0 0 60px rgba(255,255,255,0.4)',
                        '0 0 30px rgba(255,255,255,0.2)'
                      ]
                    }}
                    transition={{ duration: 2, repeat: Infinity }}
                  />
                )}

                <div className="relative z-10 flex flex-col items-center text-center">
                  <motion.div
                    className="mb-6 p-4 bg-white/10 backdrop-blur-sm rounded-2xl"
                    animate={isActive ? {
                      rotate: [0, 360],
                      scale: [1, 1.1, 1]
                    } : {}}
                    transition={{ duration: 3, repeat: Infinity }}
                  >
                    <Icon className="w-10 h-10 text-white" />
                  </motion.div>

                  <motion.h3 
                    className="font-bold text-2xl mb-3 text-white"
                    style={{ fontFamily: 'Inter, sans-serif', textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}
                  >
                    <span>{section.title} </span>
                    <span className={`bg-gradient-to-r ${section.accentColor} bg-clip-text text-transparent`}>
                      {section.highlight}
                    </span>
                  </motion.h3>

                  <p className="text-white/80 font-medium mb-4" style={{ fontFamily: 'Lato, sans-serif' }}>
                    {section.subtitle}
                  </p>

                  <p className="text-sm text-white/60 leading-relaxed" style={{ fontFamily: 'Lato, sans-serif' }}>
                    {section.description}
                  </p>
                </div>
              </motion.button>
            );
          })}
        </div>

        {/* Section Indicator */}
        <div className="flex justify-center mb-12">
          <div className="flex space-x-4">
            {sections.map((_, index) => (
              <motion.button
                key={index}
                onClick={() => setActiveSection(index)}
                className="w-4 h-4 rounded-full transition-all duration-300"
                animate={{
                  backgroundColor: activeSection === index ? '#ffffff' : 'rgba(255,255,255,0.3)',
                  scale: activeSection === index ? 1.2 : 1
                }}
                whileHover={{ scale: 1.3 }}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

// Enhanced Feature Cards Section
const EnhancedFeatureCards = () => {
  return (
    <section className="py-24 bg-gray-50">
      <div className="max-w-7xl mx-auto px-6">
        <div className="space-y-32">
          {/* How It Works Section */}
          <motion.div 
            id="functionality"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="rounded-3xl overflow-hidden shadow-2xl"
            style={{ backgroundColor: '#E3F2FD' }}
          >
            <div className="grid grid-cols-1 lg:grid-cols-2 min-h-[600px]">
              <div className="p-12 flex flex-col justify-center">
                <div className="space-y-8">
                  <div>
                    <h2 className="text-4xl font-bold mb-4" style={{ color: COLORS.black, fontFamily: 'Inter, sans-serif' }}>
                      How It Works
                    </h2>
                    <p className="text-lg leading-relaxed" style={{ color: COLORS.black, fontFamily: 'Lato, sans-serif' }}>
                      Transform your document security with our revolutionary 4-step process. Experience military-grade protection with consumer-friendly simplicity.
                    </p>
                  </div>
                  <button className="inline-flex items-center px-6 py-3 rounded-lg font-semibold transition-colors w-fit" style={{ backgroundColor: COLORS.primary, color: COLORS.white }}>
                    More about secure storage
                    <ArrowRight className="ml-2 w-4 h-4" />
                  </button>
                </div>
              </div>
              <div className="relative p-8">
                <div className="relative h-full rounded-2xl overflow-hidden">
                  <img src="https://media.istockphoto.com/id/1552881556/photo/tablet-designer-and-serious-woman-research-in-business-startup-office-at-night-on-deadline.webp?a=1&b=1&s=612x612&w=0&k=20&c=BcvnTOdxmot9qj2oF8meLUOVuBh7HBnrSQ93NqFzAFA=" alt="Professional working on tablet in office" className="w-full h-full object-cover" />
                  <div className="absolute inset-0 bg-gradient-to-l from-transparent to-black/10" />
                  <AutoAnimatedHowItWorksCards />
                </div>
              </div>
            </div>
          </motion.div>

          {/* Security Section */}
          <motion.div 
            id="security"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="rounded-3xl overflow-hidden shadow-2xl"
            style={{ backgroundColor: '#FFE5CC' }}
          >
            <div className="grid grid-cols-1 lg:grid-cols-2 min-h-[600px]">
              <div className="relative p-8">
                <div className="relative h-full rounded-2xl overflow-hidden">
                  <img src="https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center" alt="Security and encryption" className="w-full h-full object-cover" />
                  <div className="absolute inset-0 bg-gradient-to-r from-transparent to-black/10" />
                  <AutoAnimatedSecurityCards />
                </div>
              </div>
              <div className="p-12 flex flex-col justify-center">
                <div className="space-y-8">
                  <div>
                    <h2 className="text-4xl font-bold mb-4" style={{ color: COLORS.black, fontFamily: 'Inter, sans-serif' }}>
                      Your Security is Our Priority
                    </h2>
                    <p className="text-lg leading-relaxed" style={{ color: COLORS.black, fontFamily: 'Lato, sans-serif' }}>
                      Military-grade protection meets user-friendly design. Experience uncompromising security without sacrificing convenience.
                    </p>
                  </div>
                  <button className="inline-flex items-center px-6 py-3 rounded-lg font-semibold transition-colors w-fit" style={{ backgroundColor: COLORS.primary, color: COLORS.white }}>
                    Learn about our security
                    <ArrowRight className="ml-2 w-4 h-4" />
                  </button>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Accessibility Section */}
          <motion.div 
            id="accessibility"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="rounded-3xl overflow-hidden shadow-2xl"
            style={{ backgroundColor: '#E9D5FF' }}
          >
            <div className="grid grid-cols-1 lg:grid-cols-2 min-h-[600px]">
              <div className="p-12 flex flex-col justify-center">
                <div className="space-y-8">
                  <div>
                    <h2 className="text-4xl font-bold mb-4" style={{ color: COLORS.black, fontFamily: 'Inter, sans-serif' }}>
                      A Secure Vault for Everyone
                    </h2>
                    <p className="text-lg leading-relaxed" style={{ color: COLORS.black, fontFamily: 'Lato, sans-serif' }}>
                      Whether you're an individual, freelancer, or part of an admin team, our secure vault adapts to your needs with enterprise-grade protection.
                    </p>
                  </div>
                  <button className="inline-flex items-center px-6 py-3 rounded-lg font-semibold transition-colors w-fit" style={{ backgroundColor: COLORS.primary, color: COLORS.white }}>
                    Choose your plan
                    <ArrowRight className="ml-2 w-4 h-4" />
                  </button>
                </div>
              </div>
              <div className="relative p-8">
                <div className="relative h-full rounded-2xl overflow-hidden">
                  <img src="https://media.istockphoto.com/id/1291751221/photo/the-sales-department-scores-again.webp?a=1&b=1&s=612x612&w=0&k=20&c=eLEEzx6Bt0-Y3Cj2QKudklHo9sBd4vzXO4Fvt9eLzjk=" alt="Digital Security Lock" className="w-full h-full object-cover" />
                  <div className="absolute inset-0 bg-gradient-to-l from-transparent to-black/10" />
                  <AutoAnimatedVaultCards />
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

// Card-only components for unified section
const AutoAnimatedHowItWorksCards = () => {
  const [visibleSteps, setVisibleSteps] = useState([]);
  const [animationPhase, setAnimationPhase] = useState('clumpy');

  const steps = [
    { icon: User, title: "Create Account", description: "Sign up with secure credentials and verification", color: "#0061FF" },
    { icon: CloudUpload, title: "Upload & Encrypt", description: "Drag and drop files into secure portal with AES-256", color: "#0061FF" },
    { icon: ScanSearch, title: "Organize & Find", description: "AI-powered categorization with lightning-fast search", color: "#0061FF" },
    { icon: FileLock2, title: "Access & Control", description: "Access from any device with biometric authentication", color: "#0061FF" }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      if (animationPhase === 'clumpy') {
        setVisibleSteps([0, 1, 2, 3]);
        setTimeout(() => setAnimationPhase('expanding'), 1000);
      } else if (animationPhase === 'expanding') {
        setVisibleSteps(prev => {
          if (prev.length === steps.length) {
            setTimeout(() => setAnimationPhase('shrinking'), 3000);
            return prev;
          }
          return prev;
        });
      } else {
        setAnimationPhase('clumpy');
      }
    }, 1000);
    return () => clearInterval(interval);
  }, [animationPhase, steps.length]);

  return (
    <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2" style={{ marginTop: '120px' }}>
      <div className="relative">
        {visibleSteps.map((stepIndex, displayIndex) => {
          const step = steps[stepIndex];
          const Icon = step.icon;
          const isClumpy = animationPhase === 'clumpy' || animationPhase === 'shrinking';

          return (
            <motion.div
              key={`${stepIndex}-${displayIndex}`}
              initial={{ opacity: 0.8 }}
              animate={{ 
                opacity: isClumpy ? (displayIndex === 0 ? 1 : 0.85 - (displayIndex * 0.15)) : 1,
                y: isClumpy ? displayIndex * 8 : displayIndex * -120,
                x: 0, rotate: 0,
                scale: isClumpy ? 1 - (displayIndex * 0.04) : 1,
                zIndex: isClumpy ? steps.length - displayIndex : displayIndex
              }}
              transition={{ duration: 0.8, type: "spring", stiffness: 80 }}
              className="absolute bg-white rounded-xl p-4 shadow-lg"
              style={{ 
                backgroundColor: COLORS.white, border: `1px solid #E5E7EB`,
                width: '320px', left: '-160px',
                pointerEvents: isClumpy && displayIndex > 0 ? 'none' : 'auto'
              }}
            >
              <div className="flex items-start space-x-3">
                <div className="p-2 rounded-lg flex-shrink-0" style={{ backgroundColor: `${step.color}15` }}>
                  <Icon className="w-5 h-5" style={{ color: step.color }} />
                </div>
                <div className="flex-1">
                  <h4 className="font-bold text-base mb-1" style={{ color: COLORS.black, fontFamily: 'Inter, sans-serif' }}>
                    {step.title}
                  </h4>
                  <p className="text-sm leading-relaxed" style={{ color: COLORS.darkGray, fontFamily: 'Lato, sans-serif' }}>
                    {step.description}
                  </p>
                </div>
              </div>
            </motion.div>
          );
        })}
      </div>
    </div>
  );
};

const AutoAnimatedSecurityCards = () => {
  const [currentCard, setCurrentCard] = useState(0);
  const steps = [
