import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import { AuthProvider, useAuth } from "./contexts/AuthContext";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import AdminDashboard from "./components/AdminDashboard";
import LoginForm from "./components/LoginForm";
import SignupForm from "./components/SignupForm";
import DocumentViewer from "./components/DocumentViewer";
import UserFileDashboard from "./components/UserFileDashboard";

// Protected Route Component
const ProtectedRoute = ({ children, adminOnly = false }) => {
  const { isAuthenticated, user, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  // Normalize the role to handle ROLE_ADMIN, Admin, etc.
  const normalizedRole = user?.role?.toUpperCase() || "";

  if (adminOnly && !normalizedRole.includes("ADMIN")) {
    return <Navigate to="/dashboard" />;
  }

  return children;
};

// Public Route Component (redirect if already logged in)
const PublicRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  if (isAuthenticated) {
    return <Navigate to="/dashboard" />;
  }

  return children;
};

// Dashboard Redirect Component
const DashboardRedirect = () => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  // Debug logging
  console.log('üîç DashboardRedirect - Full user object:', user);
  console.log('üîç DashboardRedirect - user.role:', user.role);
  console.log('üîç DashboardRedirect - user.roles:', user.roles);

  // Handle both cases:
  // Case 1: user.role = "ROLE_ADMIN" or "ADMIN"
  // Case 2: user.roles = ["ROLE_ADMIN", "ROLE_USER"]
  let roles = [];

  if (Array.isArray(user.roles)) {
    roles = user.roles.map(r => r.toUpperCase());
  } else if (user.role) {
    roles = [user.role.toUpperCase()];
  }

  console.log('üîç DashboardRedirect - processed roles:', roles);

  // Check if user has ADMIN in any of their roles
  const isAdmin = roles.some(r => r.includes("ADMIN"));

  console.log('üîç DashboardRedirect - isAdmin:', isAdmin);

  if (isAdmin) {
    console.log('‚úÖ Redirecting to admin dashboard');
    return <Navigate to="/admin-dashboard" replace />;
  } else {
    console.log('‚úÖ Redirecting to file dashboard');
    return <Navigate to="/file-dashboard" replace />;
  }
};


function AppContent() {
  const { isAuthenticated } = useAuth();

  return (
    <div className="min-h-screen bg-gray-50">
      <Routes>
        {/* Auth routes without navbar */}
        <Route path="/login" element={
          <PublicRoute>
            <LoginForm />
          </PublicRoute>
        } />
        <Route path="/register" element={
          <PublicRoute>
            <SignupForm />
          </PublicRoute>
        } />

        {/* Main app routes with navbar */}
        <Route path="/" element={
          <>
            <Navbar />
            <main className="p-6 mt-20">
              <Home />
            </main>
          </>
        } />
        <Route path="/dashboard" element={
          <ProtectedRoute>
            <Navbar />
            <main className="p-6 mt-20">
              <DashboardRedirect />
            </main>
          </ProtectedRoute>
        } />
        <Route path="/user-dashboard" element={
          <ProtectedRoute>
            <Navbar />
            <main className="p-6 mt-20">
              <UserDashboard />
            </main>
          </ProtectedRoute>
        } />
        <Route path="/file-dashboard" element={
          <ProtectedRoute>
            <Navbar />
            <main className="p-6 mt-20">
              <UserFileDashboard />
            </main>
          </ProtectedRoute>
        } />
        <Route path="/admin-dashboard" element={
          <ProtectedRoute adminOnly={true}>
            <Navbar />
            <main className="p-6 mt-20">
              <AdminDashboard />
            </main>
          </ProtectedRoute>
        } />
        <Route path="/document/:id" element={
          <ProtectedRoute>
            <Navbar />
            <main className="p-6 mt-20">
              <DocumentViewer />
            </main>
          </ProtectedRoute>
        } />
        <Route path="*" element={<Navigate to={isAuthenticated ? "/dashboard" : "/"} />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
