import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

export default function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  const { isAuthenticated, user, logout } = useAuth();
  const [isProfileOpen, setIsProfileOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    navigate("/");
  };

  const publicLinks = [
    { to: "/", label: "Home" },
    { to: "/login", label: "Login" },
    { to: "/register", label: "Register" },
  ];

  const privateLinks = [
    { to: "/", label: "Home" },
    { to: "/dashboard", label: "Dashboard" },
    ...(user?.role === 'ADMIN' ? [{ to: "/admin-dashboard", label: "Admin" }] : []),
  ];

  return (
    <nav className="fixed w-full top-0 z-20 bg-[#F0F0F0] shadow-md border-b border-[#D9E9CF]">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <Link
          to="/"
          className="text-2xl font-extrabold text-[#5B7C56] hover:text-[#2F3E27] transition"
        >
          DocuManage
        </Link>

        <div className="flex gap-6 items-center">
          {(isAuthenticated ? privateLinks : publicLinks).map((link) => (
            <Link
              key={link.to}
              to={link.to}
              className={`font-medium ${
                location.pathname === link.to
                  ? "text-[#96A78D] border-b-2 border-[#96A78D]"
                  : "text-gray-700 hover:text-[#5B7C56]"
              } transition`}
            >
              {link.label}
            </Link>
          ))}
          
          {isAuthenticated && (
            <div className="relative">
              <button
                onClick={() => setIsProfileOpen(!isProfileOpen)}
                className="h-10 w-10 rounded-full flex items-center justify-center text-white font-semibold hover:opacity-80 transition bg-[#87A96B]"
              >
                {(user?.name || user?.email || 'U').charAt(0).toUpperCase()}
              </button>
              
              {isProfileOpen && (
                <>
                  <div className="fixed inset-0 z-10" onClick={() => setIsProfileOpen(false)} />
                  <div className="absolute right-0 mt-2 w-64 bg-white rounded-lg shadow-lg border z-20">
                    <div className="p-4 border-b">
                      <p className="font-semibold text-gray-800">{user?.name || 'User'}</p>
                      <p className="text-sm text-gray-600">{user?.email}</p>
                      <p className="text-xs text-gray-500 mt-1">Role: {user?.role || 'USER'}</p>
                    </div>
                    <div className="p-2">
                      <button
                        onClick={() => {
                          handleLogout();
                          setIsProfileOpen(false);
                        }}
                        className="w-full text-left px-3 py-2 text-sm text-red-600 hover:bg-red-50 rounded transition"
                      >
                        Logout
                      </button>
                    </div>
                  </div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}
