import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { documentAPI, folderAPI, activityAPI } from '../services/api';
import { COLORS, formatFileSize, getFileType } from './dashboard/DashboardUtils';
import { 
    Sidebar, 
    Header, 
    FileDetailModal, 
    NewFolderModal 
} from './dashboard/DashboardComponents';
import { MyFilesView, HistoryView, ActivityLogView } from './dashboard/FileViews';

export default function UserFileDashboard() {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('My Files');
    const [files, setFiles] = useState([]);
    const [folders, setFolders] = useState([]);
    const [activityLogs, setActivityLogs] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalFile, setModalFile] = useState(null);
    const [isNewFolderModalOpen, setIsNewFolderModalOpen] = useState(false);
    const [isUploading, setIsUploading] = useState(false);
    const [currentFolder, setCurrentFolder] = useState(null);
    const [breadcrumb, setBreadcrumb] = useState([]);
    const [backendConnected, setBackendConnected] = useState(true);

    // Professional features state
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('uploadDate');
    const [sortOrder, setSortOrder] = useState('desc');
    const [filterType, setFilterType] = useState('all');
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [dragActive, setDragActive] = useState(false);

    const fileInputRef = useRef(null);

    // Load data from backend
    useEffect(() => {
        if (user) {
            console.log('Loading data for user:', user);
            // Test backend connectivity first
            testBackendConnection();
            loadFolders();
            loadFiles();
            loadActivityLogs();
        }
    }, [user]);

    const testBackendConnection = async () => {
        try {
            console.log('Testing backend connection...');
            const response = await fetch('http://localhost:8081/api/auth/me', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
                }
            });
            console.log('Backend connection test:', response.status, response.statusText);
            if (response.ok) {
                const data = await response.json();
                console.log('Backend user data:', data);
                setBackendConnected(true);
            } else {
                setBackendConnected(false);
            }
        } catch (error) {
            console.error('Backend connection failed:', error);
            setBackendConnected(false);
            console.warn('Running in offline mode with sample data');
        }
    };

    const loadFolders = async () => {
        try {
            console.log('Loading folders...');
            const response = await folderAPI.getAllFolders();
            console.log('Folders loaded:', response.data);
            setFolders(response.data || []);
        } catch (error) {
            console.error('Failed to load folders:', error);
            // Add some sample data for testing if backend is not working
            setFolders([
                { id: 1, name: 'Documents', createdAt: new Date().toISOString(), parentId: null },
                { id: 2, name: 'Images', createdAt: new Date().toISOString(), parentId: null },
            ]);
        }
    };

    const loadFiles = async () => {
        try {
            console.log('Loading files...');
            const response = await documentAPI.getAllDocuments();
            console.log('Files loaded:', response.data);
            setFiles(response.data || []);
        } catch (error) {
            console.error('Failed to load files:', error);
            setFiles([]); // Set empty array on error
        }
    };

    const loadActivityLogs = async () => {
        try {
            console.log('Loading activity logs...');
            const response = await activityAPI.getAllLogs();
            console.log('Activity logs loaded:', response.data);
            setActivityLogs(response.data || []);
        } catch (error) {
            console.error('Failed to load activity logs:', error);
            // Add some sample data for testing if backend is not working
            setActivityLogs([
                { id: 1, action: 'UPLOAD', details: { fileName: 'sample.pdf' }, timestamp: new Date().toISOString() },
                { id: 2, action: 'DOWNLOAD', details: { fileName: 'image.jpg' }, timestamp: new Date().toISOString() },
            ]);
        }
    };

    // Activity Logger
    const logActivity = async (action, details) => {
        try {
            console.log('Logging activity:', action, details);
            await activityAPI.recordActivity(action, details);
            // Reload activity logs to get the latest
            await loadActivityLogs();
        } catch (error) {
            console.error('Failed to log activity:', error);
        }
    };

    // File Upload Handler
    // Drag and drop handlers
    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === 'dragenter' || e.type === 'dragover') {
            setDragActive(true);
        } else if (e.type === 'dragleave') {
            setDragActive(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);

        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            handleFileUpload({ target: { files: e.dataTransfer.files } });
        }
    };

    // Bulk actions
    const handleSelectFile = (fileId) => {
        setSelectedFiles(prev => 
            prev.includes(fileId) 
                ? prev.filter(id => id !== fileId)
                : [...prev, fileId]
        );
    };

    const handleSelectAll = () => {
        const { currentFiles } = getCurrentFolderItems();
        setSelectedFiles(currentFiles.map(f => f.id));
    };

    const handleBulkDelete = async () => {
        if (selectedFiles.length === 0) return;

        if (window.confirm(`Delete ${selectedFiles.length} selected files?`)) {
            for (const fileId of selectedFiles) {
                const file = files.find(f => f.id === fileId);
                if (file) {
                    await handleDeleteFile(file);
                }
            }
            setSelectedFiles([]);
        }
    };

    const handleFileUpload = async (event) => {
        const fileList = event.target.files;
        if (!fileList || fileList.length === 0) return;

        console.log('Starting file upload:', fileList.length, 'files');
        setIsUploading(true);

        try {
            // Handle multiple files
            for (let i = 0; i < fileList.length; i++) {
                const file = fileList[i];

                if (backendConnected) {
                    // Create FormData for file upload
                    const formData = new FormData();
                    formData.append('file', file);
                    formData.append('fileName', file.name);
                    formData.append('fileType', getFileType(file.name));
                    if (currentFolder) {
                        formData.append('folderId', currentFolder.id);
                    }

                    console.log('Uploading file to backend:', file.name);
                    const response = await documentAPI.uploadDocument(formData);
                    console.log('Upload response:', response.data);

                    await logActivity('UPLOAD', { 
                        fileName: file.name, 
                        fileSize: formatFileSize(file.size),
                        folderId: currentFolder?.id 
                    });
                } else {
                    // Offline mode - simulate upload
                    console.log('Offline mode: simulating file upload:', file.name);
                    const newFile = {
                        id: Date.now() + i,
                        name: file.name,
                        fileName: file.name,
                        fileType: getFileType(file.name),
                        size: file.size,
                        fileSize: file.size,
                        uploadedAt: new Date().toISOString(),
                        uploadDate: new Date().toISOString(),
                        parentFolder: currentFolder ? { id: currentFolder.id } : null,
                        folderId: currentFolder?.id || null,
                        tags: [], // For future tagging feature
                        expiryDate: null, // For document expiration
                    };
                    setFiles(prev => [newFile, ...prev]);

                    const newLog = {
                        id: Date.now() + i,
                        action: 'UPLOAD',
                        details: { fileName: file.name, fileSize: formatFileSize(file.size) },
                        timestamp: new Date().toISOString()
                    };
                    setActivityLogs(prev => [newLog, ...prev]);
                }
            }

            // Reload files if backend connected
            if (backendConnected) {
                await loadFiles();
            }

            alert(`${fileList.length} file(s) uploaded successfully!`);

        } catch (error) {
            console.error("Upload failed:", error);
            alert("File upload failed: " + (error.response?.data?.error || error.message));
        } finally {
            setIsUploading(false);
            if (event.target.value) event.target.value = null;
        }
    };

    // Folder Creation Handler
    const handleCreateFolder = async (folderName) => {
        try {
            console.log('Creating folder:', folderName);

            if (backendConnected) {
                const folderData = {
                    name: folderName,
                    parentId: currentFolder?.id || null,
                };

                console.log('Folder data:', folderData);
                const response = await folderAPI.createFolder(folderData);
                console.log('Folder created:', response.data);

                // Reload folders to get updated list
                await loadFolders();

                // Log activity
                await logActivity('CREATE_FOLDER', { 
                    folderName,
                    parentId: currentFolder?.id 
                });
            } else {
                // Offline mode - simulate folder creation
                console.log('Offline mode: simulating folder creation');
                const newFolder = {
                    id: Date.now(),
                    name: folderName,
                    createdAt: new Date().toISOString(),
                    createdDate: new Date().toISOString(),
                    parentId: currentFolder?.id || null,
                    parentFolder: currentFolder ? { id: currentFolder.id } : null,
                };
                setFolders(prev => [newFolder, ...prev]);

                // Add to activity logs
                const newLog = {
                    id: Date.now(),
                    action: 'CREATE_FOLDER',
                    details: { folderName },
                    timestamp: new Date().toISOString()
                };
                setActivityLogs(prev => [newLog, ...prev]);
            }

            alert('Folder created successfully!');

        } catch (error) {
            console.error('Failed to create folder:', error);
            console.error('Error response:', error.response);
            alert('Failed to create folder: ' + (error.response?.data?.error || error.message));
        }
    };

    // Folder Navigation Handlers
    const handleFolderClick = (folder) => {
        setCurrentFolder(folder);
        setBreadcrumb(prev => [...prev, folder]);
        logActivity('OPEN_FOLDER', { 
            folderName: folder.name,
            folderId: folder.id 
        });
    };

    const handleBreadcrumbClick = (index) => {
        if (index === -1) {
            // Go to root
            setCurrentFolder(null);
            setBreadcrumb([]);
        } else {
            // Go to specific folder in breadcrumb
            const targetFolder = breadcrumb[index];
            setCurrentFolder(targetFolder);
            setBreadcrumb(prev => prev.slice(0, index + 1));
        }
    };

    // File Delete Handler
    const handleDeleteFile = async (file) => {
        try {
            await documentAPI.deleteDocument(file.id);

            // Reload files to get updated list
            await loadFiles();

            // Log activity
            await logActivity('DELETE', { 
                fileName: file.fileName,
                fileId: file.id 
            });

        } catch (error) {
            console.error('Failed to delete file:', error);
            alert('Failed to delete file: ' + (error.response?.data?.error || error.message));
            throw error; // Re-throw to handle in modal
        }
    };

    // File Download Handler
    const handleDownloadFile = async (file) => {
        try {
            const response = await documentAPI.downloadDocument(file.id);

            // Create blob URL and trigger download
            const blob = new Blob([response.data]);
            const url = window.URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = file.fileName;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            window.URL.revokeObjectURL(url);

            // Log activity
            await logActivity('DOWNLOAD', { 
                fileName: file.fileName,
                fileId: file.id 
            });

        } catch (error) {
            console.error('Failed to download file:', error);
            alert('Failed to download file: ' + (error.response?.data?.error || error.message));
        }
    };

    // File Click Handler
    const handleFileClick = (file) => {
        setModalFile(file);
        setIsModalOpen(true);
    };



    // My Files View
    const MyFilesView = () => {
        const { currentFiles, currentFolders } = getCurrentFolderItems();

        return (
            <div 
                className={`bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100 ${dragActive ? 'ring-2 ring-blue-500 bg-blue-50' : ''}`}
                onDragEnter={handleDrag}
                onDragLeave={handleDrag}
                onDragOver={handleDrag}
                onDrop={handleDrop}
            >
                {/* Breadcrumb Navigation */}
                <div className="mb-4">
                    <nav className="flex items-center space-x-2 text-sm">
                        <button 
                            onClick={() => handleBreadcrumbClick(-1)}
                            className="text-blue-600 hover:text-blue-800 transition"
                        >
                            Home
                        </button>
                        {breadcrumb.map((folder, index) => (
                            <React.Fragment key={folder.id}>
                                <span className="text-gray-400">/</span>
                                <button 
                                    onClick={() => handleBreadcrumbClick(index)}
                                    className="text-blue-600 hover:text-blue-800 transition"
                                >
                                    {folder.name}
                                </button>
                            </React.Fragment>
                        ))}
                    </nav>
                </div>

                {/* Search and Filter Bar */}
                <div className="mb-6 space-y-4">
                    <div className="flex flex-col sm:flex-row gap-4">
                        {/* Search */}
                        <div className="flex-1 relative">
                            <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                            <input
                                type="text"
                                placeholder="Search files by name or type..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sage-500 focus:border-transparent"
                            />
                        </div>

                        {/* Filter by Type */}
                        <select
                            value={filterType}
                            onChange={(e) => setFilterType(e.target.value)}
                            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sage-500"
                        >
                            <option value="all">All Types</option>
                            {getFileTypes().map(type => (
                                <option key={type} value={type}>{type}</option>
                            ))}
                        </select>

                        {/* Sort */}
                        <select
                            value={`${sortBy}-${sortOrder}`}
                            onChange={(e) => {
                                const [field, order] = e.target.value.split('-');
                                setSortBy(field);
                                setSortOrder(order);
                            }}
                            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sage-500"
                        >
                            <option value="uploadDate-desc">Newest First</option>
                            <option value="uploadDate-asc">Oldest First</option>
                            <option value="name-asc">Name A-Z</option>
                            <option value="name-desc">Name Z-A</option>
                            <option value="size-desc">Largest First</option>
                            <option value="size-asc">Smallest First</option>
                        </select>
                    </div>
                </div>

                {/* Header with Actions */}
                <div className="flex flex-col sm:flex-row justify-between items-center mb-6 gap-4 border-b pb-4">
                    <h2 className="text-xl font-semibold flex-shrink-0" style={{color: COLORS.textColor}}>
                        {currentFolder ? `${currentFolder.name} (${currentFiles.length} files, ${currentFolders.length} folders)` : `My Files (${currentFiles.length} files, ${currentFolders.length} folders)`}
                    </h2>
                    <div className="flex gap-3">
                        {selectedFiles.length > 0 && (
                            <>
                                <span className="text-sm text-gray-600 self-center">
                                    {selectedFiles.length} selected
                                </span>
                                <button 
                                    onClick={handleBulkDelete}
                                    className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md bg-red-500 hover:bg-red-600"
                                >
                                    <TrashIcon className="h-5 w-5 mr-2" />
                                    Delete Selected
                                </button>
                            </>
                        )}
                        <button 
                            onClick={() => setIsNewFolderModalOpen(true)}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                            style={{backgroundColor: COLORS.darkSage}}
                        >
                            <PlusIcon className="h-5 w-5 mr-2" />
                            New Folder
                        </button>

                        <input type="file" ref={fileInputRef} onChange={handleFileUpload} className="hidden" multiple />
                        <button 
                            onClick={() => !isUploading && fileInputRef.current?.click()}
                            disabled={isUploading}
                            className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md disabled:opacity-50"
                            style={{backgroundColor: COLORS.sage}}
                        >
                            <UploadIcon className="h-5 w-5 mr-2" />
                            {isUploading ? 'Uploading...' : 'Upload Files'}
                        </button>
                    </div>
                </div>

                {/* Drag and Drop Zone */}
                {dragActive && (
                    <div className="fixed inset-0 bg-blue-500 bg-opacity-20 flex items-center justify-center z-50 pointer-events-none">
                        <div className="bg-white p-8 rounded-xl shadow-lg border-2 border-dashed border-blue-500">
                            <UploadIcon className="h-12 w-12 mx-auto mb-4 text-blue-500" />
                            <p className="text-xl font-semibold text-blue-600">Drop files here to upload</p>
                        </div>
                    </div>
                )}

                {/* Folders Section */}
                {currentFolders.length > 0 && (
                    <div className="mb-6">
                        <h3 className="text-lg font-medium mb-3" style={{color: COLORS.textColor}}>Folders</h3>
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                            {currentFolders.map(folder => (
                                <div 
                                    key={folder.id} 
                                    className="p-4 border rounded-lg hover:shadow-md transition cursor-pointer hover:bg-gray-50" 
                                    style={{borderColor: COLORS.lightSage}}
                                    onClick={() => handleFolderClick(folder)}
                                >
                                    <FolderIcon className="h-8 w-8 mb-2" style={{color: COLORS.sage}} />
                                    <p className="font-medium text-sm" style={{color: COLORS.textColor}}>{folder.name}</p>
                                    <p className="text-xs text-gray-500">{new Date(folder.createdAt || folder.createdDate || Date.now()).toLocaleDateString()}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Files Section */}
                <div className="overflow-x-auto min-h-[400px]">
                    {currentFiles.length === 0 && currentFolders.length === 0 ? (
                        <div className="p-10 text-center text-gray-500">
                            <FileTextIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                            <p>{currentFolder ? `This folder is empty. Start by uploading files or creating folders.` : `No files uploaded yet. Start by clicking "Upload File".`}</p>
                        </div>
                    ) : currentFiles.length === 0 ? (
                        <div className="p-10 text-center text-gray-500">
                            <FileTextIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                            <p>No files in this folder. Upload some files to get started.</p>
                        </div>
                    ) : (
                        <table className="min-w-full text-left border-collapse">
                            <thead className="border-b bg-gray-50">
                                <tr>
                                    <th className="p-4 font-medium text-gray-600">
                                        <input
                                            type="checkbox"
                                            checked={selectedFiles.length === currentFiles.length && currentFiles.length > 0}
                                            onChange={handleSelectAll}
                                            className="mr-2"
                                        />
                                        File Name
                                    </th>
                                    <th className="p-4 font-medium text-gray-600">Type</th>
                                    <th className="p-4 font-medium text-gray-600 cursor-pointer hover:text-sage-600" 
                                        onClick={() => {
                                            setSortBy('size');
                                            setSortOrder(sortBy === 'size' && sortOrder === 'desc' ? 'asc' : 'desc');
                                        }}>
                                        Size {sortBy === 'size' && (sortOrder === 'desc' ? '↓' : '↑')}
                                    </th>
                                    <th className="p-4 font-medium text-gray-600 cursor-pointer hover:text-sage-600"
                                        onClick={() => {
                                            setSortBy('uploadDate');
                                            setSortOrder(sortBy === 'uploadDate' && sortOrder === 'desc' ? 'asc' : 'desc');
                                        }}>
                                        Date {sortBy === 'uploadDate' && (sortOrder === 'desc' ? '↓' : '↑')}
                                    </th>
                                    <th className="p-4 font-medium text-gray-600">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {currentFiles.map(file => (
                                    <tr key={file.id} className={`border-b hover:bg-gray-50 transition duration-150 cursor-pointer ${
                                        selectedFiles.includes(file.id) ? 'bg-blue-50' : ''
                                    }`}>
                                        <td className="p-4 flex items-center gap-3">
                                            <input
                                                type="checkbox"
                                                checked={selectedFiles.includes(file.id)}
                                                onChange={() => handleSelectFile(file.id)}
                                                onClick={(e) => e.stopPropagation()}
                                                className="mr-2"
                                            />
                                            <div onClick={() => handleFileClick(file)} className="flex items-center gap-3">
                                                <FileIconByType type={file.fileType} />
                                                <div>
                                                    <p className="font-semibold" style={{color: COLORS.textColor}}>
                                                        {file.name || file.fileName}
                                                    </p>
                                                    {file.tags && file.tags.length > 0 && (
                                                        <div className="flex gap-1 mt-1">
                                                            {file.tags.slice(0, 3).map(tag => (
                                                                <span key={tag} className="text-xs bg-gray-200 px-2 py-1 rounded">
                                                                    {tag}
                                                                </span>
                                                            ))}
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        </td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>{file.fileType}</td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>{formatFileSize(file.size || file.fileSize)}</td>
                                        <td className="p-4" style={{color: COLORS.textColor}}>
                                            {new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleDateString()}
                                            {file.expiryDate && (
                                                <div className="text-xs text-orange-600 mt-1">
                                                    Expires: {new Date(file.expiryDate).toLocaleDateString()}
                                                </div>
                                            )}
                                        </td>
                                        <td className="p-4">
                                            <button
                                                onClick={(e) => { e.stopPropagation(); handleFileClick(file); }}
                                                className="text-sm px-3 py-1 rounded-full font-medium transition"
                                                style={{backgroundColor: COLORS.mint, color: COLORS.mintText}}
                                            >
                                                View Details
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        );
    };

    // History View
    const HistoryView = () => (
        <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
            <h2 className="text-xl font-semibold mb-6 border-b pb-4" style={{color: COLORS.textColor}}>Upload History</h2>

            <div className="overflow-x-auto min-h-[400px]">
                {files.length === 0 ? (
                    <div className="p-10 text-center text-gray-500">
                        <HistoryIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                        <p>No upload history found.</p>
                    </div>
                ) : (
                    <table className="min-w-full text-left border-collapse">
                        <thead className="border-b bg-gray-50">
                            <tr>
                                <th className="p-4 font-medium text-gray-600">File Name</th>
                                <th className="p-4 font-medium text-gray-600">Type</th>
                                <th className="p-4 font-medium text-gray-600">Size</th>
                                <th className="p-4 font-medium text-gray-600">Upload Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {files.map(file => (
                                <tr key={file.id} className="border-b hover:bg-gray-50 transition duration-150">
                                    <td className="p-4 flex items-center gap-3">
                                        <FileIconByType type={file.fileType} />
                                        <p className="font-semibold" style={{color: COLORS.textColor}}>{file.name || file.fileName}</p>
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{file.fileType}</td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{formatFileSize(file.size || file.fileSize)}</td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>{new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );

    // Activity Logs View
    const ActivityLogView = () => (
        <div className="bg-white p-6 rounded-xl shadow-md ring-1 ring-gray-100">
            <h2 className="text-xl font-semibold mb-6 border-b pb-4" style={{color: COLORS.textColor}}>Activity Logs</h2>

            <div className="overflow-x-auto min-h-[400px]">
                {activityLogs.length === 0 ? (
                    <div className="p-10 text-center text-gray-500">
                        <ZapIcon className="h-10 w-10 mx-auto text-gray-400 mb-3" />
                        <p>No activity logs found. Start interacting with your files!</p>
                    </div>
                ) : (
                    <table className="min-w-full text-left border-collapse">
                        <thead className="border-b bg-gray-50">
                            <tr>
                                <th className="p-4 font-medium text-gray-600">Action</th>
                                <th className="p-4 font-medium text-gray-600">Details</th>
                                <th className="p-4 font-medium text-gray-600">Timestamp</th>
                            </tr>
                        </thead>
                        <tbody>
                            {activityLogs.map(log => (
                                <tr key={log.id} className="border-b hover:bg-gray-50 transition duration-150">
                                    <td className={`p-4 font-semibold ${
                                        log.action === 'DELETE' ? 'text-red-600' : 
                                        log.action === 'UPLOAD' ? 'text-green-600' : 
                                        'text-blue-600'
                                    }`}>
                                        {log.action}
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>
                                        {log.details.fileName || log.details.folderName}
                                        {log.details.fileSize && ` (${log.details.fileSize})`}
                                    </td>
                                    <td className="p-4" style={{color: COLORS.textColor}}>
                                        {new Date(log.timestamp).toLocaleString()}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );

    const renderContent = () => {
        switch(activeView) {
            case 'My Files':
                return (
                    <MyFilesView 
                        files={files}
                        folders={folders}
                        currentFolder={currentFolder}
                        breadcrumb={breadcrumb}
                        searchTerm={searchTerm}
                        setSearchTerm={setSearchTerm}
                        filterType={filterType}
                        setFilterType={setFilterType}
                        sortBy={sortBy}
                        setSortBy={setSortBy}
                        sortOrder={sortOrder}
                        setSortOrder={setSortOrder}
                        selectedFiles={selectedFiles}
                        handleSelectFile={handleSelectFile}
                        handleSelectAll={handleSelectAll}
                        handleBulkDelete={handleBulkDelete}
                        handleFolderClick={handleFolderClick}
                        handleBreadcrumbClick={handleBreadcrumbClick}
                        handleFileClick={handleFileClick}
                        setIsNewFolderModalOpen={setIsNewFolderModalOpen}
                        fileInputRef={fileInputRef}
                        handleFileUpload={handleFileUpload}
                        isUploading={isUploading}
                        dragActive={dragActive}
                        handleDrag={handleDrag}
                        handleDrop={handleDrop}
                    />
                );
            case 'History':
                return <HistoryView files={files} />;
            case 'Activity Logs':
                return <ActivityLogView activityLogs={activityLogs} />;
            default:
                return <MyFilesView files={files} folders={folders} />;
        }
    };

    return (
        <div className="flex h-screen font-sans antialiased" style={{backgroundColor: COLORS.offWhite}}>
            <Sidebar activeView={activeView} setActiveView={setActiveView} />
            <main className="flex-1 flex flex-col overflow-hidden">
                <Header title={activeView} backendConnected={backendConnected} />
                <div className="flex-1 p-6 overflow-y-auto">
                    {renderContent()}
                </div>
            </main>

            <FileDetailModal
                file={modalFile}
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onDeleteFile={handleDeleteFile}
                onDownloadFile={handleDownloadFile}
            />

            <NewFolderModal
                isOpen={isNewFolderModalOpen}
                onClose={() => setIsNewFolderModalOpen(false)}
                onCreateFolder={handleCreateFolder}
            />
        </div>
    );
}
