import React, { useState } from "react";
import { X } from "lucide-react";

export default function UploadModal({ onClose, onUpload }) {
  const [file, setFile] = useState(null);
  const [docName, setDocName] = useState("");
  const [description, setDescription] = useState("");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setDocName(selectedFile.name.replace(/\.[^/.]+$/, ""));
    }
  };

  const handleUpload = () => {
    if (!file) return alert("Please select a file to upload!");

    const reader = new FileReader();
    reader.onload = () => {
      const base64 = reader.result; // ✅ file content in base64 format
      const uploadedFile = {
        id: Date.now(),
        name: file.name,
        size: `${(file.size / 1024).toFixed(1)} KB`,
        date: new Date().toISOString().split("T")[0],
        url: base64, // ✅ store actual file data as base64
        description,
      };
      onUpload(uploadedFile); // ✅ send the base64 data to dashboard
      onClose();
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black/50 z-50">
      <div className="bg-white rounded-lg shadow-lg w-96 p-6 relative">
        <button
          onClick={onClose}
          className="absolute top-3 right-3 text-gray-500 hover:text-gray-800"
        >
          <X className="w-5 h-5" />
        </button>

        <h2 className="text-xl font-bold mb-4 text-gray-800">Upload Document</h2>

        <div className="border-2 border-dashed border-gray-300 p-4 rounded-lg mb-4 text-center">
          <input
            type="file"
            id="file-upload"
            onChange={handleFileChange}
            className="hidden"
          />
          <label
            htmlFor="file-upload"
            className="cursor-pointer text-blue-600 font-medium"
          >
            {file ? file.name : "Click to choose a file"}
          </label>
        </div>

        <input
          type="text"
          placeholder="Document Name"
          value={docName}
          onChange={(e) => setDocName(e.target.value)}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 mb-3"
        />
        <textarea
          placeholder="Description (optional)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 mb-4"
        />

        <div className="flex justify-end gap-3">
          <button
            onClick={onClose}
            className="px-4 py-2 rounded-lg bg-gray-200 hover:bg-gray-300"
          >
            Cancel
          </button>
          <button
            onClick={handleUpload}
            className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 text-white"
          >
            Upload
          </button>
        </div>
      </div>
    </div>
  );
}
