import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
  MapPin,
  Facebook,
  Twitter,
  Linkedin,
  Play,
  Pause,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";
import "./FloatingVideo.css";

// Floating Video Section Component
const FloatingVideoSection = () => {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showVideo, setShowVideo] = useState(false);

  const handlePlayClick = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleVideoLoad = () => {
    setShowVideo(true);
  };

  return (
    <section className="py-24 px-8 bg-gradient-to-br from-gray-50 to-white relative overflow-hidden">
      {/* Background Elements */}
      <motion.div
        className="absolute top-20 left-20 w-64 h-64 bg-rose-200 rounded-full blur-3xl opacity-20"
        animate={{ y: [0, 30, 0], x: [0, 20, 0] }}
        transition={{ repeat: Infinity, duration: 12, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute bottom-20 right-20 w-80 h-80 bg-orange-200 rounded-full blur-3xl opacity-15"
        animate={{ y: [0, -40, 0], x: [0, -30, 0] }}
        transition={{ repeat: Infinity, duration: 15, ease: "easeInOut" }}
      />

      <div className="max-w-7xl mx-auto">
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-4xl font-bold mb-6 text-center"
          style={{ color: '#662222' }}
        >
          Experience SecureVault in Action
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-lg text-gray-600 text-center max-w-2xl mx-auto mb-16"
        >
          See how our AI-powered document management transforms your digital security workflow
        </motion.p>

        {/* Floating Video Container */}
        <div className="flex justify-center items-center min-h-[600px] relative floating-video-container">
          {/* Video Element - Transparent Background */}
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 1, delay: 0.3 }}
            className="relative z-10 video-float-animation"
          >
            {/* Transparent Video */}
            <div className="relative w-[500px] h-[400px] max-w-[90vw] max-h-[60vh] rounded-3xl overflow-hidden shadow-2xl">
              <video
                ref={videoRef}
                className="w-full h-full object-cover transparent-video"
                muted
                loop
                playsInline
                onLoadedData={handleVideoLoad}
              >
                <source src="/videos/DemoVideo.mp4" type="video/mp4" />
                <source src="/videos/demo-transparent.webm" type="video/webm" />
                Your browser does not support the video tag.
              </video>

              {/* Play Button Overlay */}
              <AnimatePresence>
                {!isPlaying && (
                  <motion.button
                    initial={{ opacity: 0, scale: 0.5 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.5 }}
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handlePlayClick}
                    className="absolute inset-0 flex items-center justify-center play-button-overlay rounded-3xl"
                  >
                    <motion.div
                      whileHover={{ scale: 1.2, rotate: 5 }}
                      className="w-20 h-20 bg-white/90 backdrop-blur-md rounded-full flex items-center justify-center shadow-2xl border border-white/50"
                    >
                      <Play className="w-8 h-8 ml-1" style={{ color: '#662222' }} />
                    </motion.div>
                  </motion.button>
                )}
              </AnimatePresence>

              {/* Pause Button (when playing) */}
              <AnimatePresence>
                {isPlaying && (
                  <motion.button
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    onClick={handlePlayClick}
                    className="absolute top-4 right-4 w-12 h-12 bg-white/80 backdrop-blur-md rounded-full flex items-center justify-center shadow-lg hover:bg-white/90 transition-all duration-300"
                  >
                    <Pause className="w-5 h-5" style={{ color: '#662222' }} />
                  </motion.button>
                )}
              </AnimatePresence>
            </div>
          </motion.div>

          {/* Floating Card - Gemini Style */}
          <motion.div
            initial={{ opacity: 0, x: 100, y: 50 }}
            whileInView={{ opacity: 1, x: 0, y: 0 }}
            transition={{ duration: 1, delay: 0.6 }}
            whileHover={{ y: -5, scale: 1.02 }}
            className="absolute right-0 top-1/2 transform -translate-y-1/2 translate-x-8 z-20 floating-card-desktop lg:block hidden"
          >
            <div className="w-80 p-8 rounded-3xl glass-card">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.8 }}
              >
                <div className="flex items-center mb-4">
                  <motion.div
                    animate={{ rotate: [0, 360] }}
                    transition={{ repeat: Infinity, duration: 8, ease: "linear" }}
                    className="w-10 h-10 rounded-full flex items-center justify-center mr-3"
                    style={{ background: 'linear-gradient(135deg, #662222, #8B3333)' }}
                  >
                    <ShieldCheck className="w-5 h-5 text-white" />
                  </motion.div>
                  <h3 className="text-xl font-bold" style={{ color: '#662222' }}>
                    AI-Powered Security
                  </h3>
                </div>

                <p className="text-gray-700 mb-6 leading-relaxed">
                  Watch how our intelligent document analysis automatically categorizes, encrypts, and secures your files in real-time.
                </p>

                <div className="space-y-3">
                  <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: 1 }}
                    className="flex items-center text-sm text-gray-600"
                  >
                    <div className="w-2 h-2 rounded-full mr-3" style={{ backgroundColor: '#662222' }} />
                    Instant file encryption
                  </motion.div>
                  <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: 1.1 }}
                    className="flex items-center text-sm text-gray-600"
                  >
                    <div className="w-2 h-2 rounded-full mr-3" style={{ backgroundColor: '#662222' }} />
                    Smart categorization
                  </motion.div>
                  <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: 1.2 }}
                    className="flex items-center text-sm text-gray-600"
                  >
                    <div className="w-2 h-2 rounded-full mr-3" style={{ backgroundColor: '#662222' }} />
                    Biometric access control
                  </motion.div>
                </div>

                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
                  className="w-full mt-6 py-3 px-6 rounded-xl text-white font-semibold shadow-lg transition-all duration-300"
                  style={{ backgroundColor: '#662222' }}
                  onMouseEnter={(e) => e.target.style.backgroundColor = '#4A1A1A'}
                  onMouseLeave={(e) => e.target.style.backgroundColor = '#662222'}
                >
                  Try It Now
                </motion.button>
              </motion.div>
            </div>
          </motion.div>

          {/* Mobile Card Version */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.8 }}
            className="lg:hidden mt-8 w-full max-w-md mx-auto"
          >
            <div className="p-6 rounded-3xl glass-card">
              <div className="flex items-center mb-4">
                <motion.div
                  animate={{ rotate: [0, 360] }}
                  transition={{ repeat: Infinity, duration: 8, ease: "linear" }}
                  className="w-8 h-8 rounded-full flex items-center justify-center mr-3"
                  style={{ background: 'linear-gradient(135deg, #662222, #8B3333)' }}
                >
                  <ShieldCheck className="w-4 h-4 text-white" />
                </motion.div>
                <h3 className="text-lg font-bold" style={{ color: '#662222' }}>
                  AI-Powered Security
                </h3>
              </div>

              <p className="text-gray-700 mb-4 text-sm leading-relaxed">
                Watch how our intelligent document analysis automatically categorizes, encrypts, and secures your files in real-time.
              </p>

              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="w-full py-2 px-4 rounded-xl text-white font-semibold shadow-lg transition-all duration-300 text-sm"
                style={{ backgroundColor: '#662222' }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#4A1A1A'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#662222'}
              >
                Try It Now
              </motion.button>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

// How It Works Cards Component
const HowItWorksCards = () => {
  const [currentCard, setCurrentCard] = useState(0);

  const cards = [
    {
      icon: CloudUpload,
      title: "1. Upload & Encrypt",
      description: "Simply drag and drop your files into our secure portal. Advanced AES-256 encryption happens instantly on your device before transmission, ensuring zero-knowledge security.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: ScanSearch,
      title: "2. Organize & Find",
      description: "AI-powered smart categorization automatically sorts your documents. Lightning-fast search with OCR technology finds any document in milliseconds, not minutes.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: FileLock2,
      title: "3. Access & Control",
      description: "Access your vault from any device with biometric authentication. Granular permission controls let admins manage team access with enterprise-grade security protocols.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentCard((prev) => (prev + 1) % cards.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [cards.length]);

  return (
    <div className="relative max-w-6xl mx-auto h-[500px] flex items-center justify-center">
      {cards.map((card, index) => {
        const Icon = card.icon;
        const isActive = index === currentCard;
        const isPrevious = index === (currentCard - 1 + cards.length) % cards.length;
        const isNext = index === (currentCard + 1) % cards.length;

        let zIndex = 1;
        let transform = { x: 0, z: -200, rotateY: 180, opacity: 0, scale: 0.7 };

        if (isActive) {
          zIndex = 3;
          transform = { x: 0, z: 0, rotateY: 0, opacity: 1, scale: 1 };
        } else if (isPrevious) {
          zIndex = 2;
          transform = { x: -120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        } else if (isNext) {
          zIndex = 2;
          transform = { x: 120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        }

        return (
          <motion.div
            key={index}
            animate={transform}
            transition={{
              duration: 0.8,
              type: "spring",
              bounce: 0.3
            }}
            whileHover={isActive ? {
              scale: 1.05,
              rotateY: 5,
              z: 50
            } : {
              scale: 1.02,
              z: 0,
              opacity: 1
            }}
            onClick={() => setCurrentCard(index)}
            className={`absolute p-8 bg-gradient-to-br ${card.gradient} rounded-2xl shadow-2xl ${card.border} w-80 h-96 flex flex-col justify-center items-center text-center cursor-pointer`}
            style={{ zIndex }}
          >
            <motion.div
              whileHover={{
                rotate: [0, -10, 10, 0],
                scale: 1.2
              }}
              transition={{ duration: 0.5 }}
            >
              <Icon className="w-16 h-16 mb-6 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-2xl font-bold mb-4 text-gray-800">{card.title}</h3>
            <p className="text-gray-600 leading-relaxed text-sm">
              {card.description}
            </p>
            <motion.div
              className="mt-4 w-12 h-1 rounded-full"
              style={{backgroundColor: '#662222'}}
              initial={{ width: 0 }}
              animate={{ width: isActive ? 48 : 24 }}
              transition={{ duration: 0.5 }}
            />
          </motion.div>
        );
      })}

      {/* Navigation Dots */}
      <div className="absolute bottom-8 flex space-x-2">
        {cards.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentCard(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              currentCard === index ? "w-6" : "bg-gray-400"
            }`}
            style={currentCard === index ? {backgroundColor: '#662222'} : {}}
          />
        ))}
      </div>
    </div>
  );
};

// Flip Card Stack Component
const FlipCardStack = () => {
  const [currentCard, setCurrentCard] = useState(0);

  const cards = [
    {
      icon: User,
      title: "For Individuals",
      description: "Keep your passports, birth certificates, wills, and personal IDs safe from loss or unauthorized access. Personal vault with biometric security.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: Briefcase,
      title: "For Freelancers",
      description: "Securely store client contracts, invoices, project files, and tax documents, all in one organized workspace with client portals.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    },
    {
      icon: Users,
      title: "For Admin Teams",
      description: "Manage sensitive company documents, employee records, and financial reports with robust admin controls and team permissions.",
      gradient: "from-orange-100 to-red-100",
      border: "border-red-200"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentCard((prev) => (prev + 1) % cards.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [cards.length]);

  return (
    <div className="relative max-w-6xl mx-auto h-[500px] flex items-center justify-center">
      {cards.map((card, index) => {
        const Icon = card.icon;
        const isActive = index === currentCard;
        const isPrevious = index === (currentCard - 1 + cards.length) % cards.length;
        const isNext = index === (currentCard + 1) % cards.length;

        let zIndex = 1;
        let transform = { x: 0, z: -200, rotateY: 180, opacity: 0, scale: 0.7 };

        if (isActive) {
          zIndex = 3;
          transform = { x: 0, z: 0, rotateY: 0, opacity: 1, scale: 1 };
        } else if (isPrevious) {
          zIndex = 2;
          transform = { x: -120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        } else if (isNext) {
          zIndex = 2;
          transform = { x: 120, z: -100, rotateY: 0, opacity: 0.8, scale: 0.9 };
        }

        return (
          <motion.div
            key={index}
            animate={transform}
            transition={{
              duration: 0.8,
              type: "spring",
              bounce: 0.3
            }}
            whileHover={isActive ? {
              scale: 1.05,
              rotateY: 5,
              z: 50
            } : {
              scale: 1.02,
              z: 0,
              opacity: 1
            }}
            onClick={() => setCurrentCard(index)}
            className={`absolute p-8 bg-gradient-to-br ${card.gradient} rounded-2xl shadow-2xl ${card.border} w-80 h-96 flex flex-col justify-center items-center text-center cursor-pointer`}
            style={{ zIndex }}
          >
            <motion.div
              whileHover={{
                rotate: [0, -10, 10, 0],
                scale: 1.2
              }}
              transition={{ duration: 0.5 }}
            >
              <Icon className="w-16 h-16 mb-6 mx-auto" style={{color: '#662222'}} />
            </motion.div>
            <h3 className="text-2xl font-bold mb-4 text-gray-800">{card.title}</h3>
            <p className="text-gray-600 leading-relaxed text-sm">
              {card.description}
            </p>
            <motion.div
              className="mt-4 w-12 h-1 rounded-full"
              style={{backgroundColor: '#662222'}}
              initial={{ width: 0 }}
              animate={{ width: isActive ? 48 : 24 }}
              transition={{ duration: 0.5 }}
            />
          </motion.div>
        );
      })}

      {/* Navigation Dots */}
      <div className="absolute bottom-8 flex space-x-2">
        {cards.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentCard(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              currentCard === index ? "w-6" : "bg-gray-400"
            }`}
            style={currentCard === index ? {backgroundColor: '#662222'} : {}}
          />
        ))}
      </div>
    </div>
  );
};

