import React from "react";
import { Folder, FileText } from "lucide-react";
import { Link } from "react-router-dom";

export default function FileBrowser({
  folders = [],
  currentFolder,
  onOpenFolder,
  searchQuery,
}) {
  // Get files and folders to display
  const visibleFiles = currentFolder
    ? currentFolder.files
    : folders.flatMap((f) => (f.name === "__ROOT__" ? f.files : []));

  const filteredFolders = (folders || []).filter(
    (f) =>
      f.name !== "__ROOT__" &&
      f.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredFiles = (visibleFiles || []).filter((f) =>
    f.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="bg-white rounded-lg shadow-md p-4">
      {/* --- Folder Section --- */}
      {!currentFolder && (
        <>
          <h3 className="font-semibold text-gray-700 mb-3">Folders</h3>
          {filteredFolders.length > 0 ? (
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 mb-6">
              {filteredFolders.map((folder) => (
                <div
                  key={folder.id}
                  onClick={() => onOpenFolder(folder)}
                  className="p-4 border rounded-lg cursor-pointer hover:bg-blue-50 text-center"
                >
                  <Folder className="w-10 h-10 mx-auto text-yellow-500" />
                  <p className="mt-2 font-medium">{folder.name}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 mb-6">No folders found.</p>
          )}
        </>
      )}

      {/* --- File Section --- */}
      <h3 className="font-semibold text-gray-700 mb-3">
        {currentFolder ? `Files in "${currentFolder.name}"` : "Root Files"}
      </h3>

      {filteredFiles.length > 0 ? (
        <table className="w-full text-left">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4">Name</th>
              <th className="py-2 px-4">Size</th>
              <th className="py-2 px-4">Date</th>
            </tr>
          </thead>
          <tbody>
            {filteredFiles.map((file) => (
              <tr key={file.id} className="hover:bg-blue-50">
                <td className="py-2 px-4 flex items-center gap-2">
                  <FileText className="w-5 h-5 text-blue-600" />
                  <a
  href={file.url}
  target="_blank"
  rel="noopener noreferrer"
  className="text-blue-600 hover:underline"
>
  {file.name}
</a>

                </td>
                <td className="py-2 px-4">{file.size}</td>
                <td className="py-2 px-4">{file.date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">
          {currentFolder ? "No files in this folder." : "No files uploaded yet."}
        </p>
      )}
    </div>
  );
}
