import React from "react";
import { Link } from "react-router-dom";
import { Folder, Clock, Settings } from "lucide-react";

export default function Sidebar() {
  const links = [
    { to: "/user-dashboard", label: "My Files", icon: Folder },
    { to: "/history", label: "History", icon: Clock },
    { to: "/settings", label: "Settings", icon: Settings },
  ];

  return (
    <aside className="w-64 bg-[#D9E9CF] border-r border-[#B6CEB4] shadow-md p-4 flex flex-col">
      <h2 className="text-xl font-bold text-[#2F3E27] mb-6">User Menu</h2>
      <nav className="flex flex-col gap-3">
        {links.map(({ to, label, icon: Icon }) => (
          <Link
            key={to}
            to={to}
            className="flex items-center gap-3 text-[#2F3E27] hover:bg-[#B6CEB4] hover:text-[#2F3E27] p-2 rounded-lg transition"
          >
            <Icon className="w-5 h-5 text-[#5B7C56]" />
            {label}
          </Link>
        ))}
      </nav>
    </aside>
  );
}
