import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";

// Hero Carousel Component - improved
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault provides bank-grade encryption for your most important documents. Safe, organized, and accessible anywhere, anytime.",
      image:
        "https://media.istockphoto.com/id/2206161408/photo/glowing-shield-symbolizing-cybersecurity-on-a-high-tech-circuit-background.webp?a=1&b=1&s=612x612&w=0&k=20&c=pp7VyJc4-NM3-KGhQ4rm_89T-7lEmzrF6kNefVHzU18=",
      alt: "Digital fortress and security",
    },
    {
      title: "Bank-Grade",
      highlight: "Security",
      description:
        "Military-level encryption ensures your documents are protected with the highest security standards available today.",
      image:
        "https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=1400&h=900&fit=crop&crop=center",
      alt: "Security lock and protection",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Retrieve your files from any device, anywhere in the world. Your documents follow you wherever you go.",
      image:
        "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=1400&h=900&fit=crop&crop=center",
      alt: "Global access and connectivity",
    },
    {
      title: "Smart",
      highlight: "Organization",
      description:
        "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.",
      image:
        "https://plus.unsplash.com/premium_photo-1683288662019-c92caea8276d?ixlib=rb-4.1.0&auto=format&fit=crop&q=80&w=1400",
      alt: "Document organization and filing",
    },
  ];

  // Auto-play timer
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length, isAutoPlay]);

  const handleSlideChange = (index) => {
    if (index === currentSlide) return;
    setCurrentSlide(index);
    setIsAutoPlay(false);
    // resume after 10s
    setTimeout(() => setIsAutoPlay(true), 10000);
  };

  // Smoother, longer transitions
  const contentVariants = {
    initial: { opacity: 0, y: 10 },
    animate: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.55, ease: "easeOut" },
    },
    exit: { opacity: 0, y: -8, transition: { duration: 0.35, ease: "easeIn" } },
  };

  const imageVariants = {
    initial: { opacity: 0, scale: 0.995 },
    animate: {
      opacity: 1,
      scale: 1,
      transition: { duration: 0.8, ease: "circOut" },
    },
    exit: { opacity: 0, scale: 0.995, transition: { duration: 0.45, ease: "easeInOut" } },
  };

  return (
    <section
      id="hero"
      className="min-h-[80vh] flex items-center pt-20 pb-12 px-6 relative overflow-hidden"
      aria-label="Hero carousel"
    >
      {/* BACKGROUND LAYER (below content) */}
      <div
        aria-hidden
        className="absolute inset-0 -z-20 transition-all duration-300 ease-out pointer-events-none"
        style={{
          // we will set background per-slide via inline style on child below to enable smooth transitions
        }}
      />

      {/* subtle overlay to soften image - behind content */}
      <div className="absolute inset-0 -z-10 bg-gradient-to-b from-white/20 to-white/10 pointer-events-none" />

      {/* Decorative blobs behind everything */}
      <div className="absolute inset-0 -z-30">
        <div className="absolute top-20 left-10 w-72 h-72 bg-indigo-200 rounded-full blur-3xl opacity-30 animate-animate-pulse-slow"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-200 rounded-full blur-3xl opacity-28 animate-animate-pulse-slow"></div>
      </div>

      <div className="max-w-6xl mx-auto w-full relative z-20">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center min-h-[60vh]">
          {/* TEXT / CTA */}
          <div className="space-y-6">
            <AnimatePresence mode="wait">
              <motion.div
                key={`text-${currentSlide}`}
                initial="initial"
                animate="animate"
                exit="exit"
                variants={contentVariants}
                className="space-y-6"
              >
                <div>
                  <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold text-gray-900 leading-tight">
                    {slides[currentSlide].title}{" "}
                    <span className="text-indigo-600">{slides[currentSlide].highlight}</span>
                  </h1>
                  <p className="text-lg text-gray-600 mt-4 leading-relaxed">
                    {slides[currentSlide].description}
                  </p>
                </div>

                <div className="flex flex-wrap gap-3">
                  {isAuthenticated ? (
                    <button
                      onClick={handleDashboardNavigation}
                      className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 shadow-lg flex items-center"
                    >
                      Go to Dashboard
                      <ArrowRight className="w-5 h-5 ml-2" />
                    </button>
                  ) : (
                    <>
                      <button
                        onClick={() => navigate("/register")}
                        className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-lg transition duration-300 shadow-lg"
                      >
                        Start Free Trial
                      </button>
                      <button
                        onClick={() => scrollToSection("features")}
                        className="bg-transparent text-gray-800 border border-gray-300 hover:bg-gray-100 font-semibold py-3 px-6 rounded-lg transition duration-300"
                      >
                        Learn More
                      </button>
                    </>
                  )}
                </div>
              </motion.div>
            </AnimatePresence>
          </div>

          {/* IMAGE / VISUAL */}
          <div className="relative h-[400px] flex items-center justify-center overflow-hidden rounded-xl">
            <div className="absolute inset-0 rounded-xl overflow-hidden">
              <AnimatePresence mode="wait">
                <motion.div
                  key={`image-${currentSlide}`}
                  initial="initial"
                  animate="animate"
                  exit="exit"
                  variants={imageVariants}
                  style={{
                    backgroundImage: `url(${slides[currentSlide].image})`,
                    backgroundSize: "cover",
                    backgroundPosition: "center",
                    backgroundRepeat: "no-repeat",
                    width: "100%",
                    height: "100%",
                  }}
                />
              </AnimatePresence>

              {/* subtle vignette to focus center without heavy blending */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/10 via-transparent to-black/8 pointer-events-none mix-blend-normal" />
              {/* gentle color overlay for brand tint */}
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-600/10 to-purple-600/10 pointer-events-none" />
            </div>
          </div>
        </div>

        {/* Slide indicators */}
        <div className="flex justify-center mt-8 space-x-4">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => handleSlideChange(index)}
              className={`transition-all duration-300 focus:outline-none ${
                currentSlide === index
                  ? "w-6 h-3 rounded-full bg-indigo-600"
                  : "w-3 h-3 rounded-full bg-gray-300"
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

