import React, { useState, useEffect, useRef } from "react";
import {
  Chart,
  LineController,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  BarController,
  BarElement,
  DoughnutController,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register Chart.js components
Chart.register(
  LineController,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  BarController,
  BarElement,
  DoughnutController,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

// ---- SVG Icons ----
const HomeIcon = ({ className }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="2"
    stroke="currentColor"
    className={className}
  >
    <path d="m3 9 9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" />
    <polyline points="9 22 9 12 15 12 15 22" />
  </svg>
);

const UsersIcon = ({ className }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="2"
    stroke="currentColor"
    className={className}
  >
    <path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2"></path>
    <circle cx="9" cy="7" r="4"></circle>
    <path d="M22 21v-2a4 4 0 0 0-3-3.87"></path>
    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
  </svg>
);

const FileIcon = ({ className }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="2"
    stroke="currentColor"
    className={className}
  >
    <path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z"></path>
    <polyline points="14 2 14 8 20 8"></polyline>
  </svg>
);

const AlertCircleIcon = ({ className }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="2"
    stroke="currentColor"
    className={className}
  >
    <circle cx="12" cy="12" r="10"></circle>
    <line x1="12" y1="8" x2="12" y2="12"></line>
    <line x1="12" y1="16" x2="12.01" y2="16"></line>
  </svg>
);

const SearchIcon = ({ className }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth="2"
    stroke="currentColor"
    className={className}
  >
    <circle cx="11" cy="11" r="8"></circle>
    <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
  </svg>
);

// const MoreVerticalIcon = ({ className }) => (
//   <svg
//     xmlns="http://www.w3.org/2000/svg"
//     fill="none"
//     viewBox="0 0 24 24"
//     strokeWidth="2"
//     stroke="currentColor"
//     className={className}
//   >
//     <circle cx="12" cy="12" r="1"></circle>
//     <circle cx="12" cy="5" r="1"></circle>
//     <circle cx="12" cy="19" r="1"></circle>
//   </svg>
// );

// ---- Mock Data ----
const initialUsers = [
  { id: 1, name: "Alice Johnson", email: "alice.j@example.com", role: "Premium", storageUsed: 15.2, joined: "2023-01-15", status: "Active" },
  { id: 2, name: "Bob Williams", email: "bob.w@example.com", role: "Standard", storageUsed: 4.8, joined: "2023-02-20", status: "Active" },
  { id: 3, name: "Charlie Brown", email: "charlie.b@example.com", role: "Standard", storageUsed: 9.1, joined: "2023-03-10", status: "Suspended" },
  { id: 4, name: "Diana Miller", email: "diana.m@example.com", role: "Premium", storageUsed: 25.6, joined: "2023-04-05", status: "Active" },
  { id: 5, name: "Ethan Davis", email: "ethan.d@example.com", role: "Free", storageUsed: 1.2, joined: "2023-05-22", status: "Active" },
];

const stats = [
  { name: "Total Users", value: "1,428", change: "+12.5%", icon: UsersIcon, color: "bg-blue-500" },
  { name: "Total Files", value: "25,690", change: "+8.2%", icon: FileIcon, color: "bg-green-500" },
  { name: "Storage Used", value: "1.2 TB", change: "+2.1%", icon: HomeIcon, color: "bg-yellow-500" },
  { name: "Security Alerts", value: "4", change: "New", icon: AlertCircleIcon, color: "bg-red-500" },
];

const monthlyUsageData = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  data: [350, 420, 500, 610, 750, 800],
};

// ---- Chart Containers ----
const ChartContainer = ({ title, children }) => (
  <div className="bg-white p-6 rounded-xl shadow-md h-full flex flex-col">
    <h3 className="text-lg font-semibold text-gray-800 mb-4 border-b pb-2">{title}</h3>
    <div className="flex-1 min-h-[250px]">{children}</div>
  </div>
);

const LineChart = () => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current?.getContext("2d");
    if (!ctx) return; // prevent crash if canvas not ready

    // Destroy existing chart before creating a new one
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    chartInstance.current = new Chart(ctx, {
      type: "line",
      data: {
        labels: monthlyUsageData.labels,
        datasets: [
          {
            label: "Monthly Storage Usage (GB)",
            data: monthlyUsageData.data,
            borderColor: "#3b82f6",
            backgroundColor: "rgba(59,130,246,0.2)",
            fill: true,
            tension: 0.3,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { display: true },
        },
        scales: {
          x: { ticks: { color: "#4b5563" } },
          y: { ticks: { color: "#4b5563" } },
        },
      },
    });

    // Cleanup when unmounting or switching views
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  return (
    <div className="h-64">
      <canvas ref={chartRef}></canvas>
    </div>
  );
};


// ---- Sidebar ----
const Sidebar = ({ activeView, setActiveView }) => {
  const navItems = [
    { name: "Dashboard", icon: HomeIcon },
    { name: "Users", icon: UsersIcon },
    { name: "Queries", icon: AlertCircleIcon },
  ];

  return (
    <aside className="w-64 bg-gray-800 text-gray-300 flex flex-col p-4">
      <div className="text-white text-2xl font-bold mb-10 flex items-center gap-2">
        <FileIcon className="h-6 w-6" />
        <span>Digital Locker</span>
      </div>
      <nav className="flex-grow">
        <ul>
          {navItems.map((item) => (
            <li key={item.name}>
              <button
                onClick={() => setActiveView(item.name)}
                className={`w-full text-left flex items-center gap-3 px-4 py-3 my-1 rounded-lg transition-colors ${
                  activeView === item.name
                    ? "bg-blue-600 text-white"
                    : "hover:bg-gray-700 hover:text-white"
                }`}
              >
                <item.icon className="h-5 w-5" />
                <span>{item.name}</span>
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </aside>
  );
};

// ---- Header ----
const Header = ({ title }) => (
  <header className="bg-white shadow-sm p-4 flex justify-between items-center">
    <h1 className="text-2xl font-semibold text-gray-800">{title}</h1>
    <div className="flex items-center gap-3">
      <img
        src="https://i.pravatar.cc/100?u=admin"
        alt="Admin"
        className="h-10 w-10 rounded-full"
      />
      <div>
        <p className="font-semibold text-sm text-gray-800">Admin User</p>
        <p className="text-xs text-gray-500">System Administrator</p>
      </div>
    </div>
  </header>
);

// ---- Views ----
const DashboardView = () => (
  <>
    <h2 className="text-xl font-semibold text-gray-700 mb-4">System Overview</h2>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      {stats.map((item) => (
        <div key={item.name} className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500">{item.name}</p>
              <p className="text-3xl font-bold text-gray-800 mt-1">{item.value}</p>
              <p className="text-xs mt-2 text-green-600">{item.change} vs last period</p>
            </div>
            <div className={`${item.color} p-3 rounded-full text-white`}>
              <item.icon className="h-6 w-6" />
            </div>
          </div>
        </div>
      ))}
    </div>
    <ChartContainer title="Monthly Storage Usage (GB)">
      <LineChart />
    </ChartContainer>
  </>
);

const UsersView = () => {
  const [search, setSearch] = useState("");
  const filtered = initialUsers.filter(
    (u) =>
      u.name.toLowerCase().includes(search.toLowerCase()) ||
      u.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="bg-white p-6 rounded-xl shadow-md">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-semibold text-gray-800">All Users</h2>
        <div className="relative">
          <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 text-gray-400" />
          <input
            type="text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder="Search users..."
            className="pl-10 pr-4 py-2 border rounded-lg"
          />
        </div>
      </div>
      <table className="min-w-full text-left">
        <thead>
          <tr className="border-b">
            <th className="p-2">Name</th>
            <th className="p-2">Role</th>
            <th className="p-2">Storage Used</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((u) => (
            <tr key={u.id} className="border-b hover:bg-gray-50">
              <td className="p-2">{u.name}</td>
              <td className="p-2">{u.role}</td>
              <td className="p-2">{u.storageUsed} GB</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const QueriesView = () => (
  <div className="bg-white p-10 rounded-xl shadow-md text-center">
    <h2 className="text-3xl font-bold text-gray-800 mb-4">User Queries</h2>
    <AlertCircleIcon className="h-16 w-16 text-red-500 mx-auto mb-4" />
    <p className="text-gray-600">Monitor user issues and system alerts here.</p>
  </div>
);

// ---- Main Component ----
export default function AdminDashboard() {
  const [activeView, setActiveView] = useState("Dashboard");

  const renderView = () => {
    switch (activeView) {
      case "Dashboard":
        return <DashboardView />;
      case "Users":
        return <UsersView />;
      case "Queries":
        return <QueriesView />;
      default:
        return <DashboardView />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar activeView={activeView} setActiveView={setActiveView} />
      <main className="flex-1 flex flex-col overflow-hidden">
        <Header title={activeView} />
        <div className="flex-1 p-6 overflow-y-auto">{renderView()}</div>
      </main>
    </div>
  );
}
