import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
  MapPin,
  Facebook,
  Twitter,
  Linkedin,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";

// Dropbox-style Professional Theme
const COLORS = {
  primary: '#0061FF',      // Primary Blue
  darkBg: '#1E1E2F',       // Dark Blue/Navy
  white: '#FFFFFF',        // White
  lightGray: '#F5F5F5',    // Light Gray
  lightBlue: '#E3F2FD',    // Light Blue Background
  mediumGray: '#A0A0A0',   // Medium Gray
  black: '#000000',        // Black
  deepBlue: '#1A4A9C',     // Deep Blue for headings
  electricViolet: '#6A0DAD', // Electric Violet for icons
  darkGray: '#444444',     // Dark Gray for text
};

// Unified Features Section Component
const UnifiedFeaturesSection = () => {
  const [currentSection, setCurrentSection] = useState(0);

  const sections = [
    {
      id: 'how-it-works',
      title: 'How It Works',
      subtitle: 'Simple 4-Step Process',
      component: <AutoAnimatedHowItWorks />
    },
    {
      id: 'security',
      title: 'Your Security',
      subtitle: 'Military-Grade Protection',
      component: <AutoAnimatedSecurityFeatures />
    },
    {
      id: 'vault',
      title: 'Secure Vault',
      subtitle: 'For Everyone',
      component: <AutoAnimatedSecureVault />
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSection(prev => (prev + 1) % sections.length);
    }, 8000);

    return () => clearInterval(interval);
  }, [sections.length]);

  return (
    <section className="py-24">
      <div className="max-w-7xl mx-auto px-6">
        {/* Center Title and Content */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 
            className="text-5xl font-bold mb-6"
            style={{ color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700 }}
          >
            Why Choose <span style={{ color: COLORS.primary }}>SecureVault</span>?
          </h2>
          <p 
            className="text-xl leading-relaxed max-w-4xl mx-auto mb-8"
            style={{ color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif' }}
          >
            Experience the perfect blend of security, simplicity, and accessibility. Our comprehensive platform offers enterprise-grade protection with consumer-friendly design, ensuring your documents are safe while remaining easily accessible whenever you need them.
          </p>

          {/* Section Navigation */}
          <div className="flex justify-center space-x-8 mb-12">
