// Modal Component
const Modal = ({ title, children, onClose, isOpen }) => {
    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4" onClick={onClose}>
            <div 
                className="bg-white rounded-xl shadow-2xl max-w-lg w-full transform transition-all overflow-hidden"
                onClick={e => e.stopPropagation()}
            >
                <div className="p-4 flex justify-between items-center border-b" style={{backgroundColor: COLORS.lightSage}}>
                    <h3 className="text-xl font-semibold" style={{color: COLORS.textColor}}>{title}</h3>
                    <button onClick={onClose} className="text-gray-600 hover:text-red-500 p-2 rounded-full transition">
                        <XIcon className="h-6 w-6" />
                    </button>
                </div>
                <div className="p-6 max-h-[80vh] overflow-y-auto">
                    {children}
                </div>
            </div>
        </div>
    );
};

// New Folder Modal
const NewFolderModal = ({ isOpen, onClose, onCreateFolder }) => {
    const [folderName, setFolderName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (folderName.trim()) {
            onCreateFolder(folderName.trim());
            setFolderName('');
            onClose();
        }
    };

    return (
        <Modal title="Create New Folder" isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        Folder Name
                    </label>
                    <input
                        type="text"
                        value={folderName}
                        onChange={(e) => setFolderName(e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-sage-500"
                        placeholder="Enter folder name"
                        autoFocus
                    />
                </div>
                <div className="flex justify-end gap-3 pt-4">
                    <button
                        type="button"
                        onClick={onClose}
                        className="px-4 py-2 text-gray-600 hover:text-gray-800 transition"
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                        style={{backgroundColor: COLORS.sage}}
                        disabled={!folderName.trim()}
                    >
                        Create Folder
                    </button>
                </div>
            </form>
        </Modal>
    );
};

// File Detail Modal
const FileDetailModal = ({ file, isOpen, onClose, onDeleteFile, onDownloadFile }) => {
    const [isDeleting, setIsDeleting] = useState(false);

    if (!file) return null;

    const handleDownload = () => {
        onDownloadFile(file);
    };

    const confirmDelete = async () => {
        if (!isDeleting) {
            setIsDeleting(true);
            try {
                await onDeleteFile(file);
                onClose();
            } catch (error) {
                console.error("Failed to delete file:", error);
            } finally {
                setIsDeleting(false);
            }
        }
    };

    return (
        <Modal title={`File Details: ${file.fileName}`} isOpen={isOpen} onClose={onClose}>
            <div className="space-y-4">
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Name</p>
                    <p className="text-sm">{file.fileName}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Type</p>
                    <p className="text-sm">{file.fileType}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">File Size</p>
                    <p className="text-sm">{formatFileSize(file.fileSize)}</p>
                </div>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <p className="font-semibold text-sm">Upload Date</p>
                    <p className="text-sm">{new Date(file.uploadDate).toLocaleString()}</p>
                </div>

                {/* File Preview Section */}
                {file.previewUrl && getFileType(file.fileName) === 'Image' && (
                    <div className="mt-6 border border-gray-200 rounded-lg p-4 text-center">
                        <p className="font-semibold mb-3" style={{color: COLORS.textColor}}>File Preview</p>
                        <img 
                            src={file.previewUrl} 
                            alt="File Preview" 
                            className="max-w-full max-h-60 mx-auto rounded-lg shadow-md object-contain" 
                        />
                    </div>
                )}

                {/* Actions */}
                <div className="flex justify-end gap-3 pt-4 border-t mt-6">
                    <button
                        onClick={confirmDelete}
                        disabled={isDeleting}
                        className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition bg-red-500 hover:bg-red-600 shadow-md disabled:opacity-50"
                    >
                        <TrashIcon className="h-5 w-5 mr-2" />
                        {isDeleting ? 'Deleting...' : 'Delete File'}
                    </button>
                    <button
                        onClick={handleDownload}
                        className="flex items-center px-4 py-2 rounded-lg font-semibold text-white transition shadow-md"
                        style={{backgroundColor: COLORS.sage}}
                    >
                        <DownloadIcon className="h-5 w-5 mr-2" />
                        Download
                    </button>
                </div>
            </div>
        </Modal>
    );
};

