import React, { useState } from 'react';
import { COLORS, formatFileSize, getFileTypes, filterAndSortFiles, paginateData } from './DashboardUtils';
import { FileIconByType } from './DashboardComponents';

// Modern SVG Icons
const SearchIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
    </svg>
);

const GridIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2V6zM14 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V6zM4 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2zM14 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z" />
    </svg>
);

const ListIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 10h16M4 14h16M4 18h16" />
    </svg>
);

const PlusIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
    </svg>
);

const UploadIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
    </svg>
);

const FolderIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z" />
    </svg>
);

const DotsIcon = ({ className }) => (
    <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
    </svg>
);

// Dropbox-style File Card Component
const FileCard = ({ file, isSelected, onSelect, onClick, onMenuClick }) => (
    <div 
        className={`group relative bg-white border rounded-lg hover:shadow-lg transition-all duration-200 cursor-pointer ${
            isSelected ? 'ring-2 ring-blue-500 border-blue-500' : 'border-gray-200 hover:border-gray-300'
        }`}
        onClick={onClick}
    >
        {/* Selection Checkbox */}
        <div className="absolute top-3 left-3 z-10">
            <input
                type="checkbox"
                checked={isSelected}
                onChange={onSelect}
                onClick={(e) => e.stopPropagation()}
                className="w-4 h-4 text-blue-600 rounded border-gray-300 focus:ring-blue-500"
            />
        </div>

        {/* Menu Button */}
        <button
            onClick={(e) => { e.stopPropagation(); onMenuClick(); }}
            className="absolute top-3 right-3 z-10 opacity-0 group-hover:opacity-100 p-1 rounded-full hover:bg-gray-100 transition-opacity"
        >
            <DotsIcon className="w-4 h-4 text-gray-500" />
        </button>

        {/* File Icon */}
        <div className="p-6 flex flex-col items-center">
            <div className="mb-4">
                <FileIconByType type={file.fileType} fileName={file.name || file.fileName} size="large" />
            </div>

            {/* File Name */}
            <h3 className="text-sm font-medium text-gray-900 text-center mb-1 line-clamp-2">
                {file.name || file.fileName}
            </h3>

            {/* File Details */}
            <div className="text-xs text-gray-500 text-center space-y-1">
                <div>{formatFileSize(file.size || file.fileSize)}</div>
                <div>{new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleDateString()}</div>
            </div>
        </div>
    </div>
);

// Dropbox-style Folder Card Component
const FolderCard = ({ folder, onClick }) => (
    <div 
        className="group bg-white border border-gray-200 rounded-lg hover:shadow-lg hover:border-gray-300 transition-all duration-200 cursor-pointer"
        onClick={() => onClick(folder)}
    >
        <div className="p-6 flex flex-col items-center">
            <div className="mb-4">
                <FolderIcon className="w-12 h-12" style={{color: COLORS.primary}} />
            </div>
            <h3 className="text-sm font-medium text-gray-900 text-center mb-1">
                {folder.name}
            </h3>
            <div className="text-xs text-gray-500">
                {new Date(folder.createdAt || folder.createdDate || Date.now()).toLocaleDateString()}
            </div>
        </div>
    </div>
);

// Modern Dropbox-style My Files View
export const DropboxMyFilesView = ({ 
    files, 
    folders, 
    currentFolder, 
    breadcrumb, 
    searchTerm, 
    setSearchTerm, 
    filterType, 
    setFilterType, 
    sortBy, 
    setSortBy, 
    sortOrder, 
    setSortOrder, 
    selectedFiles, 
    handleSelectFile, 
    handleSelectAll, 
    handleBulkDelete, 
    handleFolderClick, 
    handleBreadcrumbClick, 
    handleFileClick, 
    setIsNewFolderModalOpen, 
    fileInputRef, 
    handleFileUpload, 
    isUploading, 
    dragActive, 
    handleDrag, 
    handleDrop 
}) => {
    const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(20);

    const currentFolderId = currentFolder?.id || null;
    const allFiles = filterAndSortFiles(files, searchTerm, filterType, sortBy, sortOrder, currentFolderId);
    const currentFolders = folders.filter(folder => {
        const folderParentId = folder.parentFolder?.id || folder.parentId || null;
        return folderParentId === currentFolderId;
    });

    const paginatedFiles = paginateData(allFiles, currentPage, itemsPerPage);
    const currentFiles = paginatedFiles.data;

    return (
        <div className="h-full flex flex-col bg-white">
            {/* Header Section */}
            <div className="border-b border-gray-200 bg-white sticky top-0 z-10">
                {/* Breadcrumb */}
                <div className="px-6 py-4">
                    <nav className="flex items-center space-x-2 text-sm">
                        <button 
                            onClick={() => handleBreadcrumbClick(-1)}
                            className="text-blue-600 hover:text-blue-800 font-medium transition"
                        >
                            All files
                        </button>
                        {breadcrumb.map((folder, index) => (
                            <React.Fragment key={folder.id}>
                                <span className="text-gray-400">/</span>
                                <button 
                                    onClick={() => handleBreadcrumbClick(index)}
                                    className="text-blue-600 hover:text-blue-800 font-medium transition"
                                >
                                    {folder.name}
                                </button>
                            </React.Fragment>
                        ))}
                    </nav>
                </div>

                {/* Toolbar */}
                <div className="px-6 py-4 border-t border-gray-100">
                    <div className="flex flex-col lg:flex-row gap-4 items-start lg:items-center justify-between">
                        {/* Left side - Search and filters */}
                        <div className="flex flex-col sm:flex-row gap-3 flex-1 max-w-2xl">
                            {/* Search */}
                            <div className="relative flex-1">
                                <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                                <input
                                    type="text"
                                    placeholder="Search in files"
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    className="w-full pl-10 pr-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-sm"
                                />
                            </div>

                            {/* Filters */}
                            <div className="flex gap-2">
                                <select
                                    value={filterType}
                                    onChange={(e) => setFilterType(e.target.value)}
                                    className="px-3 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-sm bg-white"
                                >
                                    <option value="all">All types</option>
                                    {getFileTypes(files).map(type => (
                                        <option key={type} value={type}>{type}</option>
                                    ))}
                                </select>

                                <select
                                    value={`${sortBy}-${sortOrder}`}
                                    onChange={(e) => {
                                        const [field, order] = e.target.value.split('-');
                                        setSortBy(field);
                                        setSortOrder(order);
                                    }}
                                    className="px-3 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-sm bg-white"
                                >
                                    <option value="uploadDate-desc">Date modified</option>
                                    <option value="name-asc">Name A-Z</option>
                                    <option value="name-desc">Name Z-A</option>
                                    <option value="size-desc">Size (largest)</option>
                                    <option value="size-asc">Size (smallest)</option>
                                </select>
                            </div>
                        </div>

                        {/* Right side - Actions and view toggle */}
                        <div className="flex items-center gap-3">
                            {/* Selection info */}
                            {selectedFiles.length > 0 && (
                                <div className="flex items-center gap-3">
                                    <span className="text-sm text-gray-600">
                                        {selectedFiles.length} selected
                                    </span>
                                    <button 
                                        onClick={handleBulkDelete}
                                        className="px-3 py-2 text-sm font-medium text-red-600 hover:text-red-800 transition"
                                    >
                                        Delete
                                    </button>
                                </div>
                            )}

                            {/* Action buttons */}
                            <button 
                                onClick={() => setIsNewFolderModalOpen(true)}
                                className="flex items-center px-4 py-2.5 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition"
                            >
                                <PlusIcon className="h-4 w-4 mr-2" />
                                New folder
                            </button>

                            <input type="file" ref={fileInputRef} onChange={handleFileUpload} className="hidden" multiple />
                            <button 
                                onClick={() => !isUploading && fileInputRef.current?.click()}
                                disabled={isUploading}
                                className="flex items-center px-4 py-2.5 text-sm font-medium text-white rounded-lg transition disabled:opacity-50"
                                style={{backgroundColor: COLORS.primary}}
                            >
                                <UploadIcon className="h-4 w-4 mr-2" />
                                {isUploading ? 'Uploading...' : 'Upload'}
                            </button>

                            {/* View toggle */}
                            <div className="flex border border-gray-300 rounded-lg overflow-hidden">
                                <button
                                    onClick={() => setViewMode('grid')}
                                    className={`p-2 ${viewMode === 'grid' ? 'bg-gray-100 text-gray-900' : 'text-gray-500 hover:text-gray-700'}`}
                                >
                                    <GridIcon className="h-4 w-4" />
                                </button>
                                <button
                                    onClick={() => setViewMode('list')}
                                    className={`p-2 ${viewMode === 'list' ? 'bg-gray-100 text-gray-900' : 'text-gray-500 hover:text-gray-700'}`}
                                >
                                    <ListIcon className="h-4 w-4" />
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Content Area */}
            <div 
                className={`flex-1 p-6 overflow-y-auto ${dragActive ? 'bg-blue-50' : ''}`}
                onDragEnter={handleDrag}
                onDragLeave={handleDrag}
                onDragOver={handleDrag}
                onDrop={handleDrop}
            >
                {/* Drag overlay */}
                {dragActive && (
                    <div className="fixed inset-0 bg-blue-500 bg-opacity-10 flex items-center justify-center z-50 pointer-events-none">
                        <div className="bg-white p-8 rounded-xl shadow-lg border-2 border-dashed border-blue-500">
                            <UploadIcon className="h-12 w-12 mx-auto mb-4 text-blue-500" />
                            <p className="text-xl font-semibold text-blue-600">Drop files here to upload</p>
                        </div>
                    </div>
                )}

                {/* Empty state */}
                {currentFiles.length === 0 && currentFolders.length === 0 ? (
                    <div className="flex flex-col items-center justify-center h-96 text-center">
                        <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mb-6">
                            <FolderIcon className="w-12 h-12 text-gray-400" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900 mb-2">
                            {currentFolder ? 'This folder is empty' : 'No files yet'}
                        </h3>
                        <p className="text-gray-500 mb-6 max-w-sm">
                            {currentFolder 
                                ? 'Add files by uploading or creating new folders.' 
                                : 'Upload your first file or create a folder to get started.'
                            }
                        </p>
                        <div className="flex gap-3">
                            <button 
                                onClick={() => setIsNewFolderModalOpen(true)}
                                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50"
                            >
                                Create folder
                            </button>
                            <button 
                                onClick={() => fileInputRef.current?.click()}
                                className="px-4 py-2 text-sm font-medium text-white rounded-lg"
                                style={{backgroundColor: COLORS.primary}}
                            >
                                Upload files
                            </button>
                        </div>
                    </div>
                ) : (
                    <>
                        {/* Folders Section */}
                        {currentFolders.length > 0 && (
                            <div className="mb-8">
                                <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide mb-4">
                                    Folders
                                </h3>
                                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
                                    {currentFolders.map(folder => (
                                        <FolderCard
                                            key={folder.id}
                                            folder={folder}
                                            onClick={handleFolderClick}
                                        />
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Files Section */}
                        {currentFiles.length > 0 && (
                            <div>
                                <div className="flex items-center justify-between mb-4">
                                    <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide">
                                        Files ({allFiles.length})
                                    </h3>
                                    {allFiles.length > 0 && (
                                        <button
                                            onClick={handleSelectAll}
                                            className="text-sm text-blue-600 hover:text-blue-800 font-medium"
                                        >
                                            {selectedFiles.length === currentFiles.length && currentFiles.length > 0 
                                                ? 'Deselect all' 
                                                : 'Select all'
                                            }
                                        </button>
                                    )}
                                </div>

                                {viewMode === 'grid' ? (
                                    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
                                        {currentFiles.map(file => (
                                            <FileCard
                                                key={file.id}
                                                file={file}
                                                isSelected={selectedFiles.includes(file.id)}
                                                onSelect={() => handleSelectFile(file.id)}
                                                onClick={() => handleFileClick(file)}
                                                onMenuClick={() => handleFileClick(file)}
                                            />
                                        ))}
                                    </div>
                                ) : (
                                    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                                        <table className="min-w-full divide-y divide-gray-200">
                                            <thead className="bg-gray-50">
                                                <tr>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                        <input
                                                            type="checkbox"
                                                            checked={selectedFiles.length === currentFiles.length && currentFiles.length > 0}
                                                            onChange={handleSelectAll}
                                                            className="w-4 h-4 text-blue-600 rounded border-gray-300"
                                                        />
                                                    </th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                        Name
                                                    </th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                        Size
                                                    </th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                        Modified
                                                    </th>
                                                    <th className="relative px-6 py-3">
                                                        <span className="sr-only">Actions</span>
                                                    </th>
                                                </tr>
                                            </thead>
                                            <tbody className="bg-white divide-y divide-gray-200">
                                                {currentFiles.map(file => (
                                                    <tr 
                                                        key={file.id} 
                                                        className={`hover:bg-gray-50 cursor-pointer ${
                                                            selectedFiles.includes(file.id) ? 'bg-blue-50' : ''
                                                        }`}
                                                        onClick={() => handleFileClick(file)}
                                                    >
                                                        <td className="px-6 py-4 whitespace-nowrap">
                                                            <input
                                                                type="checkbox"
                                                                checked={selectedFiles.includes(file.id)}
                                                                onChange={() => handleSelectFile(file.id)}
                                                                onClick={(e) => e.stopPropagation()}
                                                                className="w-4 h-4 text-blue-600 rounded border-gray-300"
                                                            />
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap">
                                                            <div className="flex items-center">
                                                                <FileIconByType 
                                                                    type={file.fileType} 
                                                                    fileName={file.name || file.fileName} 
                                                                />
                                                                <div className="ml-3">
                                                                    <div className="text-sm font-medium text-gray-900">
                                                                        {file.name || file.fileName}
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                            {formatFileSize(file.size || file.fileSize)}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                            {new Date(file.uploadedAt || file.uploadDate || Date.now()).toLocaleDateString()}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                                            <button
                                                                onClick={(e) => { e.stopPropagation(); handleFileClick(file); }}
                                                                className="text-blue-600 hover:text-blue-900"
                                                            >
                                                                <DotsIcon className="w-5 h-5" />
                                                            </button>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                )}

                                {/* Pagination */}
                                <div className="flex items-center justify-between mt-6 px-4 py-3 bg-white border border-gray-200 rounded-lg">
                                    <div className="flex items-center gap-4">
                                        <div className="flex items-center gap-2">
                                            <span className="text-sm text-gray-600">Show:</span>
                                            <select 
                                                value={itemsPerPage} 
                                                onChange={(e) => {
                                                    setItemsPerPage(Number(e.target.value));
                                                    setCurrentPage(1);
                                                }}
                                                className="border rounded px-2 py-1 text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                            >
                                                <option value={10}>10</option>
                                                <option value={15}>15</option>
                                                <option value={20}>20</option>
                                                <option value={25}>25</option>
                                            </select>
                                        </div>
                                        <span className="text-sm text-gray-700">
                                            Showing {((currentPage - 1) * itemsPerPage) + 1} to {Math.min(currentPage * itemsPerPage, allFiles.length)} of {allFiles.length} files
                                        </span>
                                    </div>
                                    {paginatedFiles.totalPages > 1 && (
                                        <div className="flex items-center space-x-2">
                                            <button
                                                onClick={() => setCurrentPage(currentPage - 1)}
                                                disabled={currentPage === 1}
                                                className="px-3 py-1 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                                            >
                                                Previous
                                            </button>
                                            <span className="text-sm text-gray-700">
                                                Page {currentPage} of {paginatedFiles.totalPages}
                                            </span>
                                            <button
                                                onClick={() => setCurrentPage(currentPage + 1)}
                                                disabled={currentPage === paginatedFiles.totalPages}
                                                className="px-3 py-1 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                                            >
                                                Next
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};
