import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import {
  ShieldCheck,
  CloudUpload,
  LockKeyhole,
  Mail,
  Phone,
  Menu,
  X,
  DatabaseZap,
  Fingerprint,
  FileLock2,
  ScanSearch,
  Users,
  User,
  Briefcase,
  ArrowRight,
  MapPin,
  Facebook,
  Twitter,
  Linkedin,
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";

// Dropbox-style Professional Theme
const COLORS = {
  primary: '#0061FF',      // Primary Blue
  darkBg: '#1E1E2F',       // Dark Blue/Navy
  white: '#FFFFFF',        // White
  lightGray: '#F5F5F5',    // Light Gray
  mediumGray: '#A0A0A0',   // Medium Gray
  black: '#000000',        // Black
  deepBlue: '#1A4A9C',     // Deep Blue for headings
  electricViolet: '#6A0DAD', // Electric Violet for icons
  darkGray: '#444444',     // Dark Gray for text
};

// Scrollytelling How It Works Component
const ScrollytellingHowItWorks = () => {
  const [activeStep, setActiveStep] = useState(0);
  const triggerRefs = useRef([]);
  const contentRef = useRef(null);

  const steps = [
    {
      icon: CloudUpload,
      title: "Upload & Encrypt",
      description: "Drag and drop files into secure portal. Advanced AES-256 encryption protects your data with zero-knowledge security."
    },
    {
      icon: ScanSearch,
      title: "Organize & Find",
      description: "AI-powered smart categorization and lightning-fast search with OCR help you find documents in milliseconds."
    },
    {
      icon: FileLock2,
      title: "Access & Control",
      description: "Access from any device with biometric authentication and enterprise-grade security protocols."
    }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = triggerRefs.current.indexOf(entry.target);
            if (index !== -1) {
              setActiveStep(index);
            }
          }
        });
      },
      { threshold: 0.5 }
    );

    triggerRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <section id="how-it-works-wrapper" className="relative">
      <div className="grid grid-cols-1 lg:grid-cols-2 min-h-screen">
        {/* Left Panel - Sticky Content */}
        <div 
          ref={contentRef}
          className="sticky top-0 h-screen flex items-center justify-center p-8"
          style={{ backgroundColor: COLORS.white }}
        >
          <div className="max-w-md">
            <motion.div
              key={activeStep}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, ease: "easeInOut" }}
              className="text-center"
            >
              {React.createElement(steps[activeStep].icon, {
                className: "w-20 h-20 mx-auto mb-6",
                style: { color: COLORS.electricViolet }
              })}
              <h3 
                className="text-3xl font-bold mb-4"
                style={{ color: COLORS.deepBlue, fontFamily: 'Inter, sans-serif' }}
              >
                {steps[activeStep].title}
              </h3>
              <p 
                className="text-lg leading-relaxed"
                style={{ color: COLORS.darkGray, fontFamily: 'Lato, sans-serif' }}
              >
                {steps[activeStep].description}
              </p>
            </motion.div>
          </div>
        </div>

        {/* Right Panel - Fixed Background */}
        <div 
          className="relative min-h-screen"
          style={{
            backgroundImage: 'url("https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center")',
            backgroundAttachment: 'fixed',
            backgroundSize: 'cover',
            backgroundPosition: 'center'
          }}
        >
          <div className="absolute inset-0 bg-gradient-to-l from-black/40 to-transparent" />
        </div>
      </div>

      {/* Scroll Trigger Area */}
      <div className="scroll-trigger-area absolute top-0 left-0 w-full pointer-events-none">
        {steps.map((_, index) => (
          <div
            key={index}
            ref={(el) => (triggerRefs.current[index] = el)}
            className="h-screen"
          />
        ))}
      </div>
    </section>
  );
};

// Flip Card Stack Component
const FlipCardStack = () => {
  const cards = [
    {
      icon: User,
      title: "For Individuals",
      description: "• Secure personal documents\n• Biometric vault protection\n• Prevent unauthorized access"
    },
    {
      icon: Briefcase,
      title: "For Freelancers",
      description: "• Store client contracts & invoices\n• Organized workspace\n• Secure client portals"
    },
    {
      icon: Users,
      title: "For Admin Teams",
      description: "• Manage company documents\n• Robust admin controls\n• Team permission management"
    }
  ];

  return (
    <div className="w-full px-6">
      <div className="grid grid-cols-1 gap-6 max-w-md mx-auto">
        {cards.map((card, index) => {
          const Icon = card.icon;

          return (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
              whileHover={{ y: -5 }}
              className="p-6 rounded-lg border flex flex-col items-center text-center"
              style={{backgroundColor: COLORS.white, borderColor: COLORS.mediumGray + '40', boxShadow: '0 2px 8px rgba(0,0,0,0.1)'}}
            >
              <Icon className="w-12 h-12 mb-4" style={{color: COLORS.primary}} />
              <h3 className="text-lg font-bold mb-3" style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}>{card.title}</h3>
              <div className="leading-relaxed text-sm whitespace-pre-line" style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}>
                {card.description}
              </div>
            </motion.div>
          );
        })}
      </div>
    </div>
  );
};

// HERO SLIDE COMPONENT (Modernized)
const HeroCarousel = ({ isAuthenticated, handleDashboardNavigation, navigate, scrollToSection }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      title: "Your Digital",
      highlight: "Fortress",
      description:
        "SecureVault protects your most important files with next-generation encryption, powered by AI and cloud security.",
      image:
        "https://media.istockphoto.com/id/2206161408/photo/glowing-shield-symbolizing-cybersecurity-on-a-high-tech-circuit-background.webp?a=1&b=1&s=612x612&w=0&k=20&c=pp7VyJc4-NM3-KGhQ4rm_89T-7lEmzrF6kNefVHzU18=",
      alt: "Cyber fortress and digital protection",
    },
    {
      title: "Bank-Grade",
      highlight: "Encryption",
      description:
        "We use cutting-edge AES-256 encryption to keep your digital identity locked and protected at all times.",
      image:
        "https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=800&h=600&fit=crop&crop=center",
      alt: "Encryption and cyber lock",
    },
    {
      title: "Access",
      highlight: "Anywhere",
      description:
        "Access your documents securely from any device. Sync instantly with cloud backups and smart categorization.",
      image:
        "https://images.unsplash.com/photo-1605902711622-cfb43c4437b5?w=1400&h=900&fit=crop&crop=center",
      alt: "Access documents globally",
    },
    { title: "Smart",
      highlight: "Organization",
      description: "AI-powered categorization and lightning-fast search help you find any document in seconds, not minutes.", 
      image: "https://plus.unsplash.com/premium_photo-1683288662019-c92caea8276d?ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&q=80&w=1074",
      alt: "Document organization and filing" 
    }
  ];

  // Auto-play
  useEffect(() => {
    if (!isAutoPlay) return;
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, [isAutoPlay, slides.length]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
    setIsAutoPlay(false);
    setTimeout(() => setIsAutoPlay(true), 8000);
  };

  return (
    <section className="relative min-h-screen flex items-center justify-center" style={{ marginTop: '-80px', paddingTop: '80px', backgroundColor: COLORS.white }}>
      {/* Content */}
      <div className="relative z-20 w-full px-0 grid grid-cols-1 lg:grid-cols-2 gap-10 items-center">
        {/* Text Content */}
        <motion.div
          key={`text-${currentSlide}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7 }}
          className="text-center"
        >
          <h1 className="text-5xl lg:text-6xl font-bold leading-tight mb-6" style={{ fontFamily: 'Inter, sans-serif', fontWeight: 700, color: COLORS.black }}>
            Secure Document{" "}
            <span style={{ color: COLORS.primary }}>Storage</span>
          </h1>
          <p className="text-xl leading-relaxed max-w-2xl mx-auto mb-8" style={{ fontFamily: 'Lato, sans-serif', color: COLORS.mediumGray }}>
            Professional-grade security for your important files. Simple, secure, and always accessible.
          </p>

          <div className="mt-8 flex flex-wrap gap-4 justify-center">
            {isAuthenticated ? (
              <button
                onClick={handleDashboardNavigation}
                className="text-white font-semibold py-4 px-8 rounded-lg flex items-center transition duration-300"
                style={{backgroundColor: COLORS.primary, fontFamily: 'Inter, sans-serif', fontWeight: 600}}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#0052E0'}
                onMouseLeave={(e) => e.target.style.backgroundColor = COLORS.primary}
              >
                Go to Dashboard <ArrowRight className="ml-2 w-5 h-5" />
              </button>
            ) : (
              <>
                <button
                  onClick={() => navigate("/register")}
                  className="text-white font-semibold py-4 px-8 rounded-lg transition duration-300 flex items-center"
                  style={{backgroundColor: COLORS.primary, fontFamily: 'Inter, sans-serif', fontWeight: 600}}
                  onMouseEnter={(e) => e.target.style.backgroundColor = '#0052E0'}
                  onMouseLeave={(e) => e.target.style.backgroundColor = COLORS.primary}
                >
                  Start Protecting My Files <ArrowRight className="ml-2 w-5 h-5" />
                </button>
                <button
                  onClick={() => scrollToSection("features")}
                  className="font-semibold py-4 px-8 rounded-lg border-2 transition duration-300"
                  style={{ 
                    backgroundColor: COLORS.white, 
                    borderColor: COLORS.primary,
                    color: COLORS.primary,
                    fontFamily: 'Inter, sans-serif', 
                    fontWeight: 600
                  }}
                  onMouseEnter={(e) => e.target.style.backgroundColor = COLORS.lightGray}
                  onMouseLeave={(e) => e.target.style.backgroundColor = COLORS.white}
                >
                  Learn More
                </button>
              </>
            )}
          </div>
        </motion.div>

        {/* Slide Indicators + Image Text */}
        <div className="hidden lg:flex flex-col justify-center items-center relative">
          <motion.div
            key={`img-${currentSlide}`}
            initial={{ opacity: 0, scale: 0.97 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            className="w-full h-[400px] rounded-3xl overflow-hidden shadow-2xl relative"
          >
            <img
              src={slides[currentSlide].image}
              alt={slides[currentSlide].alt}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-black/30" />
          </motion.div>

          <div className="flex justify-center mt-6 space-x-3">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => handleSlideChange(index)}
                className={`transition-all duration-300 ${
                  currentSlide === index
                    ? "w-6 h-3 rounded-full"
                    : "w-3 h-3 rounded-full"
                }`}
                style={currentSlide === index ? {backgroundColor: COLORS.primary} : {backgroundColor: COLORS.mediumGray}}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

// MAIN HOME PAGE COMPONENT
export default function Home() {
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth();

  const handleDashboardNavigation = () => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    if (user?.role === "ADMIN") navigate("/admin-dashboard");
    else navigate("/dashboard");
  };

  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="min-h-screen flex flex-col bg-white text-gray-900 font-inter" style={{ margin: 0, padding: 0 }}>
      <Navbar />

      {/* HERO SLIDE SECTION */}
      <HeroCarousel
        isAuthenticated={isAuthenticated}
        handleDashboardNavigation={handleDashboardNavigation}
        navigate={navigate}
        scrollToSection={scrollToSection}
      />

      {/* HOW IT WORKS SCROLLYTELLING SECTION */}
      <ScrollytellingHowItWorks />

      {/* SECURITY FEATURES SECTION */}
      <section id="security" className="py-24 px-0" style={{backgroundColor: COLORS.white}}>
        <motion.h2 
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, type: "spring", bounce: 0.3 }}
          className="text-4xl font-bold mb-4 text-center"
          style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}
        >
          Your Security is Our Priority
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-lg text-center max-w-3xl mx-auto mb-16"
          style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}
        >
          Military-grade protection meets user-friendly design. Experience uncompromising security without sacrificing convenience.
        </motion.p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 w-full px-6">
          {/* Card 1: End-to-End Encryption */}
          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.1 }}
            whileHover={{ y: -5 }}
            className="p-8 rounded-lg border flex flex-col items-center text-center"
            style={{backgroundColor: COLORS.white, borderColor: COLORS.mediumGray + '40', boxShadow: '0 2px 8px rgba(0,0,0,0.1)'}}
          >
            <ShieldCheck className="w-14 h-14 mb-6" style={{color: COLORS.primary}} />
            <h3 className="text-xl font-bold mb-3" style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}>
              End-to-End Encryption
            </h3>
            <p className="text-sm leading-relaxed" style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}>
              AES-256 military-grade encryption ensures only you can access your files. Zero-knowledge architecture means even we can't see your data.
            </p>
          </motion.div>

          {/* Card 2: Multi-Factor Authentication */}
          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            whileHover={{ y: -5 }}
            className="p-8 rounded-lg border flex flex-col items-center text-center"
            style={{backgroundColor: COLORS.white, borderColor: COLORS.mediumGray + '40', boxShadow: '0 2px 8px rgba(0,0,0,0.1)'}}
          >
            <Fingerprint className="w-14 h-14 mb-6" style={{color: COLORS.primary}} />
            <h3 className="text-xl font-bold mb-3" style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}>
              Multi-Factor Authentication
            </h3>
            <p className="text-sm leading-relaxed" style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}>
              Biometric authentication, SMS codes, and authenticator apps provide multiple layers of security for your account.
            </p>
          </motion.div>

          {/* Card 3: Real-time Monitoring */}
          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.3 }}
            whileHover={{ y: -5 }}
            className="p-8 rounded-lg border flex flex-col items-center text-center"
            style={{backgroundColor: COLORS.white, borderColor: COLORS.mediumGray + '40', boxShadow: '0 2px 8px rgba(0,0,0,0.1)'}}
          >
            <DatabaseZap className="w-14 h-14 mb-6" style={{color: COLORS.primary}} />
            <h3 className="text-xl font-bold mb-3" style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}>
              Real-time Monitoring
            </h3>
            <p className="text-sm leading-relaxed" style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}>
              24/7 threat detection and anomaly monitoring protect your data from unauthorized access attempts and breaches.
            </p>
          </motion.div>

          {/* Card 4: Compliance & Auditing */}
          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            whileHover={{ y: -5 }}
            className="p-8 rounded-lg border flex flex-col items-center text-center"
            style={{backgroundColor: COLORS.white, borderColor: COLORS.mediumGray + '40', boxShadow: '0 2px 8px rgba(0,0,0,0.1)'}}
          >
            <ShieldCheck className="w-14 h-14 mb-6" style={{color: COLORS.primary}} />
            <h3 className="text-xl font-bold mb-3" style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}>
              Compliance & Auditing
            </h3>
            <p className="text-sm leading-relaxed" style={{color: COLORS.mediumGray, fontFamily: 'Lato, sans-serif'}}>
              SOC 2, GDPR, and HIPAA compliant infrastructure with regular third-party security audits and certifications.
            </p>
          </motion.div>
        </div>
      </section>

      {/* USE CASES SECTION */}
      <section id="features" className="py-24 px-0" style={{backgroundColor: COLORS.lightGray}}>
        <motion.h2 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-4xl font-bold mb-16 text-center"
          style={{color: COLORS.black, fontFamily: 'Inter, sans-serif', fontWeight: 700}}
        >
          A Secure Vault for Everyone
        </motion.h2>

        <div className="w-full px-6 grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
          {/* Cards Side */}
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <FlipCardStack />
          </motion.div>

          {/* Lock Image Side */}
          <motion.div
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="flex justify-center relative"
          >
            <div className="relative">
              <img
                src="https://images.unsplash.com/photo-1614064641938-3bbee52942c7?w=500&h=600&fit=crop&crop=center"
                alt="Digital Security Lock"
                className="w-96 h-96 object-cover rounded-3xl shadow-2xl"
              />
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                <LockKeyhole className="w-24 h-24 text-white drop-shadow-2xl" />
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* VIDEO SHOWCASE SECTION */}
      <section className="py-24 px-0" style={{backgroundColor: COLORS.white}}>
        <div className="w-full px-6 text-center">
          <motion.h2
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-4xl lg:text-5xl font-bold mb-6"
            style={{ color: COLORS.black }}
          >
            See SecureVault in <span style={{ color: COLORS.primary }}>Action</span>
          </motion.h2>

          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            className="text-xl mb-16 max-w-3xl mx-auto leading-relaxed"
            style={{ color: COLORS.mediumGray }}
          >
            Watch how our revolutionary platform transforms document security with military-grade encryption and AI-powered organization
          </motion.p>

          {/* Video Container */}
          <motion.div
            initial={{ opacity: 0, y: 50, scale: 0.9 }}
            whileInView={{ opacity: 1, y: 0, scale: 1 }}
            transition={{ duration: 1, delay: 0.6 }}
            className="relative max-w-4xl mx-auto"
          >
            <div className="relative rounded-3xl overflow-hidden p-2" style={{backgroundColor: COLORS.lightGray}}>
              <video
                className="w-full rounded-2xl shadow-2xl"
                controls
                preload="metadata"
                poster="/videos/DemoImage.png"
                style={{ aspectRatio: '16/9', objectFit: 'contain' }}
              >
                <source src="/videos/DemoVideo.mp4" type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            </div>
          </motion.div>

          {/* Call to Action */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.4 }}
            className="mt-16"
          >
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="px-8 py-4 text-white font-semibold rounded-lg transition-all duration-300 mr-4"
              style={{ backgroundColor: COLORS.primary }}
              onMouseEnter={(e) => e.target.style.backgroundColor = '#0052E0'}
              onMouseLeave={(e) => e.target.style.backgroundColor = COLORS.primary}
            >
              Start Your Free Trial
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
